const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	overflow-x: hidden;
	.slick-track {
		position: relative;
		left: 0;
		top: 0;
		display: table;
		/* display: block; */
	}
	.slick-track:before,
	.slick-track:after {
		content: '';
		display: table;
		margin-right: 250px;
	}
	.slick-track:after {
		clear: both;
	}
	.slick-slide {
		float: left;
		width: auto;
		height: 100%;
		min-height: 1px;
		display: inline-block;
	}

	.slick-initialized .slick-slide {
		display: block;
	}
`;
