		height: 34px;
		font-weight: 600;
		color: #36f;
	}
	.container-check {
		display: flex;
		align-items: center;
		position: relative;
		height: 14px;
	}
	.footer-level1 {
		margin-top: 18px;
	}
	.style-label {
		flex: 1 1;
		font-size: 15px;
	}

	.style-check {
		display: flex;
		justify-content: center;
		align-items: center;
		width: 17.3px;
		height: 17.3px;
	}
	.link-agreement {
		float: right;
		font-size: 13px;
		line-height: 19px;
		color: #767676 !important;
	}
	.checkbox-agreement {
		margin-bottom: 40px;
		color: #ececec;
		> div:first-of-type {
			margin-right: 5px;
		}
		> div:last-of-type {
			color: #939393;
		}
		p {
			font-weight: 400;
		}
	}
	.style-guidance {
		color: #767676;
		bottom: 0;
		margin-top: 6px;
		font-size: 12px;
	}
	.hint-level1 {
		font-weight: 500;
		font-size: 12px;
		color: #aaa;
		margin-top: 10px;
	}
	.select-icon {
		position: absolute;
		top: 50%;
		right: 10px;
		-webkit-transform: translateY(-50%);
		transform: translateY(-50%);
	}

	.style-wrapper {
		position: relative;
		padding-bottom: 22px;
		color: #767676;

		input[type='checkbox'] {
			position: absolute;
			left: 1px;
			opacity: 0;
			vertical-align: top;
			cursor: pointer;
		}
	}
	.style-body {
		margin-top: 11px;
	}
	.wrapper-body-container {
		padding: 29px 19px 24px;
	}
	.header-wrapper {
		margin-bottom: 34px;
		font-size: 15px;
		text-align: left;
		color: #333;
		font-weight: 600;
	}
	.header-desc {
		line-height: 1.53;
		white-space: pre-line;
	}

	.step {
		position: relative;
		border-bottom: 1px solid #e1e2e3;
		padding: 20px;
	}
	.step-container {
		display: flex;
		justify-content: flex-start;
		> li > span {
			display: inline-block;
			font-size: 14px;
			color: #333;
			margin-left: 4px;
			font-weight: 700;
			line-height: 23px;
		}
		> li.active,
		> li.completed {
			margin-left: 0;
			color: #333;
			margin-right: auto;
			::after {
				background: #36f;
				width: 33.33%;
				transition: width 0.5s;
				left: 0;
				position: absolute;
				content: '';
				display: block;
				bottom: -1px;
				height: 1px;
			}
		}
		> li.active > em,
		> li.completed > em {
			background: #36f;
			color: #fff;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			width: 23px;
			height: 23px;
			line-height: 1em;
			border-radius: 50%;
			font-style: normal;
			margin-right: 6px;
		}
		> li {
			flex: none;
			height: 23px;
		}
		> li > em {
			display: inline-flex;
			align-items: center;
			justify-content: center;
			width: 23px;
			height: 23px;
			line-height: 1em;
			border-radius: 50%;
			font-style: normal;
			background: #f2f4f7;
			color: #ccc;
			margin-right: 6px;
		}
		> li:nth-of-type(2):after {
			left: 33.33%;
		}
		> li:after {
			position: absolute;
			content: '';
			display: block;
			bottom: -1px;
			height: 1px;
			width: 0;
		}
	}
	.wrapper-body {
		border: 1px solid #ececec;
		border-radius: 5px;
		margin-top: 39px;
		background-color: #fff;
	}

	.wrapper-onboarding {
		width: 400px;
		max-width: 100vw;
	}
	.wrapper-logo {
		display: flex;
		justify-content: center;
		align-items: center;
		> svg:nth-child(2) {
			margin-top: -4px px;
			margin-left: 6px;
		}
	}
