import React from 'react';
import styled from 'styled-components';

function ToastModal() {
	return (
		<ComponentWrapper>
			<span className="icon-notice">
				<i className=".icon-reload" />
			</span>
			<span>직무는 최대 5개까지 선택 가능합니다.</span>
		</ComponentWrapper>
	);
}
const ComponentWrapper = styled.div`
	display: flex;
	align-items: center;
	position: fixed;
	left: 50%;
	bottom: 0;
	padding: 15px;
	color: #fff;
	font-size: 14px;
	font-weight: 500;
	line-height: normal;
	background-color: #3d4045;
	border-radius: 4px;
	transform: translate(-50%);
	transition: 0.3s;
	box-shadow: 0 4px 4px rgb(0 0 0 / 25%);
	opacity: 1;
	/* visibility: hidden; */
	visibility: visible;
	z-index: 1600;
	.icon-notice {
		color: #ff415c;

		position: relative;
		font-size: 14px;
		margin-right: 8px;
	}
`;

export default ToastModal;
