import { useEffect } from 'react';
import { useHistory, useLocation, useNavigate } from 'react-router-dom';

export default function ScrollToTop() {
	const { pathname } = useLocation();
	const navigate = useNavigate();

	useEffect(() => {
		window.scrollTo(0, 0);
		window.onpopstate = (e) => {
			// navigate('/');
			e.preventDefault();
			console.log(e);
			navigate(-1);
		};
	}, [pathname]);

	return null;
}
