/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/label-has-associated-control */
import React, { useEffect, useState } from 'react';

function ResumeDetailList({ isLink, isCareer, isSkill, isEducation }) {
	const [isWorking, setIsWorking] = useState(false);
	const [title, setTitle] = useState('');
	const [subtitle, setSubTitle] = useState('');
	const [educationText, setEducationText] = useState('');

	const [startYear, setStartYear] = useState('');
	const [startMonth, setStartMonth] = useState('');

	const [exportData, setExportData] = useState({});
	console.log(startYear);
	const handleChangeDate = (e, type) => {
		if (type === 'SY') {
			setStartYear(e.target.value);
		}
	};

	useEffect(() => {
		if (isCareer) {
			setTitle('회사명');
			setSubTitle('부서명/직책');
		}
		if (isEducation) {
			setTitle('학교명');
			setSubTitle('전공 및 학위 (ex: 경영학과 학사)');
			setEducationText('이수과목 또는 연구내용');
		}
		if (isLink) {
			setSubTitle('http://');
		}
	}, []);

	return (
		<ul className="">
			<li className="sortableitem-container">
				{/* <div className="draghandler">
					<div className="DragHandler_DragHandler_handler__D_XV_" />
				</div> */}
				<div className="careeritem-container clearfix">
					{!isLink && (
						<div>
							<div>
								<div className="period-datetime">
									<div className="period-form">
										<div>
											<input
												name="startYear"
												field="start_year"
												type="text"
												maxLength="4"
												className="year"
												onChange={(e) => {
													handleChangeDate(e, 'SY');
												}}
												placeholder="YYYY"
											/>
											<span>
												.
												<input
													name="startMonth"
													type="text"
													maxLength="2"
													className="month"
													placeholder="MM"
												/>
											</span>
										</div>
									</div>
									{!isWorking && (
										<div className="period-form-end">
											<span>-</span>
											<div className="period-form">
												<input
													name="endYear"
													type="text"
													maxLength="4"
													className="year"
													placeholder="YYYY"
												/>
											</div>
											<div className="period-form">
												<span>.</span>
												<input
													name="endMonth"
													type="text"
													maxLength="2"
													className="month"
													placeholder="MM"
												/>
											</div>
											<span className="datetiem-isrequired">
												*
											</span>
										</div>
									)}
								</div>
								<div className="period-checkbox">
									<input
										onClick={() => {
											setIsWorking(!isWorking);
										}}
										type="checkbox"
										name="checked"
										value="false"
									/>
									<label
										htmlFor="checked"
										className="Period_Period__form_checkbox_label__btW7m"
									>
										현재 재직중
									</label>
								</div>
							</div>
						</div>
					)}
					<div>
						{!isLink && (
							<div>
								<form action=".">
									<div>
										<input
											type="text"
											className="searchmodalinput-input resume-input"
											placeholder={title}
											name="maintitle"
										/>
									</div>
								</form>
							</div>
						)}

						<div>
							<input
								className="resumeInput-container resume-input title careers"
								type="text"
								maxLength="255"
								placeholder={subtitle}
								name="title"
							/>
						</div>
						{isCareer && (
							<div>
								<div>
									<button
										type="button"
										className="resumecontentlist-body-container"
									>
										+ 주요 성과 추가
									</button>
								</div>
							</div>
						)}
						{isEducation && (
							<div>
								<input
									className="input-education-container"
									type="text"
									maxLength="255"
									placeholder={educationText}
									name="title"
								/>
							</div>
						)}
					</div>
					<button type="button" className="btn-delete">
						<i className="icon-close" />
					</button>
				</div>
			</li>
		</ul>
	);
}

export default ResumeDetailList;
