import TagListItem from 'components/common/TagListItem';
import { LeftArticleArrow, RightArticleArrow } from 'pages/home/ArticleArrows';
import React, { useState } from 'react';
import { useInView } from 'react-intersection-observer';
import Slider from 'react-slick';
import styled from 'styled-components';
import { JobMainCategoryDummy, TagListItemDummy } from 'utils/mockData';
import { Link } from 'react-router-dom';
import LocationModal from 'components/wdlist/LocationModal';
import { useDispatch, useSelector } from 'react-redux';
import {
	setLocationModalInvisible,
	setLocationModalVisible,
} from 'store/action/WdListAction';
import RcSlider from 'rc-slider';
import JobListContentContainer from './JobListContentContainer';
import 'rc-slider/assets/index.css';

function JobListContainer() {
	const dispatch = useDispatch();

	const { locationModalVisible } = useSelector(
		(state) => state.WdListReducer,
	);

	const [careerDuration, setCareerDuration] = useState(0);

	const [isSorting, setIsSorting] = useState(false);
	const [arrowState, setArrowState] = useState(0);
	const [tagList, setTagList] = useState([]);
	const [ref, inView, entry] = useInView();

	const LocationmodalVisible = () => {
		dispatch(setLocationModalVisible());
	};
	const LocationmodalInvisible = () => {
		dispatch(setLocationModalInvisible());
	};

	const ArrowStyle = {
		top: '0px',
		width: '30px',
		height: '30px',
		minWidth: '30px',
		minHeight: '30px',
		fontSize: '15px',
		zIndex: '10',
		backgroundColor: '#fff',
	};

	const settings = {
		dots: false,
		speed: 500,
		infinite: false,
		slidesToShow: 5,
		slidesToScroll: 3,
		variableWidth: true,
		prevArrow: <LeftArticleArrow customStyle={ArrowStyle} />,
		nextArrow: <RightArticleArrow customStyle={ArrowStyle} />,
		arrows: true,
		afterChange: (prev) => setArrowState(prev),
	};

	const handleChangeTags = (e, tag) => {
		if (tagList.includes(tag)) {
			const temp = tagList.filter((element) => element !== tag);
			setTagList(temp);
		} else {
			const temp = [...tagList, tag];
			setTagList(temp);
		}
	};

	const sliderHandle = (e) => {
		const { value, offset } = e;
		console.log(value, offset);
		// return <p {...e}>{value}</p>;
	};

	return (
		<JobListContainers inView={!inView}>
			<div ref={ref} className="fixed-dummy" />
			<div className="container-filterwdlist">
				<div className="container-filterwdlist-inner">
					<div className="btngroup-filterwdlist">
						<button
							onClick={() => LocationmodalVisible()}
							type="button"
							className="btn-filterwdlist-first"
						>
							<span className="text-filterbtn">지역</span>
							<span className="text-filterbtn-display">한국</span>
						</button>

						<div className="container-filterbtn">
							<button
								type="button"
								className="btn-filterwdlist-first"
							>
								<span className="text-filterbtn">경력</span>
								<span className="text-filterbtn-display">
									전체
								</span>
								<svg
									width="8"
									height="7"
									viewBox="0 0 8 7"
									fill="none"
									xmlns="https://www.w3.org/2000/svg"
								>
									<path
										d="M7.33334 0.494202C7.85691 0.494202 8.14842 1.1611 7.82205 1.61224L4.50038 6.20371C4.25071 6.54882 3.77503 6.54971 3.5243 6.20554L0.179295 1.61408C-0.149094 1.16332 0.14211 0.494202 0.666672 0.494202H7.33334Z"
										fill="#333333"
									/>
								</svg>
							</button>
							<section className="container-yearspopup">
								<header className="header-header">
									<div className="header-range">
										<span>전체</span>
										{/* <span>-</span> */}
										{/* <span>10년 이상</span> */}
									</div>
								</header>
								<div className="rcSlider-wrapper">
									<CustomRcSlider
										range
										min={0}
										max={10}
										defaultValue={[0, 10]}
										handle={(e) => {
											sliderHandle(e);
										}}
										railStyle={{
											backgroundColor: '#EEF0F2',
										}}
										trackStyle={{
											backgroundColor: '#3366FF',
										}}
										handleStyle={{
											height: 25,
											width: 25,
											marginTop: -9,
											backgroundColor: '#fff',
											border: '1px solid #3366ff',
											borderRadius: 50,
											opacity: 1,
										}}
									/>
								</div>
								{/* <hr
									className="divider"
									style={{ opacity: '0.2' }}
								/> */}
							</section>
						</div>

						<div className="container-filterbtn">
							<button
								type="button"
								className="btn-filterwdlist-first"
							>
								<span className="text-filterbtn">기술스택</span>
								<span className="text-filterbtn-display" />
								<svg
									width="8"
									height="7"
									viewBox="0 0 8 7"
									fill="none"
									xmlns="https://www.w3.org/2000/svg"
								>
									<path
										d="M7.33334 0.494202C7.85691 0.494202 8.14842 1.1611 7.82205 1.61224L4.50038 6.20371C4.25071 6.54882 3.77503 6.54971 3.5243 6.20554L0.179295 1.61408C-0.149094 1.16332 0.14211 0.494202 0.666672 0.494202H7.33334Z"
										fill="#333333"
									/>
								</svg>
							</button>
						</div>
					</div>

					<JobSortContainer
						issort={isSorting}
						onClick={() => {
							setIsSorting(!isSorting);
						}}
					>
						<div className="selector-jobsort">
							<div className="customselector-jobsort">
								<button className="" type="button">
									응답률순
									<svg
										width="8"
										height="7"
										viewBox="0 0 8 7"
										fill="none"
										xmlns="https://www.w3.org/2000/svg"
									>
										<path
											d="M7.33334 0.494202C7.85691 0.494202 8.14842 1.1611 7.82205 1.61224L4.50038 6.20371C4.25071 6.54882 3.77503 6.54971 3.5243 6.20554L0.179295 1.61408C-0.149094 1.16332 0.14211 0.494202 0.666672 0.494202H7.33334Z"
											fill="#333333"
										/>
									</svg>
								</button>
								<ul
									style={{
										display: isSorting ? 'block' : 'none',
									}}
								>
									<li>
										<button
											name="job.latest_order"
											type="button"
										>
											최신순
										</button>
									</li>
									<li>
										<button
											name="job.compensation_order"
											type="button"
											data-attribute-id="explore__sort__update"
											data-sort-kind="reward"
											data-job-category-id="all"
											data-job-category="all"
											data-job-role-id="all"
											data-job-role="all"
										>
											보상금순
										</button>
									</li>
									<li>
										<button
											name="job.popularity_order"
											type="button"
											data-attribute-id="explore__sort__update"
											data-sort-kind="popular"
											data-job-category-id="all"
											data-job-category="all"
											data-job-role-id="all"
											data-job-role="all"
										>
											인기순
										</button>
									</li>
								</ul>
							</div>
							<div className="mobileicon-selector">
								<svg
									width="8"
									height="7"
									viewBox="0 0 8 7"
									fill="none"
									xmlns="https://www.w3.org/2000/svg"
								>
									<path
										d="M7.33334 0.494202C7.85691 0.494202 8.14842 1.1611 7.82205 1.61224L4.50038 6.20371C4.25071 6.54882 3.77503 6.54971 3.5243 6.20554L0.179295 1.61408C-0.149094 1.16332 0.14211 0.494202 0.666672 0.494202H7.33334Z"
										fill="#333333"
									/>
								</svg>
							</div>
							<select className="visible-xs">
								<option
									value="company.response_rate_order"
									data-attribute-id="explore__sort__update"
									data-sort-kind="responseRate"
									data-job-category-id="all"
									data-job-category="all"
									data-job-role-id="all"
									data-job-role="all"
								>
									응답률순
								</option>
								<option
									value="job.latest_order"
									data-attribute-id="explore__sort__update"
									data-sort-kind="latest"
									data-job-category-id="all"
									data-job-category="all"
									data-job-role-id="all"
									data-job-role="all"
								>
									최신순
								</option>
								<option
									value="job.compensation_order"
									data-attribute-id="explore__sort__update"
									data-sort-kind="reward"
									data-job-category-id="all"
									data-job-category="all"
									data-job-role-id="all"
									data-job-role="all"
								>
									보상금순
								</option>
								<option
									value="job.popularity_order"
									data-attribute-id="explore__sort__update"
									data-sort-kind="popular"
									data-job-category-id="all"
									data-job-category="all"
									data-job-role-id="all"
									data-job-role="all"
								>
									인기순
								</option>
							</select>
						</div>
					</JobSortContainer>
				</div>
				<hr className="divider-filterwdlist" />

				<div className="container-taglist">
					<CustomSlider prev={arrowState} {...settings}>
						{TagListItemDummy.map((value) => (
							<TagListItem
								onClick={(e, content) => {
									handleChangeTags(e, content);
								}}
								hasClicked={tagList.includes(value.content)}
								key={value.idx}
								content={value.content}
								color={value.color}
								imgUrl={value.imgUrl}
							/>
						))}
					</CustomSlider>
				</div>
			</div>

			<hr className="divier" />

			{locationModalVisible && <LocationModal />}

			<JobListContentContainer />
		</JobListContainers>
	);
}

const CustomRcSlider = styled(RcSlider)``;

const CustomSlider = styled(Slider)`
	z-index: 1;

	.slick-list {
		position: relative;
		overflow: hidden;
		display: block;
		margin: 0;
		padding: 0;
		::after,
		::before {
			content: '';
			display: block;
			position: absolute;
			top: 0;
			z-index: 2;
			width: 80px;
			height: 32px;
		}
		::after {
			background: linear-gradient(90deg, hsla(0, 0%, 100%, 0), #fff 50%);
			right: 0;
		}

		::before {
			left: 0;
			background: ${(props) =>
				props.prev !== 0 &&
				'linear-gradient(270deg, hsla(0, 0%, 100%, 0), #fff 50%)'};
		}
	}

	.slick-track {
		position: relative;
		left: 0;
		top: 0;
		display: block;
	}
	.slick-track:before,
	.slick-track:after {
		content: '';
		display: table;
		box-sizing: border-box;
	}
	.slick-track:after {
		clear: both;
	}
	.slick-slide {
		float: left;
		height: 100%;
		min-height: 1px;
		display: block;
	}
`;

const JobSortContainer = styled.div`
	height: 40px;
	position: relative;

	.mobileicon-selector {
		display: none;
		svg:not(:root) {
			overflow: hidden;
		}
	}
	select {
		appearance: none;
		display: none !important;
		padding: 0 15px;
		border: 1px solid #e1e2e3;
		border-radius: 5px;
		background-color: #fff;
		color: #333;
		font-size: 15px;
		font-weight: 400;
		width: 100%;
		height: 40px;
	}
	.customselector-jobsort {
		width: 104px;
		position: relative;
		svg {
			transform: ${(props) => props.issort && 'rotate(180deg)'};
			transition: 0.3s;
		}
		> ul {
			position: absolute;
			top: 39px;
			left: 0;
			right: 0;
			z-index: 98;
			background: #fff;
			border: 1px solid #ececec;
			border-radius: 0 0 4px 4px;
		}
		> ul > li button {
			width: 100%;
			font-size: 14px;
			font-weight: 400;
			font-stretch: normal;
			font-style: normal;
			line-height: 40px;
			letter-spacing: normal;
			color: #333;
			text-align: left;
			padding: 0 0 0 16px;
			border-bottom: 1px solid #ececec;
		}
		> button {
			border-radius: 4px;
			border: 1px solid #ececec;
			background-color: #fff;
			font-size: 14px;
			font-weight: 400;
			height: 40px;
			letter-spacing: normal;
			color: #000;
			padding: 0 16px;
			position: relative;
			width: 100%;
			text-align: left;
			display: flex;
			justify-content: space-between;
			align-items: center;
		}
	}
	.selector-jobsort {
		position: relative;
	}
`;

const JobListContainers = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	position: relative;
	max-width: 1060px;

	.fixed-dummy {
		/* height: ${(props) => props.inView && '60px'}; */
	}
	.divier {
		width: 100vw;
		margin: 0;
		margin-bottom: 38px;
		margin-left: calc(-50vw + 50%);
		height: 1px;
		border: none;
		flex-shrink: 0;
		background-color: #ececec;
	}
	.divider-filterwdlist {
		max-width: 1060px;
		height: 1px;
		margin: 25px auto;
		opacity: 0.3;
		background-color: rgba(236, 236, 236, 0.7);
	}

	.container-filterbtn {
		margin-left: 10px;
		position: relative;
		.container-yearspopup {
			position: absolute;
			width: 468px;
			height: 174px;
			overflow: hidden;
			background-color: #fff;
			z-index: 99;
			border: 1px solid #e1e2e3;
			box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
			border-radius: 5px;
			left: -1px;
			top: 45px;
			.rcSlider-wrapper {
				padding: 5px 27px 35px;
			}
		}
		.header-header {
			display: flex;
			color: #333;
			font-size: 16px;
			font-weight: 400;
			line-height: 19px;
			padding: 30px 15px 20px;
			font-family: inherit;
			div {
				> span:nth-child(2) {
					margin-left: 20px;
					margin-right: 20px;
				}
			}
		}
	}

	.text-filterbtn-display {
		color: #36f !important;
		font-weight: 600;
		margin-right: 0;
		overflow: hidden;
		text-overflow: ellipsis;
		font-size: 14px;
		letter-spacing: normal;
		white-space: nowrap;
	}
	.text-filterbtn {
		margin-right: 15px;
		font-size: 14px;
		font-weight: 400;
		letter-spacing: normal;
		color: #333;
		white-space: nowrap;
	}

	.btn-filterwdlist-first {
		align-items: center;
		display: inline-flex;
		height: 40px;
		padding: 0 15px !important;
		margin-bottom: 0;
		border-radius: 5px;
		border: 1px solid #ececec;
		position: relative;
		background: #fff;
		color: #333;
		font-size: 13px;
		font-weight: 400;
		> svg {
			margin-left: 8px;
			transition: 0.3s;
		}
	}

	.btngroup-filterwdlist {
		display: flex;
		align-items: center;
		flex: auto;
		justify-content: left;
		max-width: none;
	}

	.container-filterwdlist-inner {
		display: flex;
		flex-direction: row;
		justify-content: space-between;
		position: relative;

		width: ${(props) => props.inView && '87.72%'};
		margin: ${(props) => props.inView && '0 auto'};
		max-width: ${(props) => props.inView && '1060px'};
	}

	.container-taglist {
		position: relative;
		width: ${(props) => props.inView && '87.72%'};
		margin: ${(props) => props.inView && '0 auto'};
		max-width: ${(props) => props.inView && '1060px'};
	}
	.container-filterwdlist {
		top: 50px;
		margin-bottom: 25px;
		padding-top: 10px;

		/* position: fixed; */
		position: ${(props) => props.inView && 'fixed'};
		top: ${(props) => props.inView && '50'};
		z-index: ${(props) => props.inView && '2'};
		left: ${(props) => props.inView && '0'};
		right: ${(props) => props.inView && '0'};
		border-bottom: ${(props) => props.inView && '1px solid #36f'};
		background: ${(props) => props.inView && '#fff'};
		padding: ${(props) => props.inView && '20px 0 25px !important'};
	}
`;

export default JobListContainer;
