import { WindowsFilled } from '@ant-design/icons';
import LabelResponseLevel from 'components/LabelResponseLevel';
import React, { useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import styled from 'styled-components';

function WdPage() {
	const { wdIndex } = useParams();
	const ref = useRef();

	const handleScrollPrev = (type) => {
		if (type === 'left') {
			ref.current.scrollBy({
				top: 0,
				left: -1,
				behavior: 'smooth',
			});
		} else {
			ref.current.scrollBy({
				top: 0,
				left: 1,
				behavior: 'smooth',
			});
		}
	};

	return (
		<ComponentWrapper>
			<div className="jobDetail-container">
				<div style={{ position: 'relative' }}>
					<div className="jobcontent-container">
						<section className="jobimage-imgcontainer">
							<div className="jobimage-slider" ref={ref}>
								<Button
									onClick={() => {
										handleScrollPrev('left');
									}}
									className="arrow-left"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<Button
									onClick={() => {
										handleScrollPrev('right');
									}}
									className="arrow-right"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2F2cv2kbezyp0xheqs__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
							</div>
						</section>
						<section className="jobheader">
							<h2>Senior AI Engineer (시니어 AI 개발자)</h2>
							<div>
								<h6 style={{ maxWidth: 'calc(100% - 88px)' }}>
									<a
										href="/company/28654"
										data-company-name="엔도아이(endoAI)"
									>
										엔도아이(endoAI)
									</a>
								</h6>
								<LabelResponseLevel />
								<span className="location-conatiner">
									서울
									<span className="header-dot">.</span>
									한국
								</span>
							</div>
							<div className="taglist">
								<ul>
									<li>
										<a href="/">육아휴직</a>
									</li>
								</ul>
							</div>
						</section>
					</div>
					<div className="jobprocess-container" />
				</div>
			</div>
		</ComponentWrapper>
	);
}

const Button = styled.button`
	position: absolute;
	width: 9%;
	height: 100%;
	font-size: 26px;
	color: #b5b5b5;
	opacity: 0.7;
	z-index: 1;
`;

const ComponentWrapper = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	max-width: 1060px;

	.jobimage-imgcontainer {
		position: relative;
		overflow: hidden;

		.jobimage-slider {
			display: flex;
			flex-wrap: nowrap;
			flex-direction: row;
			align-items: flex-start;
			padding-bottom: 4px;
			height: auto;
			overflow-x: auto;
			scroll-snap-type: x mandatory;
			overscroll-behavior: contain;
			.arrow-right {
				right: 0;
			}
			.arrow-left {
				i {
					display: block;
					transform: rotate(180deg);
				}
			}
			::-webkit-scrollbar {
				height: 10px;
			}
			::-webkit-scrollbar-thumb {
				background-color: #b5b5b5;
				border-radius: 10px;
			}

			.img-jobimage {
				scroll-snap-align: start;
				position: relative;
				width: 100%;
				flex-shrink: 0;
				padding-bottom: 70%;
			}
			img {
				position: absolute;
				width: 100%;
				height: 100%;
				left: 0;
				top: 0;
				border-radius: 4px;
				object-fit: cover;
				outline: 1px solid rgba(0, 0, 0, 0.05);
				outline-offset: -1px;
			}
		}
	}
	.jobcontent-container {
		display: inline-block;
		width: calc(100% - 360px);
		vertical-align: top;
		.jobheader {
			margin: 40px 0 30px;
			> h2 {
				color: #333;
				font-size: 22px;
				font-weight: 600;
				margin: 0 0 10px;
				word-break: keep-all;
				word-wrap: break-word;
			}
			> div > h6 {
				color: #999;
				font-size: 14px;
				font-weight: 600;
				margin: 0;
				display: inline-flex;
			}
			> div > div {
				display: inline-flex;
			}
			.label-response-container {
				display: inline-flex;
				white-space: nowrap;
				margin: 0 0 0 10px;
				position: relative;
				top: -1px;
			}
			> div > span {
				padding: 0 10px;
				position: relative;
				white-space: nowrap;
				margin-left: 10px;
				color: #999;
				:before {
					content: '';
					display: block;
					width: 1px;
					height: 12px;
					background: #e1e2e3;
					position: absolute;
					top: 4px;
					left: 0;
				}
				.header-dot {
					margin: 0 5px;
					top: -3px;
					position: relative;
				}
				> div:after {
					display: block;
					content: '';
					clear: both;
				}
			}
		}
	}
`;

export default WdPage;
