/* eslint-disable consistent-return */
const { default: axios } = require('axios');

export const API = axios.create({
	baseURL: 'https://dev.seojin.shop/',
	timeout: 5000,
	headers: {
		'Content-Type': `application/json;charset=UTF-8`,
		'Access-Control-Allow-Origin': '*',
		Accept: 'application/json',
	},
});

function wrapPromise(promise) {
	let status = 'pending';
	let result;
	const suspender = promise.then(
		(r) => {
			status = 'success';
			result = r;
		},
		(e) => {
			status = 'error';
			result = e;
		},
	);
	return {
		read() {
			if (status === 'pending') {
				throw suspender;
			} else if (status === 'error') {
				throw result;
			} else if (status === 'success') {
				return result;
			}
		},
	};
}

export async function CustomPost({ url, body = null, headers = null }) {
	try {
		const res = await API.post(url, body, { headers });
		console.log(res);
		return wrapPromise(res);
		// return res.data;
	} catch (error) {
		console.log(error);
		throw new Error(error);
	}
}

export async function CustomGet({ url, headers, body }) {
	try {
		const res = await API.get(url);
		return res.data;
	} catch (error) {
		throw new Error(error);
	}
}

function fetchCompanyByTag() {
	console.log('fetch posts...');
	return API.get({
		url: 'web/jobsFeed/companyByTag',
	});
	// return new Promise((resolve) => {
	// 	setTimeout(() => {
	// 		console.log('fetched posts');
	// 		resolve([
	// 			{
	// 				id: 0,
	// 				text: 'I get by with a little help from my friends',
	// 			},
	// 		]);
	// 	}, 2000);
	// });
}

export const fetchJobFreedData = () => {
	const companyByTag = fetchCompanyByTag();
	return {
		user: wrapPromise(companyByTag),
	};
};
