import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { setJobCategoryInvisible } from 'store/action/JobCategoryAction';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

function JobCategory() {
	const dispatch = useDispatch();

	const setJobInvisibleReducer = () => {
		dispatch(setJobCategoryInvisible());
	};

	return (
		<JobCategoryContainer>
			<div>
				<nav
					onMouseLeave={() => {
						setJobInvisibleReducer();
					}}
					className="box-jobcategory"
				>
					<section className="box-maincategory-container">
						<Link to="/wdlist" aria-label="">
							<em>
								<h2>직군 전체</h2>
							</em>
						</Link>
						<ul>
							<li className="category-item">
								<a href="/wdlist/518">
									<em>개발</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/507"
									className=""
									aria-label=""
								>
									<em>경영·비즈니스</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/523"
									className=""
									aria-label=""
								>
									<em>마케팅·광고</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/511"
									className=""
									aria-label=""
								>
									<em>디자인</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/530"
									className=""
									aria-label=""
								>
									<em>영업</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/510"
									className=""
									aria-label=""
								>
									<em>고객서비스·리테일</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/517"
									className=""
									aria-label=""
								>
									<em>HR</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a
									href="/wdlist/959"
									className=""
									aria-label=""
								>
									<em>게임 제작</em>
								</a>
							</li>
							<li
								data-testid="category-item"
								className="category-item"
							>
