import React, { Component } from 'react';
import styled from 'styled-components';

function ArticleCard({ linUrl, imgUrl, title, subtitle, publisher }) {
	return (
		<ComponentWrapper>
			<a href={linUrl}>
				<div className="img-wrapper">
					<img
						src="https://image.wanted.co.kr/optimize?src=http%3A%2F%2Ft1.daumcdn.net%2Fbrunch%2Fservice%2Fuser%2F4BkI%2Fimage%2FtPY3S3sKQEPoSV1ILk5sa93eR_c&amp;w=500&amp;q=75"
						alt="[삼삼한] 목표, 3가지 키워드"
					/>
				</div>
				<p class="ExternalContentCard_ExternalContentCard__Title__4Ce8Z">
					[삼삼한] 목표, 3가지 키워드
				</p>
			</a>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.li`
	display: flex;
	flex-direction: column;
	width: calc(25% - 20px);
	margin: 0 10px 50px;
	.img-wrapper {
		position: relative;
		height: auto;
		padding-bottom: 70%;
		padding-left: 0;
		img {
			position: absolute;
			width: 100%;
			height: 100%;
			left: 0;
			top: 0;
			border-radius: 4px;
			object-fit: cover;
			object-position: top;
			outline: 1px solid rgba(0, 0, 0, 0.05);
			outline-offset: -1px;
		}
	}
`;

export default ArticleCard;
