import React, { Component, useDeferredValue, useEffect, useState } from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';
import HandleNextArrow from './HandleNextArrow';
import HandlePrevArrow from './HandlePrevArrow';
import MainBannerContent from './MainBannerContent';

function TopBanner() {
	const dummyData = [
		{
			idx: 1,
			title: '대규모 신입 채용 프로젝트',
			subtitle: '유니콘,스타트업 채용공고에 지원하세요!',
			imgUrl: 'https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fbanners%2F1680%2Fbdb407ab.jpg&w=1060&q=100',
			linkUrl: '',
		},
		{
			idx: 2,
			title: '도전! 최고의 인재 채용하기',
			subtitle: 'Recruiter가 아닌 Talent Partner가 필요해요!',
			imgUrl: 'https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fbanners%2F1738%2F26401a6a.jpg&w=1060&q=100',
			linkUrl: '',
		},
		{
			idx: 3,
			title: 'OO으로 생산성과 창의성을 높인다!?',
			subtitle: '그 비밀을 지금 확인해 보세요!',
			imgUrl: 'https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fbanners%2F1739%2F479066c4.jpg&w=1060&q=100',
			linkUrl: '',
		},
	];

	const settings = {
		dots: false,
		autoplay: true,
		autoplaySpeed: 2000,
		infinite: true,
		speed: 600,
		slidesToShow: 1,
		slidesToScroll: 1,
		centerMode: true,
		variableWidth: true,
		nextArrow: <HandleNextArrow />,
		prevArrow: <HandlePrevArrow />,
		arrows: true,
	};

	return (
		<ComponentWrapper>
			<CustomSlider {...settings}>
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
			</CustomSlider>
		</ComponentWrapper>
	);
}

const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	touch-action: pan-y;

	.slick-list {
		padding: 0px 50px;
		position: relative;
		overflow: hidden;
		display: block;
		margin: 0;
	}

	.slick-slide {
		display: inline-block;
		padding: 0 12px;
		float: left;
		width: 1060px;
		/* width: ${(props) => props.width || '1060px'}; */
	}
`;

const ComponentWrapper = styled.div`
	height: auto;
	position: relative;
	overflow: hidden;
`;

export default TopBanner;
