import Divider from 'pages/home/Divider';
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Link, useLocation, useParams } from 'react-router-dom';
import { JobMainCategoryDummy } from 'utils/mockData';
import LocationModal from 'components/wdlist/LocationModal';
import JobListContainer from './JobListContainer';

function WdlistPage() {
	const [isMoreJob, setIsmoreJob] = useState(false);
	const [isMoreSubJob, setIsMoreSubJob] = useState(false);
	// Index 값
	const { wdIndex } = useParams();

	const [tagList, setPageTagList] = useState([]);
	const [title, setTitle] = useState('전체');
	const [jobMainCategory, setJobMainCategory] = useState({});

	const [prevCareer, setPagePrevCareer] = useState(0);
	const [nextCareer, setPageNextCareer] = useState(10);
	// wlist=518?country=kr&job_sort=company.response_rate_order&years=2&years=7&locations=all

	console.log(jobMainCategory);
	useEffect(() => {
		if (wdIndex) {
			const temp = JobMainCategoryDummy.filter(
				(item) => item.idx.toString() === wdIndex,
			);
			setJobMainCategory(temp[0]);
			setTitle(temp[0].title);
		} else {
			setTitle('전체');
		}
	}, [wdIndex]);

	return (
		<WdListPageComponent>
			<article className="navbar-category-container">
				<div className="navbar-categorymain">
					<MoreButton
						clicked={isMoreJob}
						onClick={() => {
							setIsmoreJob(!isMoreJob);
						}}
					>
						<span className="text-jobgroup-title">{title}</span>
						<span className="btn-jobgroup">
							<svg
								xmlns="https://www.w3.org/2000/svg"
								width="12"
								height="12"
								viewBox="0 0 12 12"
							>
								<path
									fill="#666"
									fillRule="nonzero"
									d="M2.28 3.22a.75.75 0 0 0-1.06 1.06l4.25 4.25a.75.75 0 0 0 1.06 0l4.25-4.25a.75.75 0 0 0-1.06-1.06L6 6.94 2.28 3.22z"
								/>
							</svg>
						</span>
					</MoreButton>

					{isMoreJob && (
						<JobGroupOverlay>
							<Link
								className={
									title === '전체' ? 'active' : undefined
								}
								to="/wdlist"
								onClick={() => {
									setIsmoreJob(false);
								}}
							>
								전체
								{tagList}
							</Link>
							<ul>
								{JobMainCategoryDummy.map((value) => (
									<li>
										<Link
											className={
												title === value.title
													? 'active'
													: undefined
											}
											to={`/wdlist/${value.idx}`}
											onClick={() => {
												setIsmoreJob(false);
											}}
										>
											{value.title}
										</Link>
									</li>
								))}
							</ul>
						</JobGroupOverlay>
					)}

					<div className="box-jobcategory">
						{/* <span className="btn-jobcategorytitle-empty">
							직군을 선택해주세요.
						</span> */}

						<MoreButton
							clicked={isMoreSubJob}
							onClick={() => {
								setIsMoreSubJob(!isMoreSubJob);
							}}
						>
							<span
								className="text-jobgroup-title"
								style={{ fontWeight: '400' }}
							>
								{title} 전체
							</span>
							<span className="btn-jobgroup">
								<svg
									xmlns="https://www.w3.org/2000/svg"
									width="12"
									height="12"
									viewBox="0 0 12 12"
								>
									<path
										fill="#666"
										fillRule="nonzero"
										d="M2.28 3.22a.75.75 0 0 0-1.06 1.06l4.25 4.25a.75.75 0 0 0 1.06 0l4.25-4.25a.75.75 0 0 0-1.06-1.06L6 6.94 2.28 3.22z"
									/>
								</svg>
							</span>
						</MoreButton>
						<JubJobGroupOverlay>
							<div className="container-subjob-top">
								<p className="text-subjob-title">
									직무를 선택해 주세요. (최대 5개 선택 가능)
								</p>
								{jobMainCategory.itemList.map((value) => (
									<div className="container-jobcategorylist">
										<button
											type="button"
											className="btn-jobcategoryitem"
										>
											{value.title}
										</button>
									</div>
								))}
							</div>
							<button type="button">
								<span className="button-label">
									선택 완료하기
								</span>
							</button>
							<div className="container-subjob-bottom" />
						</JubJobGroupOverlay>
					</div>
				</div>
			</article>

			<JobListContainer
				setPagePrevCareer={setPagePrevCareer}
				setPageNextCareer={setPageNextCareer}
				setPageTagList={setPageTagList}
			/>
		</WdListPageComponent>
	);
}

const JubJobGroupOverlay = styled.section`
	position: absolute;
	left: 0;
	bottom: 0;
	top: 40px;
	width: calc(100% - 140px);
	max-width: 910px;
	display: table;
	height: fit-content;
	background-color: #fff;
	border: 1px solid #e1e2e3;
	-webkit-box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	border-radius: 5px;
	z-index: 99;

	.btn-jobcategoryitem {
		// Actvie
		border: 1px solid #36f;
		color: #36f;
		background-color: #fff;
		font-weight: 600;

		height: 32px;
		margin-right: 10px;
		margin-bottom: 12px;
		border: 1px solid #f2f4f7;
		background: #f2f4f7;
		border-radius: 20px;
		padding: 8px 14px;
		font-size: 13px;
		line-height: 16px;
		font-weight: 400;
		color: #333;
		:hover {
			border: 1px solid rgba(51, 102, 255, 0.3);
		}
	}
	.container-jobcategorylist {
		display: flex;
		flex-wrap: wrap;
	}
	.text-subjob-title {
		color: #666;
		font-weight: 400;
		font-size: 12px;
		line-height: 14.4px;
		margin-bottom: 10px;
	}
	.container-subjob-top {
		padding: 25px 25px 8px;
		max-width: 910px;
	}
	.container-subjob-bottom {
		padding: 16px;
		text-align: right;
		border-top: 1px solid #ececec;
		background-color: #fff;
	}
`;

const JobGroupOverlay = styled.section`
	position: absolute;
	max-width: 190px;
	left: 0;
	right: 0;
	bottom: 0;
	top: 50px;
	overflow-y: scroll;
	height: 70vh;
	background-color: #fff;
	border: 1px solid #e1e2e3;
	box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	border-radius: 5px;
	z-index: 99;
	padding: 15px 0;

	.active {
		color: #36f;
	}

	::-webkit-scrollbar {
		width: 6px; /* 스크롤바의 너비 */
	}

	::-webkit-scrollbar-thumb {
		height: 30%; /* 스크롤바의 길이 */
		background: #cccccc; /* 스크롤바의 색상 */

		border-radius: 10px;
	}

	a {
		font-weight: 500;
		font-size: 16px;
		padding: 10px 0 10px 25px;
		display: flex;
		color: #333;
		align-items: center;
		:hover {
			background-color: #f8f8f8;
			color: #36f;
		}
	}
`;

const MoreButton = styled.button`
	display: flex;
	align-items: center;
	white-space: nowrap;

	.text-jobgroup-title {
		padding-top: 3px;
		margin-right: 15.5px;
		font-size: 24px;
		line-height: 29px;
		font-weight: 700;
		color: #333;
	}
	.btn-jobgroup {
		transform: ${(props) => props.clicked && 'rotate(180deg)'};
		position: relative;
		border: 1px solid #e1e2e3;
		border-radius: 9999px;
		background-color: #fff;
		width: 26px;
		height: 26px;
		transition: 0.3s;
		flex-shrink: 0;
		svg {
			margin-top: 7px;
		}
	}
`;

const WdListPageComponent = styled.div`
	background: #fff;
	min-height: calc(100vh - 50px);
	overflow-x: hidden;
	font-family: inherit;
	position: relative;

	.navbar-category-container {
		background: #fff;
