import Divider from 'pages/home/Divider';
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import { JobMainCategoryDummy } from 'utils/mockData';
import LocationModal from 'components/wdlist/LocationModal';
import { useDispatch, useSelector } from 'react-redux';
import { setToastModalVisible } from 'store/action/ToastModalAction';
import { setDutyCategoryIdsReducer } from 'store/action/WdListAction';
import JobListContainer from './JobListContainer';

function WdlistPage() {
	const navigate = useNavigate();
	const dispatch = useDispatch();
	const { wdIndex, subJobCategory } = useParams();

	const { locations, years, userTags, jobSort, country, dutyCategoryIds } =
		useSelector((state) => state.WdListReducer);
	const [isMoreJob, setIsmoreJob] = useState(false);
	const [isMoreSubJob, setIsMoreSubJob] = useState(false);
	const [title, setTitle] = useState('전체');
	const [jobMainCategory, setJobMainCategory] = useState({});
	const [selectedSubjobList, setSelectedSubjoblist] = useState([]);

	// console.log(wdIndex);
	// console.log(subJobCategory);
	// console.log(dutyCategoryIds);

	const confirmUrl = () => {
		// locations, years, userTags, jobSort, country, dutyCategoryIds
		let finalUrl = '';
		if (userTags.length >= 0) {
			finalUrl += `${userTags
				.map((item) => `user_tags=${item}`)
				.join('&')}`;
		}
		finalUrl += `&${dutyCategoryIds
			.map((item) => `dutyCategoryIds=${item}`)
			.join('&')}`;
		finalUrl += `&years=${years[0]}&years=${years[1]}`;
		finalUrl += `&${locations
			.map((item) => `locations=${item}`)
			.join('&')}`;
		navigate(
			`/wdlist/${wdIndex}?country=${country}&job_sort=${jobSort}${finalUrl}`,
		);
	};

	const handleClickSubJob = (value) => {
		if (selectedSubjobList.length >= 5) {
			if (value.idx === -1) {
				setSelectedSubjoblist([value]);
				return;
			}
			if (selectedSubjobList.includes(value)) {
				const temp = selectedSubjobList.filter(
					(element) => element !== value,
				);
				setSelectedSubjoblist(temp);
				return;
			}
			dispatch(
				setToastModalVisible({
					text: '직무는 최대 5개까지 선택 가능합니다.',
				}),
			);
		} else {
			if (selectedSubjobList.includes(value)) {
				const temp = selectedSubjobList.filter(
					(element) => element !== value,
				);
				setSelectedSubjoblist(temp);
			} else {
				let temp = selectedSubjobList.filter(
					(element) => element.idx !== -1,
				);
				temp = [...temp, value];
				setSelectedSubjoblist(temp);
			}
			if (value.idx === -1) {
				setSelectedSubjoblist([value]);
			}
		}
	};

	const handleChangeSelectedSubJob = (e, type) => {
		if (type === 'cancle') {
			setIsMoreSubJob(!isMoreSubJob);
			if (jobMainCategory.itemList !== undefined) {
				// setSelectedSubjoblist([jobMainCategory.itemList[0]]);
				setSelectedSubjoblist(selectedSubjobList);
			} else {
				setSelectedSubjoblist([]);
			}
		}
		if (type === 'submit') {
			setIsMoreSubJob(false);
			const result = selectedSubjobList.map((a) => a.idx);
			dispatch(setDutyCategoryIdsReducer({ dutyCategoryIds: result }));
		}
	};

	useEffect(() => {
		if (wdIndex) {
			// WdIndex 정보 불러오기
			const temp = JobMainCategoryDummy.filter(
				(item) => item.idx.toString() === wdIndex,
			);
			setJobMainCategory(temp[0]);
			setTitle(temp[0].title);
			// 더미리스트 데이터에 없으면 예외처리
			if (temp[0].itemList !== undefined) {
				setSelectedSubjoblist([temp[0].itemList[0]]);
				dispatch(setDutyCategoryIdsReducer({ dutyCategoryIds: [-1] }));
			} else {
				setSelectedSubjoblist(null);
			}
			confirmUrl();
		} else {
			setTitle('전체');
			setSelectedSubjoblist(null);
		}
	}, [wdIndex]);

	useEffect(() => {
		confirmUrl();
	}, [locations, years, userTags, jobSort, country, dutyCategoryIds]);

	return (
		<WdListPageComponent>
			<article className="navbar-category-container">
				<div className="navbar-categorymain">
					<MoreButton
						clicked={isMoreJob}
						onClick={() => {
							setIsmoreJob(!isMoreJob);
						}}
					>
						<span className="text-jobgroup-title">{title}</span>
						<span className="btn-jobgroup">
							<svg
								xmlns="https://www.w3.org/2000/svg"
								width="12"
								height="12"
								viewBox="0 0 12 12"
							>
								<path
									fill="#666"
									fillRule="nonzero"
									d="M2.28 3.22a.75.75 0 0 0-1.06 1.06l4.25 4.25a.75.75 0 0 0 1.06 0l4.25-4.25a.75.75 0 0 0-1.06-1.06L6 6.94 2.28 3.22z"
								/>
							</svg>
						</span>
					</MoreButton>

					{isMoreJob && (
						<JobGroupOverlay>
							<Link
								className={
									title === '전체' ? 'active' : undefined
								}
								to="/wdlist"
								onClick={() => {
									setIsmoreJob(false);
								}}
							>
								전체
							</Link>
							<ul>
								{JobMainCategoryDummy.map((value) => (
									<li>
										<Link
											className={
												title === value.title
													? 'active'
													: undefined
											}
											to={`/wdlist/${value.idx}`}
											onClick={() => {
												setIsmoreJob(false);
											}}
										>
											{value.title}
										</Link>
									</li>
								))}
							</ul>
						</JobGroupOverlay>
					)}

					<div className="box-jobcategory">
						{selectedSubjobList === null ? (
							<span className="btn-jobcategorytitle-empty">
								직군을 선택해주세요.
							</span>
						) : (
							<>
								<MoreButton
									clicked={isMoreSubJob}
									onClick={(e) => {
										handleChangeSelectedSubJob(e, 'cancle');
									}}
								>
									<span
										className="text-jobgroup-title"
										style={{ fontWeight: '400' }}
									>
										{dutyCategoryIds.length === 1 &&
										dutyCategoryIds[0] === -1
											? `${title} 전체`
											: dutyCategoryIds.map((value) => {
													const dataList =
														JobMainCategoryDummy.find(
															(item) =>
																item.idx.toString() ===
																wdIndex,
														);
													if (
														dataList.itemList ===
														undefined
													) {
														return '';
													}
													const result =
														dataList.itemList?.find(
															(item) =>
																item.idx ===
																value,
														);
													return `${result.title}, `;
											  })}
									</span>
									<span className="btn-jobgroup">
										<svg
											xmlns="https://www.w3.org/2000/svg"
											width="12"
											height="12"
											viewBox="0 0 12 12"
										>
											<path
												fill="#666"
												fillRule="nonzero"
												d="M2.28 3.22a.75.75 0 0 0-1.06 1.06l4.25 4.25a.75.75 0 0 0 1.06 0l4.25-4.25a.75.75 0 0 0-1.06-1.06L6 6.94 2.28 3.22z"
											/>
										</svg>
									</span>
								</MoreButton>

								{isMoreSubJob && (
									<JubJobGroupOverlay>
										<div className="container-subjob-top">
											<p className="text-subjob-title">
												직무를 선택해 주세요. (최대 5개
												선택 가능)
											</p>
											<div className="container-jobcategorylist">
												{jobMainCategory.itemList?.map(
													(value) => (
														<button
															type="button"
															onClick={() => {
																handleClickSubJob(
																	value,
																);
															}}
															className={`btn-jobcategoryitem ${
																selectedSubjobList.includes(
																	value,
																) && 'selected'
															}`}
														>
															{value.title}
														</button>
													),
												)}
											</div>
										</div>

										<div className="container-subjob-bottom">
											<button
												onClick={(e) =>
													handleChangeSelectedSubJob(
														e,
														'submit',
													)
												}
												type="button"
											>
												선택 완료하기
											</button>
										</div>
									</JubJobGroupOverlay>
								)}
							</>
						)}
					</div>
				</div>
			</article>

			<JobListContainer />
		</WdListPageComponent>
	);
}

const JubJobGroupOverlay = styled.section`
	position: absolute;
	left: 0;
	bottom: 0;
	top: 40px;
	width: calc(100% - 140px);
	max-width: 910px;
	display: table;
	height: fit-content;
	background-color: #fff;
	border: 1px solid #e1e2e3;
	-webkit-box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	border-radius: 5px;
	z-index: 99;

	.btn-jobcategoryitem {
		// Actvie
		border: 1px solid #36f;
		color: #36f;
		background-color: #fff;
		font-weight: 600;

		height: 32px;
		margin-right: 10px;
		margin-bottom: 12px;
		border: 1px solid #f2f4f7;
		background: #f2f4f7;
		border-radius: 20px;
		padding: 8px 14px;
		font-size: 13px;
		line-height: 16px;
		font-weight: 400;
		color: #333;
		:hover {
			border: 1px solid rgba(51, 102, 255, 0.3);
		}
	}
	.container-jobcategorylist {
		display: flex;
		flex-wrap: wrap;
		button {
			height: 32px;
			margin-right: 10px;
			margin-bottom: 12px;
			border: 1px solid #f2f4f7;
			background: #f2f4f7;
			border-radius: 20px;
			padding: 8px 14px;
			font-size: 13px;
			line-height: 16px;
			font-weight: 400;
			color: #333;
		}
		.selected {
			border: 1px solid #36f;
			color: #36f;
			background-color: #fff;
			font-weight: 600;
		}
	}
	.text-subjob-title {
		color: #666;
		font-weight: 400;
		font-size: 12px;
		line-height: 14.4px;
		margin-bottom: 10px;
	}
	.container-subjob-top {
		padding: 25px 25px 8px;
		max-width: 910px;
	}
	.container-subjob-bottom {
		padding: 16px;
		text-align: right;
		border-top: 1px solid #ececec;
		background-color: #fff;
		button {
			width: 160px;
			height: 40px;
			font-size: 15px;
			color: #fff;
			background-color: #36f;
			border: none;
			min-width: 64px;
			padding: 0 27px;
			-webkit-box-sizing: border-box;
			box-sizing: border-box;
			border-radius: 25px;
			font-weight: 700;
			line-height: normal;
		}
	}
`;

const JobGroupOverlay = styled.section`
	position: absolute;
	max-width: 190px;
	left: 0;
	right: 0;
	bottom: 0;
	top: 50px;
	overflow-y: scroll;
	height: 70vh;
	background-color: #fff;
	border: 1px solid #e1e2e3;
	box-shadow: 0 4px 8px rgb(0 0 0 / 15%);
	border-radius: 5px;
	z-index: 99;
	padding: 15px 0;

	.active {
		color: #36f;
	}

	::-webkit-scrollbar {
		width: 6px; /* 스크롤바의 너비 */
	}

	::-webkit-scrollbar-thumb {
		height: 30%; /* 스크롤바의 길이 */
		background: #cccccc; /* 스크롤바의 색상 */

		border-radius: 10px;
	}

	a {
		font-weight: 500;
		font-size: 16px;
		padding: 10px 0 10px 25px;
		display: flex;
		color: #333;
		align-items: center;
		:hover {
			background-color: #f8f8f8;
			color: #36f;
		}
	}
`;

const MoreButton = styled.button`
	display: flex;
	align-items: center;
	white-space: nowrap;

	.text-jobgroup-title {
		padding-top: 3px;
		margin-right: 15.5px;
		font-size: 24px;
		line-height: 29px;
		font-weight: 700;
		color: #333;
	}
	.btn-jobgroup {
		transform: ${(props) => props.clicked && 'rotate(180deg)'};
		position: relative;
		border: 1px solid #e1e2e3;
		border-radius: 9999px;
		background-color: #fff;
		width: 26px;
		height: 26px;
		transition: 0.3s;
		flex-shrink: 0;
		svg {
			margin-top: 7px;
		}
	}
`;

const WdListPageComponent = styled.div`
	background: #fff;
	min-height: calc(100vh - 50px);
	overflow-x: hidden;
	font-family: inherit;
	position: relative;

	.navbar-category-container {
		background: #fff;
		padding-top: 40px;
	}
	.navbar-categorymain {
		margin: 0 auto;
		width: 87.72%;
		position: relative;
		display: flex;
		max-width: 1060px;
		align-items: center;
	}

	.box-jobcategory {
		display: flex;
		align-items: center;
		white-space: nowrap;
		:before {
			content: '|';
			font-size: 28px;
			line-height: 29px;
			padding: 3px 24px 0;
			color: #ececec;
		}
	}
	.btn-jobcategorytitle-empty {
		padding-top: 3px;
		font-size: 24px;
		line-height: 29px;
		font-weight: 400;
		color: #aaa;
	}
`;

export default WdlistPage;
