/* eslint-disable no-return-assign */
import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { Link, Outlet, useLocation } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setLoginModalVisible } from 'store/action/LoginAction';
import { setJobCategoryVisible } from 'store/action/JobCategoryAction';
import UserPopup from 'components/UserPopup';
import { useOutsideClick } from 'hoc/useOutsideClick';
import JobCategory from './JobCategory';
import { PaddingHeader } from './styled';

function Header() {
	const dispatch = useDispatch();
	const { pathname } = useLocation();
	const { profileImageUrl, userId, isLogin } = useSelector(
		(state) => state.UserDataReducer,
	);
	const userpopupRef = useRef();
	const { jobcategoryVisible } = useSelector(
		(state) => state.JobCategoryReducer,
	);
	// const [isUserPopup, setIsUserPopup] = useState(false);
	const [isUserPopup, setIsUserPopup] = useOutsideClick(userpopupRef, false);

	const handleClickOutside = (e, onClick) => {
		console.log(e.target);
		if (!userpopupRef.current || !userpopupRef.current.contains(e.target)) {
			console.log('들어옴1');
			setIsUserPopup(false);
		}
	};

	useEffect(() => {
		document.addEventListener('mousedown', handleClickOutside);
		return () => {
			document.removeEventListener('mousedown', handleClickOutside);
		};
	});

	const setJobVisibleReducer = () => {
		dispatch(setJobCategoryVisible());
	};

	const handleLoginVisible = () => {
		dispatch(setLoginModalVisible());
	};

	return (
		<>
			<ComponentsWrapper>
				<div className="box-mainbar">
					<nav className="box-mainbar-nav">
						<NavLogo>
							<button
								type="button"
								onMouseEnter={() => setJobVisibleReducer()}
							>
								<img
									src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Ficon-menu.png&amp;w=undefined&amp;q=75"
									alt="hamberger menu"
								/>
							</button>
							<Link to="/">
								<i className="icon-logo-new" />
							</Link>
						</NavLogo>
						<MenuClassName>
							<li
								className={
									pathname.includes('jobsfeed') &&
									'selectedNav'
								}
							>
								<Link to="/jobsfeed">채용</Link>
							</li>
							<li
								className={
									pathname.includes('events') && 'selectedNav'
								}
							>
								<Link to="/events">이벤트</Link>
							</li>
							<li
								className={
									pathname.includes('salary') && 'selectedNav'
								}
							>
								<Link to="/salary">직군별 연봉</Link>
							</li>
							<li
								className={
									pathname.includes('cv') && 'selectedNav'
								}
							>
								<Link to="/cv/list">이력서</Link>
							</li>
							<li
								className={
									pathname.includes('community') &&
									'selectedNav'
								}
							>
								<Link to="/community">
									커뮤니티
									<em>
										<svg
											xmlns="http://www.w3.org/2000/svg"
											width="18"
											height="11"
											viewBox="0 0 18 11"
										>
											<g
												fill="none"
												fillRule="evenodd"
												fontFamily="AppleSDGothicNeo-SemiBold, Apple SD Gothic Neo"
												fontSize="9"
												fontWeight="500"
											>
												<g fill="#36F">
													<g>
														<g>
															<g>
																<g>
																	<g>
																		<g>
																			<text transform="translate(-931.000000, -13.000000) translate(224.000000, 7.000000) translate(210.000000, 6.000000) translate(350.000000, 0.000000) translate(147.000000, 0.000000)">
																				<tspan
																					x="0"
																					y="8"
																				>
																					New
																				</tspan>
																			</text>
																		</g>
																	</g>
																</g>
															</g>
														</g>
													</g>
												</g>
											</g>
										</svg>
									</em>
								</Link>
							</li>
							<li>
								<Link to="https://www.wanted.co.kr/gigs/experts">
									프리랜서
								</Link>
							</li>
							<li>
								<Link to="/aiscore/resume">
									AI 합격예측
									<span>
										<svg
											xmlns="http://www.w3.org/2000/svg"
											width="18"
											height="11"
											viewBox="0 0 18 11"
										>
											<g
												fill="none"
												fillRule="evenodd"
												fontFamily="AppleSDGothicNeo-SemiBold, Apple SD Gothic Neo"
												fontSize="9"
												fontWeight="500"
											>
												<g fill="#36F">
													<g>
														<g>
															<g>
																<g>
																	<g>
																		<g>
																			<text transform="translate(-931.000000, -13.000000) translate(224.000000, 7.000000) translate(210.000000, 6.000000) translate(350.000000, 0.000000) translate(147.000000, 0.000000)">
																				<tspan
																					x="0"
																					y="8"
																				>
																					Beta
																				</tspan>
																			</text>
																		</g>
																	</g>
																</g>
															</g>
														</g>
													</g>
												</g>
											</g>
										</svg>
									</span>
								</Link>
							</li>
						</MenuClassName>
						<Aside>
							<ul>
								<li>
									<button
										className="btn-search"
										type="button"
									>
										<svg
											xmlns="https://www.w3.org/2000/svg"
											width="18"
											height="18"
											viewBox="0 0 18 18"
										>
											<defs>
												<path
													id="qt2dnsql4a"
													d="M15.727 17.273a.563.563 0 10.796-.796l-4.875-4.875-.19-.165a.563.563 0 00-.764.028 5.063 5.063 0 111.261-2.068.562.562 0 101.073.338 6.188 6.188 0 10-1.943 2.894l4.642 4.644z"
												/>
											</defs>
											<g fill="none" fillRule="evenodd">
												<use
													fill="#333"
													fillRule="nonzero"
													stroke="#333"
													strokeWidth=".3"
													xlinkHref="#qt2dnsql4a"
												/>
											</g>
										</svg>
									</button>
								</li>
								{isLogin ? (
									<>
										<li>
											<button
												type="button"
												className="btn-notification"
											>
												<svg
													xmlns="https://www.w3.org/2000/svg"
													width="18"
													height="18"
													viewBox="0 0 18 18"
												>
													<defs>
														<path
															id="bpnpn3yn0a"
															d="M7.554 14.813h3.183a1.689 1.689 0 01-3.183 0zm1.592 2.25a2.813 2.813 0 002.812-2.813.563.563 0 00-.562-.563h-7.5c-.31 0-.541-.014-.699-.04.018-.036.04-.077.066-.123.036-.065.354-.605.46-.8.477-.875.735-1.676.735-2.599V6.75c0-2.656 2.057-4.688 4.688-4.688 2.63 0 4.687 2.032 4.687 4.688v3.375c0 .923.258 1.724.736 2.6.106.194.424.734.46.799.026.046.047.087.065.123-.157.026-.389.04-.698.04a.564.564 0 000 1.126c1.263 0 1.896-.221 1.896-1.002 0-.26-.092-.494-.28-.833-.045-.083-.361-.619-.456-.792-.395-.724-.598-1.355-.598-2.061V6.75c0-3.28-2.563-5.813-5.812-5.813S3.333 3.47 3.333 6.75v3.375c0 .706-.203 1.337-.598 2.06-.094.174-.41.71-.456.793-.188.339-.279.572-.279.833 0 .78.632 1.002 1.896 1.002H6.39a2.813 2.813 0 002.756 2.25z"
														/>
													</defs>
													<g
														fill="none"
														fillRule="evenodd"
													>
														<g transform="translate(-1079 -16) translate(224 7) translate(855 9)">
															<mask
																id="1dencd96ob"
																fill="#fff"
															>
																<use xlinkHref="#bpnpn3yn0a" />
															</mask>
															<use
																fillRule="nonzero"
																stroke="currentColor"
																strokeWidth=".3"
																xlinkHref="#bpnpn3yn0a"
															/>
															<g
																fill="currentColor"
																mask="url(#1dencd96ob)"
															>
																<path d="M0 0H18V18H0z" />
															</g>
														</g>
													</g>
												</svg>
											</button>
										</li>
										<li
											className="profilebox"
											onClick={(e) => {
												setIsUserPopup(!isUserPopup);
											}}
											role="presentation"
										>
											<button
												type="button"
												className="btn-frofile"
											>
												<div className="avatarBorder">
													<div
														className="avatarImage"
														ref={userpopupRef}
														style={{
															backgroundImage: `url(${profileImageUrl}), url(https://static.wanted.co.kr/images/profile_default.png)`,
														}}
													/>
												</div>
											</button>
											{/* <span
													className="Badge_Badge__BJTzL"
													style={{
														backgroundColor: `rgb(51, 102, 255)`,
														width: `13px`,
														height: `13px`,
													}}
												>
													<svg
														className=""
														width="5"
														height="5"
														viewBox="0 0 6 6"
													>
														<g
															fill="#fff"
															fillRule="nonzero"
														>
															<path
																d="M6.647 11L6.647 7.259 6.688 7.259 9.158 11 11 11 11 5 9.353 5 9.353 8.357 9.322 8.357 7.089 5 5 5 5 11z"
																transform="translate(-123 -375) translate(20 365) translate(98 5)"
															/>
														</g>
													</svg>
												</span> */}
											{isUserPopup && <UserPopup />}
										</li>
									</>
								) : (
									<button
										onClick={() => {
											handleLoginVisible();
										}}
										className="btn-signup"
										type="button"
									>
										회원가입/로그인
									</button>
								)}
								<li className="left-division">
									<Link
										className="btn-dashboard"
										to="/dashboard"
									>
										기업 서비스
									</Link>
								</li>
							</ul>
						</Aside>
					</nav>
					{jobcategoryVisible && <JobCategory />}
				</div>
			</ComponentsWrapper>
			<PaddingHeader />
			<Outlet />
		</>
	);
}

const Aside = styled.aside`
	height: 100%;
	padding: 10px 0;
	> ul {
		height: 100%;
		margin: 0;
		.left-division {
			display: inline-flex;
			::before {
				display: block;
				content: '';
				width: 1px;
				height: 10px;
				background-color: #e1e2e3;
				margin: auto 10px auto 5px;
			}
		}
	}
	.profilebox {
		display: inline-flex;
		flex-direction: row;
		align-items: center;
		margin-right: 5px;
		.avatarBorder {
			width: 32px;
			height: 32px;
			border-radius: 50%;
			border: 1px solid #e1e2e3;
			background-color: #fff;
			display: flex;
			justify-content: center;
			align-items: center;
			overflow: hidden;
			> div {
				width: 28px;
				height: 28px;
				border-radius: 50%;
				background-position: 50%;
				background-size: cover;
				background-repeat: no-repeat;
			}
		}
	}
	> ul li {
		position: relative;
		display: inline-block;
		height: 100%;
		vertical-align: middle;
		margin-right: 5px;
		* {
			font-size: 14px;
			color: #333;
			font-weight: 600;
			line-height: 1;
		}
		.btn-search {
			position: relative;
			margin-top: 5px;
			padding: 0 5px;
		}
		.btn-signup {
			margin-right: 6px;
			line-height: 1.4;
		}
		.btn-notification {
			position: relative;
			margin-top: 5px;
			padding: 0 5px;
		}
		.btn-profile {
			height: 32px;
			display: flex;
			align-items: center;
			justify-content: space-between;
			vertical-align: top;
			padding: 0;
			margin-right: 11px;
		}
		.btn-dashboard {
			font-size: 13px;
			color: #666;
			line-height: 30px;
			height: 30px;
			border: 1px solid #e1e2e3;
			border-radius: 15px;
			padding: 0 10px;
			margin-left: 15px;
			font-weight: 400;
			display: inline-block;
			vertical-align: bottom;
		}
	}
`;

const NavLogo = styled.div`
	display: flex;
	align-items: center;
	justify-content: space-between;
	button {
		margin-top: -2px;
		margin-right: 15px;
		img {
			width: 17px;
			height: 14px;
			object-fit: contain;
		}
	}
	i {
		font-family: 'Elysio Bold' !important;
		line-height: 0.7;
		font-size: 23px;
	}
	.icon-logo-new::before {
		content: 'wanted';
	}
`;

const MenuClassName = styled.ul`
	height: inherit;
	text-align: center;
	margin: 0;
	.selectedNav {
		box-shadow: inset 0 -2px #258bf7;
	}
	li {
		height: inherit;
		display: inline-block;
		a {
			position: relative;
			vertical-align: middle;
			font-size: 14px;
			line-height: 20px;
			font-weight: 600;
			padding: 15px;
			display: inline-block;
			:hover {
				box-shadow: inset 0 -2px #ddd;
			}
			em {
				position: absolute;
				top: 10px;
				right: -5px;
				pointer-events: none;
				font-style: normal;
			}
		}
		a:after {
			height: 100%;
			content: '';
			display: inline-block;
			vertical-align: middle;
		}
	}
`;

const ComponentsWrapper = styled.div`
	position: fixed;
	padding-right: initial;
	width: 100%;
	background-color: #fff;
	box-shadow: 0 1px 0 0 rgb(0 0 0 / 10%);
	z-index: 800;
	.box-mainbar {
		margin: 0 auto;
		height: 50px;
		width: 90%;
		position: relative;
		max-width: 1060px;
		.box-mainbar-nav {
			display: flex;
			align-items: center;
			justify-content: space-between;
			flex-direction: row;
			flex-wrap: nowrap;
		}
	}
`;

export default Header;
