import axios from 'axios';
import { useEffect, useState } from 'react';

const useHandleNetwork = () => {
	// const [data, setData] = useState();
	// const [isLoading, setIsLoading] = useState(false);
	// const [error, setError] = useState('');

	const Get = ({ url, header, body }) => {
		let result;
		const fetchData = async () => {
			try {
				await axios(url)
					.then((res) => {
						result = res.data;
					})
					.finally(() => {});
			} catch (error) {
				throw new Error(error);
			}
		};

		return { result };
	};

	function Post({ url, header, body }) {
		let result;

		const fetchData = async () => {
			try {
				await axios
					.post(url, body)
					.then((res) => {
						result = res.data;
					})
					.finally((res) => {
						console.log(result);
					});
			} catch (error) {
				throw new Error(error);
			}
		};

		return fetchData;
	}

	return { Get, Post };
};

export default useHandleNetwork;
