import ArticleCard from 'components/common/ArticleCard';
import Articletitle from 'components/common/Articletitle';
import HandleNextArrow from 'components/common/HandleNextArrow';
import HandlePrevArrow from 'components/common/HandlePrevArrow';
import React from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';
import { HomeChipDummydata } from 'utils/mockData';

function ArticleWrapper() {
	const settings = {
		dots: false,
		// autoplay: true,
		// autoplaySpeed: 2000,
		infinite: true,
		speed: 600,
		slidesToShow: 4,
		slidesToScroll: 3,
		centerMode: true,
		variableWidth: true,
		// nextArrow: <HandleNextArrow />,
		// prevArrow: <HandlePrevArrow />,
		arrows: true,
	};

	return (
		<>
			<Articletitle
				title="3분만에 읽는 Wanted+ 아티클"
				subtitle="아티클 전체보기"
				icon={false}
			/>
			<CustomSlider {...settings}>
				<ArticleCard />
				<ArticleCard />
				<ArticleCard />
				<ArticleCard />
				<ArticleCard />
			</CustomSlider>
		</>
	);
}

const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	touch-action: pan-y;

	.slick-active {
		filter: brightness(100%) !important;
	}

	.slick-list {
		padding: 0px 50px;
		position: relative;
		overflow: hidden;
		display: block;
		margin: 0;
	}

	.slick-slide {
		display: inline-block;
		filter: brightness(50%);
		padding: 0 12px;
		/* width: ${(props) => props.width || '1060px'}; */
	}
`;

export default ArticleWrapper;
