import React, { Component, useDeferredValue, useEffect, useState } from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';
import HandleNextArrow from './HandleNextArrow';
import HandlePrevArrow from './HandlePrevArrow';
import MainBannerContent from './MainBannerContent';

function TopBanner() {
	const [bannerWidthState, setBannerwidth] = useState(
		window.innerWidth * 0.8,
	);
	const bannerWidth = useDeferredValue(bannerWidthState);

	const handelResize = () => {
		setBannerwidth(
			window.innerWidth * 0.8 > 1060 ? '1060' : window.innerWidth,
		);
	};

	useEffect(() => {
		window.addEventListener('resize', handelResize);
		return () => {
			window.removeEventListener('resize', handelResize);
		};
	}, []);

	const settings = {
		dots: false, // 점 안 보이게
		infinite: true, // 무한으로 즐겨요
		speed: 600,
		slidesToShow: 1,
		slidesToScroll: 1,
		variableWidth: true,
		nextArrow: <HandleNextArrow />,
		prevArrow: <HandlePrevArrow />,
		arrows: true,
	};

	return (
		<ComponentWrapper>
			<CustomSlider {...settings} width={`${bannerWidth.toString()}px`}>
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
			</CustomSlider>
		</ComponentWrapper>
	);
}

const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	touch-action: pan-y;

	.slick-list {
		padding: 0px 50px;
		position: relative;
		overflow: hidden;
		display: block;
		margin: 0;
	}

	.slick-slide {
		display: inline-block;
		padding: 0 12px;
		float: left;
		width: 1060px;
		/* width: ${(props) => props.width || '1060px'}; */
	}
`;

const ComponentWrapper = styled.div`
	height: auto;
	position: relative;
	overflow: hidden;
`;

export default TopBanner;
