/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable react/button-has-type */
/* eslint-disable jsx-a11y/control-has-associated-label */
import React, { useContext, useState } from 'react';
import { VisibilityContext } from 'react-horizontal-scrolling-menu';
import styled from 'styled-components';

export function ChipScrollBox({ itemId, selected, onClick, title }) {
	const visibility = useContext(VisibilityContext);
	const visible = visibility.isItemVisible(itemId);
	const [onhover, setHover] = useState(false);

	return (
		<ChipButton
			onMouseLeave={() => setHover(false)}
			onMouseEnter={() => setHover(true)}
			onClick={() => onClick(visibility)}
			selected={selected}
			onhover={onhover}
		>
			{title}
		</ChipButton>
	);
	// return React.createElement(
	// 	'div',
	// 	{
	// 		onClick: () => onClick(visibility),
	// 		role: 'button',
	// 		style: {
	// 			border: '1px solid',
	// 			display: 'inline-block',
	// 			margin: '0 10px',
	// 			width: '160px',
	// 			userSelect: 'none',
	// 		},
	// 		tabIndex: 0,
	// 		className: 'card',
	// 	},
	// 	React.createElement(
	// 		'div',
	// 		null,
	// 		React.createElement('div', null, title),
	// 		React.createElement(
	// 			'div',
	// 			{
	// 				style: {
	// 					backgroundColor: visible ? 'transparent' : 'gray',
	// 				},
	// 			},
	// 			'visible: ',
	// 			JSON.stringify(visible),
	// 		),
	// 		React.createElement(
	// 			'div',
	// 			null,
	// 			'selected: ',
	// 			JSON.stringify(!!selected),
	// 		),
	// 	),
	// 	React.createElement('div', {
	// 		style: {
	// 			backgroundColor: selected ? 'green' : 'bisque',
	// 			height: '200px',
	// 		},
	// 	}),
	// );
}

const ChipButton = styled.button`
	font-weight: ${(props) => props.selected && 700};
	color: ${(props) => (props.selected ? '#36f' : '#333')};
	/* background-color: ${(props) =>
		props.selected ? `#f2f4f7` : '#f2f4f7'}; */
	background-color: ${(props) => (props.selected ? `#fff` : '#fff')};
	border: ${(props) =>
		props.selected ? `1px solid #36f` : '1px solid #f2f4f7'};
	outline: ${(props) => props.selected && 'none'};

	font-weight: ${(props) => props.onhover && 700};
	color: ${(props) => props.onhover && '#36f'};
	background-color: ${(props) => props.onhover && `#fff`};
	border: ${(props) => props.onhover && `1px solid #36f`};
	outline: ${(props) => props.onhover && 'none'};

	padding: 0 29px;
	height: 50px;
	font-size: 15px;
	scroll-snap-align: start;
	margin-right: 10px;
	background-color: #f2f4f7;
	border-radius: 5px;
	white-space: nowrap;
	scroll-margin-left: 400px;

	letter-spacing: -1px;
`;
