/* eslint-disable no-use-before-define */
import ArticleCard from 'components/common/ArticleCard';
import Articletitle from 'components/common/Articletitle';
import HandleNextArrow from 'components/common/HandleNextArrow';
import HandlePrevArrow from 'components/common/HandlePrevArrow';
import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';
import { Article3Minutes } from 'utils/mockData';
import {
	LeftArticleArrow,
	RightArticleArrow,
} from '../../components/common/ArticleArrows';

function ArticleWrapper({ getData, title, subtitle, data }) {
	const [dataList, setDataList] = useState(getData.read().data);
	console.log(dataList);

	const settings = {
		dots: false,
		infinite: true,
		speed: 600,
		slidesToShow: 4,
		slidesToScroll: 4,
		nextArrow: <RightArticleArrow />,
		prevArrow: <LeftArticleArrow />,
		arrows: true,
	};

	return (
		<ComponentsWrapper>
			<Articletitle title={title} subtitle={subtitle} icon={false} />

			<CustomSlider {...settings}>
				{data.map((value) => (
					<ArticleCard
						key={value.idx}
						title={value.title}
						tags={value.tags}
						subtitle={value.subtitle}
						imgUrl={value.imgUrl}
						type="List"
					/>
				))}
			</CustomSlider>
		</ComponentsWrapper>
	);
}

const ComponentsWrapper = styled.div`
	position: relative;
`;

const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	touch-action: pan-y;
	padding-top: 70px;
	top: -40px;
	overflow-x: hidden;

	.slick-slide {
		float: left;
		height: 100%;
		min-height: 1px;
		display: inline-block;
	}
`;

export default ArticleWrapper;
