/* eslint-disable no-use-before-define */
import ArticleCard from 'components/common/ArticleCard';
import Articletitle from 'components/common/Articletitle';
import React from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';

function ArticleWrapper() {
	const settings = {
		dots: false,
		// autoplay: true,
		// autoplaySpeed: 2000,
		infinite: true,
		speed: 600,
		slidesToShow: 4,
		slidesToScroll: 3,
		nextArrow: <CustomHandleNextArrow />,
		// prevArrow: <HandlePrevArrow />,
		arrows: true,
	};

	return (
		<ComponentsWrapper>
			<div className="title-wrapper">
				<CustomHandleNextArrow>
					<span className="icon-svg-wrapper">
						<svg className="icon-svg" viewBox="0 0 18 18">
							<path d="m11.955 9-5.978 5.977a.563.563 0 0 0 .796.796l6.375-6.375a.563.563 0 0 0 0-.796L6.773 2.227a.562.562 0 1 0-.796.796L11.955 9z" />
						</svg>
					</span>
				</CustomHandleNextArrow>

				<Articletitle
					title="3분만에 읽는 Wanted+ 아티클"
					subtitle="아티클 전체보기"
					icon={false}
				/>

				<CustomHandleNextArrow>
					<span className="icon-svg-wrapper">
						<svg className="icon-svg" viewBox="0 0 18 18">
							<path d="m11.955 9-5.978 5.977a.563.563 0 0 0 .796.796l6.375-6.375a.563.563 0 0 0 0-.796L6.773 2.227a.562.562 0 1 0-.796.796L11.955 9z" />
						</svg>
					</span>
				</CustomHandleNextArrow>
			</div>

			<CustomSlider {...settings}>
				<ArticleCard type="List" />
				<ArticleCard type="List" />
				<ArticleCard type="List" />
				<ArticleCard type="List" />
				<ArticleCard type="List" />
			</CustomSlider>
		</ComponentsWrapper>
	);
}

const ComponentsWrapper = styled.div`
	position: relative;
	.title-wrapper {
		justify-content: space-between;
		display: flex;
		align-items: center;
		margin-bottom: 30px;
		grid-gap: 8px;
	}
`;

const CustomHandleNextArrow = styled.button`
	width: 36px;
	height: 36px;
	min-width: 36px;
	min-height: 36px;
	top: 0px;
	right: 0px;
	display: inline-flex;
	justify-content: center;
	align-items: center;
	border: 1px solid #e1e2e3;
	border-radius: 50%;
	background-color: #fff;
	box-shadow: 0 2px 2px 0 rgb(0 0 0 / 5%);
	font-size: 16px;
	color: #767676;
`;

const CustomSlider = styled(Slider)`
	z-index: 1;
	position: relative;
	display: block;
	box-sizing: border-box;
	user-select: none;
	touch-action: pan-y;
	overflow-x: hidden;

	.slick-slide {
		float: left;
		height: 100%;
		min-height: 1px;
		display: inline-block;
	}
`;

export default ArticleWrapper;
