import React, { Component } from 'react';
import styled from 'styled-components';

function ArticleCard({ linUrl, imgUrl, title, subtitle, publisher }) {
	return (
		<ComponentWrapper>
			<a href={linUrl}>
				<div className="img-wrapper">
					<img
						src="https://image.wanted.co.kr/optimize?src=http%3A%2F%2Ft1.daumcdn.net%2Fbrunch%2Fservice%2Fuser%2F4BkI%2Fimage%2FtPY3S3sKQEPoSV1ILk5sa93eR_c&amp;w=500&amp;q=75"
						alt="[삼삼한] 목표, 3가지 키워드"
					/>
				</div>
				<p className="title-wrapper">[삼삼한] 목표, 3가지 키워드</p>
				<p className="subcontent-wrapper">
					곧 목표 설정 강의를 한다. 요컨대 포기하지 않고 목표를
					완주하는 실행력과 목표를 대하는 사고방식 · 태도를 다루려고
					한다. 실행력은 목표를 세우고 작심삼일 하지 않는 방법이다. 그
					방법으로 꼽은 것은 첫째 목표를 글로 쓰라는 것이고,{' '}
				</p>
				<div className="ExternalContentCard_ExternalContentCard__Info___VHvM">
					<img
						src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Ft1.daumcdn.net%2Fbrunch%2Fstatic%2Ficon%2Fios%2Ficon120.png&amp;w=60&amp;q=90"
						alt="brunch"
					/>
					<p>한봉규 PHILIP</p>
				</div>
			</a>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.li`
	display: flex;
	flex-direction: column;
	width: calc(25% - 20px);
	margin: 0 10px 50px;
	.subcontent-wrapper {
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;

		overflow: hidden;
		text-overflow: ellipsis;
		font-style: normal;
		font-weight: 600;
		font-size: 13px;
		line-height: 21px;
		color: #aaa;
	}
	.title-wrapper {
		overflow: hidden;
		text-overflow: ellipsis;
		font-style: normal;
		font-weight: 700;
		font-size: 16px;
		line-height: 24px;
		color: #333;
		margin: 17px 0 8px;
	}
	.img-wrapper {
		position: relative;
		height: auto;
		padding-bottom: 70%;
		padding-left: 0;
		img {
			position: absolute;
			width: 100%;
			height: 100%;
			left: 0;
			top: 0;
			border-radius: 4px;
			object-fit: cover;
			object-position: top;
			outline: 1px solid rgba(0, 0, 0, 0.05);
			outline-offset: -1px;
		}
	}
`;

export default ArticleCard;
