/* eslint-disable no-return-assign */
/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { setLocationModalInvisible } from 'store/action/WdListAction';
import styled from 'styled-components';
import { LocationListDummy } from 'utils/mockData';

function LocationModal() {
	const dispatch = useDispatch();

	const [locationList, setLocationList] = useState(['all']);
	const [subLocationList, setSubLocationList] = useState('kr');
	const [country, setCountry] = useState('kr');

	const LocationmodalInvisible = () => {
		dispatch(setLocationModalInvisible());
	};

	useEffect(() => {
		document.body.style = `overflow: hidden`;
		return () => (document.body.style = `overflow: auto`);
	}, []);

	const handleChangeCountry = (e) => {
		setCountry(e.target.value);
	};

	const handleChangeLocation = ({ value, idx }) => {
		if (!locationList.includes(value)) {
			let temp = locationList.filter((element) => element !== 'all');
			temp = [...temp, value];
			setLocationList(temp);
		}
		if (idx === -1) {
			setSubLocationList([]);
		} else if (LocationListDummy[idx].subLocationList) {
			setSubLocationList(LocationListDummy[idx].subLocationList);
		} else {
			setSubLocationList([]);
		}
	};

	useEffect(() => {
		// console.log(locationList);
	}, [locationList]);

	return (
		<ComponentWrapper>
			<div className="popup-location">
				<div className="modal-header">
					<button type="button" className="btn-reset">
						<i className="icon-reload" />
						초기화
					</button>
					<span>
						지역
						<span className="filter-count">1</span>
					</span>
					<button
						onClick={() => dispatch(setLocationModalInvisible())}
						type="button"
					>
						<svg
							width="24"
							height="24"
							viewBox="0 0 24 24"
							color="#999"
						>
							<path
								fill="currentColor"
								d="M17.97 19.03a.75.75 0 001.06-1.06l-6.5-6.5a.75.75 0 00-1.06 0l-6.5 6.5a.75.75 0 001.06 1.06L12 13.06l5.97 5.97zM12 10.94L6.03 4.97a.75.75 0 00-1.06 1.06l6.5 6.5a.75.75 0 001.06 0l6.5-6.5a.75.75 0 00-1.06-1.06L12 10.94z"
							/>
						</svg>
					</button>
				</div>
				<div className="modal-body">
					<div className="container-selector">
						<h6 className="header-selector">국가</h6>
						<div className="select-selector">
							<select onChange={handleChangeCountry}>
								<option value="all">전세계</option>
								<option value="tw">대만</option>
								<option value="sg">싱가폴</option>
								<option value="jp">일본</option>
								<option selected value="kr">
									한국
								</option>
								<option value="others">기타</option>
							</select>
						</div>
					</div>
					<div className="container-location">
						<div className="column-location">
							<h6>지역</h6>
							<ul>
								<li>
									<button
										className={
											locationList.includes('all') &&
											'selected'
										}
										onClick={() => {
											handleChangeLocation({
												value: 'all',
												idx: -1,
											});
										}}
										type="button"
									>
										전국
									</button>
								</li>
								{LocationListDummy.map((value) => (
									<li>
										<button
											className={
												locationList.includes(
													value.en,
												) && 'selected'
											}
											onClick={() => {
												handleChangeLocation({
													value: value.en,
													idx: value.idx,
												});
											}}
											type="button"
										>
											{value.kr}
										</button>
									</li>
								))}
							</ul>
						</div>
						<div className="column-location">
							<h6>상세지역</h6>
							{subLocationList.length === 0 ? (
								<div className="container-empylocation">
									지역을 선택하면
									<br />
									상세 지역을 확인할 수 있습니다.
								</div>
							) : (
								<ul>
									<li>
										<button
											// className={
											// 	locationList.includes('all') &&
											// 	'selected'
											// }
											// onClick={() => {
											// 	handleChangeLocation({
											// 		value: 'all',
											// 		idx: -1,
											// 	});
											// }}
											type="button"
										>
											전국
										</button>
									</li>
									{subLocationList.map((value) => (
										<li>
											<button
												className={
													locationList.includes(
														value.en,
													) && 'selected'
												}
												onClick={() => {
													handleChangeLocation({
														value: value.en,
														idx: value.idx,
													});
												}}
												type="button"
											>
												{value.kr}
											</button>
										</li>
									))}
								</ul>
							)}
						</div>
					</div>
				</div>
				<div className="modal-footer">
					<div className="selectedLocation-container">
						<ul className="selectedLocation-ul-container">
							<li className="selectedLocation-li-container">
								<span>한국 전국</span>
							</li>
						</ul>
						{/* <ul className="SelectedLocations_listContainer__46isv visible-xs">
							<li className="SelectedLocations_locationItem__8j9AK">
								<div className="SelectedLocations_locationItemInner__6JCAs">
									<span>한국 전국</span>
								</div>
							</li>
						</ul> */}
						<p>최대 15개까지 선택 가능합니다.</p>
					</div>
					<button className="btn-footer" type="submit">
						확인
					</button>
				</div>
			</div>
			<div
				onClick={() => {
					LocationmodalInvisible();
				}}
				className="overlay-modal"
			/>
		</ComponentWrapper>
	);
}

