import React from 'react';
import ReactDOM from 'react-dom';
import {
	ScrollMenu,
	getItemsPos,
	slidingWindow,
} from 'react-horizontal-scrolling-menu';
import { LeftArrow, RightArrow } from './arrows';
import { Card } from './card';
import './globalStyles.css';

const elemPrefix = 'test';
const getId = (index) => `${elemPrefix}${index}`;
const getItems = () =>
	Array(20)
		.fill(0)
		.map((_, ind) => ({ id: getId(ind) }));
function App() {
	const [items] = React.useState(getItems);

	const [selected, setSelected] = React.useState('');
	const handleItemClick =
		(itemId) =>
		({ getItemById, scrollToItem }) => {
			setSelected(selected !== itemId ? itemId : '');
			// NOTE: for center items
			scrollToItem(getItemById(itemId), 'smooth', 'center', 'nearest');
		};
	return React.createElement(
		React.Fragment,
		null,
		React.createElement(
			'div',
			{ className: 'example', style: { paddingTop: '100px' } },
			React.createElement(
				'div',
				React.createElement(
					ScrollMenu,
					{
						LeftArrow,
						RightArrow,
						onMouseDown: () => dragStart,
						onMouseUp:
							({ getItemById, scrollToItem, visibleItems }) =>
							() => {
								// NOTE: for center items
								dragStop();
								const { center } = getItemsPos(visibleItems);
								scrollToItem(
									getItemById(center),
									'smooth',
									'center',
								);
							},
						options: { throttle: 0 },
						onMouseMove: handleDrag,
					},
					items.map(({ id }) =>
						React.createElement(Card, {
							title: id,
							itemId: id,
							key: id,
							onClick: handleItemClick(id),
							selected: id === selected,
						}),
					),
				),
			),
			React.createElement(Footer, null),
		),
	);
}
export default App;
function onWheel({ getItemById, items, visibleItems, scrollToItem }, ev) {
	const isThouchpad = Math.abs(ev.deltaX) !== 0 || Math.abs(ev.deltaY) < 15;
	if (isThouchpad) {
		ev.stopPropagation();
		return;
	}
	if (ev.deltaY < 0) {
		// NOTE: for center items
		const nextGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).next();
		const { center } = getItemsPos(nextGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	} else if (ev.deltaY > 0) {
		const prevGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).prev();
		const { center } = getItemsPos(prevGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	}
}
ReactDOM.render(
	React.createElement(App, null),
	document.getElementById('root'),
);
