import React from 'react';
import styled from 'styled-components';

function JobCategory() {
	return (
		<JobCategoryContainer>
			<div>
				<nav className="box-jobcategory">
					<section className="box-maincategory-container">
						<a href="/wdlist" aria-label="">
							<em>
								<h2>직군 전체</h2>
							</em>
						</a>
						<ul>
							<li
								data-testid="category-item"
								className="category-item"
							>
								<a href="/wdlist/518">
									<em>개발</em>
								</a>
							</li>
						</ul>
					</section>
				</nav>
			</div>
		</JobCategoryContainer>
	);
}

const JobCategoryContainer = styled.div`
	position: fixed;
	left: 0;
	right: 0;
	bottom: 0;
	top: 50px;
	overflow: hidden;
	width: 100%;
	.box-jobcategory {
		position: relative;
		height: 100%;
		width: 100%;
		max-width: 1100px;
		display: flex;
		margin: 0 auto;
		z-index: 1400 !important;
	}
	.box-maincategory-container {
		border-left: 1px solid #f7f9fa;
		border-right: 1px solid #f7f9fa;
		height: calc(100vh - 50px);
		width: 220px;
		background-color: #fff;
		overflow-y: auto;
		a {
			height: 40px;
			display: flex;
			align-items: center;
			flex: 1 1;
			padding: 0 20px;
			width: 100%;
		}
		h2 {
			font-size: 14px;
			font-weight: 700;
			font-style: normal;
			line-height: 2.86;
			letter-spacing: normal;
			color: #333;
		}
		.Explore_Container__QaOhW .Explore_MainCategory__rpxri a em,
		.Explore_Container__QaOhW .Explore_MainCategory__rpxri button em {
			font-size: 13px;
			font-weight: 700;
			font-style: normal;
			letter-spacing: normal;
			color: #333;
			width: 100%;
			text-overflow: ellipsis;
			white-space: nowrap;
			overflow: hidden;
		}
	}
`;

export default JobCategory;
