import React, { useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import styled from 'styled-components';

function WdPage() {
	const { wdIndex } = useParams();
	const ref = useRef();

	console.log(ref.current.getBoundingClientRect());
	const handleScrollPrev = () => {
		ref.current.scrollTo({
			top: 0,
			left: `10%`,
			behavior: 'smooth',
		});
	};

	return (
		<ComponentWrapper>
			<div className="jobDetail-container">
				<div style={{ position: 'relative' }}>
					<div className="jobcontent-container">
						<section className="jobimage-imgcontainer">
							<div className="jobimage-slider" ref={ref}>
								<Button
									onClick={() => {
										handleScrollPrev();
									}}
									type="button"
								>
									test
								</Button>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2F2cv2kbezyp0xheqs__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
							</div>
						</section>
					</div>
					<div className="jobprocess-container" />
				</div>
			</div>
		</ComponentWrapper>
	);
}

const Button = styled.button`
	position: absolute;
	width: 9%;
	height: 100%;
	font-size: 26px;
	color: #b5b5b5;
	opacity: 0.7;
	z-index: 1;
	color: #fff;
`;

const ComponentWrapper = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	max-width: 1060px;

	.jobimage-imgcontainer {
		position: relative;
		overflow: hidden;

		.jobimage-slider {
			display: flex;
			flex-wrap: nowrap;
			flex-direction: row;
			align-items: flex-start;
			padding-bottom: 4px;
			height: auto;
			overflow-x: auto;
			scroll-snap-type: x mandatory;
			overscroll-behavior: contain;

			::-webkit-scrollbar {
				height: 10px;
			}
			::-webkit-scrollbar-thumb {
				background-color: #b5b5b5;
				border-radius: 10px;
			}

			.img-jobimage {
				scroll-snap-align: start;
				position: relative;
				width: 100%;
				flex-shrink: 0;
				padding-bottom: 70%;
			}
			img {
				position: absolute;
				width: 100%;
				height: 100%;
				left: 0;
				top: 0;
				border-radius: 4px;
				object-fit: cover;
				outline: 1px solid rgba(0, 0, 0, 0.05);
				outline-offset: -1px;
			}
		}
	}
	.jobcontent-container {
		display: inline-block;
		width: calc(100% - 360px);
		vertical-align: top;
	}
`;

export default WdPage;
