/* eslint-disable jsx-a11y/label-has-associated-control */
import React, { useState } from 'react';
import styled from 'styled-components';

function OnboardingPage() {
	const [jobGroup, setJobGroup] = useState('-2');
	const [career, setCareer] = useState('-2');

	const handleSelectJobGroup = (e) => {
		// 태그로 들어감
		if (e.target.value === '-1') {
			setJobGroup('-2');
		} else {
			setJobGroup(e.target.value);
		}
	};

	const handleSelectCareer = (e) => {
		// 태그로 들어감
		if (e.target.value === '-1') {
			setCareer('-2');
		} else {
			setCareer(e.target.value);
		}
	};

	return (
		<OnboardingPageWrapper>
			<div className="wrapper-onboarding">
				<div className="wrapper-logo">
					<svg width="50" height="20" viewBox="0 0 79 32">
						<path
							fill="#24e0a6"
							d="M27.755 5.155A15.953 15.953 0 0015.992 0C7.152 0-.012 7.164-.012 16.004s7.164 16.004 16.004 16.004c4.653 0 8.84-1.985 11.763-5.155A15.953 15.953 0 0031.996 16c0-4.183-1.606-7.992-4.241-10.845z"
						/>
						<path
							fill="#438bff"
							d="M51.286 5.155A15.953 15.953 0 0039.523 0c-8.84 0-16.004 7.164-16.004 16.004s7.164 16.004 16.004 16.004c4.653 0 8.84-1.985 11.763-5.155A15.953 15.953 0 0055.527 16c0-4.179-1.61-7.992-4.241-10.845z"
						/>
						<path
							fill="#2c5bf2"
							d="M74.812 5.155A15.953 15.953 0 0063.049 0c-8.84 0-16.004 7.164-16.004 16.004s7.164 16.004 16.004 16.004c4.653 0 8.84-1.985 11.763-5.155A15.953 15.953 0 0079.053 16c0-4.183-1.61-7.992-4.241-10.845z"
						/>
						<path
							fill="#24e0bc"
							d="M27.755 5.155A15.953 15.953 0 0015.992 0C7.152 0-.012 7.164-.012 16.004s7.164 16.004 16.004 16.004c4.653 0 8.84-1.985 11.763-5.155A15.953 15.953 0 0123.514 16c0-4.183 1.61-7.992 4.241-10.845z"
						/>
						<path
							fill="#438bff"
							d="M51.282 5.155C48.359 1.985 44.171 0 39.519 0s-8.84 1.985-11.763 5.155a15.953 15.953 0 014.241 10.853c0 4.187-1.61 8-4.241 10.853 2.923 3.17 7.111 5.155 11.763 5.155s8.84-1.985 11.763-5.155c-2.631-2.853-4.241-6.662-4.241-10.853s1.61-8 4.241-10.853z"
						/>
						<path
							fill="#3a68f9"
							d="M27.755 5.155a15.953 15.953 0 00-4.241 10.853c0 4.187 1.61 8 4.241 10.853 2.631-2.853 4.241-6.662 4.241-10.853s-1.606-8-4.241-10.853z"
						/>
						<path
							fill="#2c5bf2"
							d="M63.049 0c-4.653 0-8.84 1.985-11.763 5.155a15.953 15.953 0 014.241 10.853c0 4.187-1.61 8-4.241 10.853a15.953 15.953 0 0011.763 5.155c8.84 0 16.004-7.164 16.004-16.004C79.053 7.164 71.885 0 63.049 0z"
						/>
						<path
							fill="#0049db"
							d="M51.282 5.155a15.953 15.953 0 00-4.241 10.853c0 4.187 1.61 8 4.241 10.853a15.953 15.953 0 004.241-10.853c.004-4.191-1.606-8-4.241-10.853z"
						/>
					</svg>
					<svg width="87.5" height="20" viewBox="0 0 140 32">
						<path
							fill="currentColor"
							d="M89.8 2.2l-5.6 2.4v4.8h-3.8v5.2h3.8v10.2c0 4.2 2.6 7 6.6 7 1.6 0 2.6-.4 3.2-.6V26c-.2 0-1 .2-1.8.2-1.6 0-2.4-.6-2.4-2.6v-8.8H94V9.6h-4.2V2.2zM28.6 9.6l-4 14-4.6-14h-5.6l-4.6 14L6 9.6H0l6.8 21.8h6l4.4-14.6 4.6 14.6h6l6.8-21.8zM134.4 2.2v8.6c-1.4-1-3-1.6-4.8-1.8h-.4-1.6c-5 .4-8.2 4.2-9.2 9-.2.8-.2 1.4-.2 2.2V22c.6 5.6 4.4 10 10.2 10 2.4 0 4.4-.6 6-1.8v1.4h5.4V0l-5.4 2.2zm-5.2 24.4c-3 0-5.6-2.4-5.6-6.2 0-4 2.6-6.2 5.6-6.2s5.2 2.2 5.2 6c0 4.2-2.2 6.4-5.2 6.4zM116.2 18c-.8-5.2-4.6-9-10-9s-9.2 3.8-10 9c-.2.8-.2 1.6-.2 2.6v1.6c.6 5.6 4.4 10 10.2 10 4.6 0 8-2.8 9.4-6.8l-5-1.2c-.8 1.8-2.4 3.2-4.4 3.2-2.8 0-4.6-2.2-5-5.2h15.2v-1.6c0-1 0-1.8-.2-2.6zm-14.8 0c.8-2.2 2.4-3.6 4.8-3.6s4 1.6 4.8 3.6h-9.6zM50.6 11c-1.4-1-3.2-1.8-5.2-1.8H44.8h-.6c-5.2.4-8.6 4-9.4 9-.2.8-.2 1.4-.2 2.2v1.8c.6 5.6 4.4 10 10.2 10 2.4 0 4.4-.6 6-1.8v1.4h5.6V9.6h-5.6V11zm-5.2 15.6c-3 0-5.6-2.4-5.6-6.2 0-4 2.6-6.2 5.6-6.2s5.2 2.2 5.2 6c0 4.2-2.2 6.4-5.2 6.4zM71.2 9c-2.2 0-4.6 1-6 3.2V9.6h-5.6v21.8h5.6V18.8c0-2.6 1.4-4.6 4-4.6 2.8 0 3.8 2 3.8 4.4v12.8h5.6V17.6c.2-4.8-2.2-8.6-7.4-8.6z"
						/>
					</svg>
				</div>
				<div className="wrapper-body">
					<div className="step">
						<ul className="step-container">
							<li className="active">
								<em>
									<span>1</span>
								</em>
								<span>전문분야 설정</span>
							</li>
							<li className="">
								<em>
									<span>2</span>
								</em>
							</li>
							<li className="">
								<em>
									<span>3</span>
								</em>
							</li>
						</ul>
					</div>
					<div className="wrapper-body-container">
						<div className="header-wrapper">
							<p className="header-desc">
								어떤 일을 하고 계신가요?
								<br />딱 맞는 회사와 콘텐츠를 추천해 드려요!
							</p>
						</div>
						<div className="style-wrapper">
							<p className="style-label">직군</p>
							<div className="style-body">
								<SelectWrapper hasType={jobGroup}>
									<select
										onChange={handleSelectJobGroup}
										name="category_id"
										value={jobGroup}
									>
										<option value="-1" hidden>
											직군을 선택 해주세요
										</option>
										<option value="518">개발</option>
										<option value="507">
											경영·비즈니스
										</option>
										<option value="523">마케팅·광고</option>
										<option value="511">디자인</option>
										<option value="530">영업</option>
										<option value="510">
											고객서비스·리테일
										</option>
										<option value="517">HR</option>
										<option value="959">게임 제작</option>
										<option value="524">미디어</option>
										<option value="513">
											엔지니어링·설계
										</option>
										<option value="508">금융</option>
										<option value="532">물류·무역</option>
										<option value="515">
											의료·제약·바이오
										</option>
										<option value="522">제조·생산</option>
										<option value="10101">교육</option>
										<option value="10057">식·음료</option>
										<option value="521">
											법률·법집행기관
										</option>
										<option value="509">건설·시설</option>
										<option value="514">공공·복지</option>
									</select>
									<svg
										width="20"
										height="20"
										viewBox="0 0 24 24"
										className="select-icon"
									>
										<path
											fill="currentColor"
											d="M16 9.25a.75.75 0 01.586 1.218l-3.986 5a.75.75 0 01-1.17.002l-4.015-5A.75.75 0 018 9.25h8z"
										/>
									</svg>
								</SelectWrapper>
								<div className="hint-level1">
									신입의 경우, 희망 직군을 선택해 주세요.
								</div>
							</div>
							<div className="style-guidance" />
						</div>
						<div className="style-wrapper">
							<p htmlFor="annual" className="style-label">
								경력
							</p>
							<div className="style-body">
								<SelectWrapper hasType={career}>
									<select
										onChange={handleSelectCareer}
										name="annual"
										value={career}
									>
										<option value="-2" disabled hidden>
											선택해 주세요.
										</option>
										<option value="-1">년차 선택</option>
										<option value="0">신입</option>
										<option value="1">1 년</option>
										<option value="2">2 년</option>
										<option value="3">3 년</option>
										<option value="4">4 년</option>
										<option value="5">5 년</option>
										<option value="6">6 년</option>
										<option value="7">7 년</option>
										<option value="8">8 년</option>
										<option value="9">9 년</option>
										<option value="10">10 년 이상</option>
									</select>
									<svg
										width="20"
										height="20"
										viewBox="0 0 24 24"
										className="select-icon"
									>
										<path
											fill="currentColor"
											d="M16 9.25a.75.75 0 01.586 1.218l-3.986 5a.75.75 0 01-1.17.002l-4.015-5A.75.75 0 018 9.25h8z"
										/>
									</svg>
								</SelectWrapper>
							</div>
							<div className="style-guidance" />
						</div>
						<div className="style-wrapper checkbox-agreement container-check">
							<div className="style-check">
								<svg width="24" height="24" viewBox="0 0 24 24">
									<path
										fill="currentColor"
										d="M18.75 21.75H5.25a3 3 0 0 1-3-3V5.25a3 3 0 0 1 3-3h13.5a3 3 0 0 1 3 3v13.5a3 3 0 0 1-3 3zm0-1.5a1.5 1.5 0 0 0 1.5-1.5V5.25a1.5 1.5 0 0 0-1.5-1.5H5.25a1.5 1.5 0 0 0-1.5 1.5v13.5a1.5 1.5 0 0 0 1.5 1.5h13.5z"
									/>
								</svg>
							</div>
							<div className="style-label">
								<input
									type="checkbox"
									id="rejectEventEmail"
									name="rejectEventEmail"
								/>
								<label>이벤트 소식 등 알림 정보 수신거부</label>
								<a
									href="https://help.wanted.co.kr/hc/ko/articles/360040540111"
									rel="noopener noreferrer"
									target="_blank"
									className="link-agreement"
								>
									자세히
								</a>
							</div>
						</div>
						<div className="footer-level1">
							<Button type="button" disabled>
								다음
							</Button>
							<div className="style-divider">or</div>
							<div>
								<input
									type="file"
									autoComplete="off"
									style={{ display: 'none' }}
								/>
								<ButtonType2 type="button">
									내 이력서 업로드하기
								</ButtonType2>
							</div>
						</div>
					</div>
				</div>
			</div>
		</OnboardingPageWrapper>
	);
}

const SelectWrapper = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: center;
	position: relative;
	width: 100%;
	height: 50px;
	padding-right: 15px;
	padding-left: 15px;
	border-radius: 5px;
	border: 1px solid #e1e2e3;
	background-color: #fff;
	font-size: 15px;
	> select {
		position: absolute;
		top: 0;
		left: 0;
		z-index: 1;
		width: 100%;
		height: 100%;
		padding-left: 15px;
		border: 0;
		appearance: none;
		background: transparent;
		color: #aaa;
		color: ${(props) => props.hasType !== '-2' && '#333'};
		font-size: 15px;
		font-family: 'Pretendard', 'PretendardStd-ExtraLight',
			'PretendardStd-Thin', 'PretendardStd-Light', 'PretendardStd';
	}
	.select-items {
		position: absolute;
		background-color: DodgerBlue;
	}
`;

const ButtonType2 = styled.button`
	border: 1px solid #36f;
	background-color: #fff;
	color: #36f;
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
	height: 54px;
	border-radius: 27px;
	font-size: 16px;
	font-weight: 600;
	cursor: pointer;
`;

const Button = styled.button`
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
	height: 54px;
	border: 0;
	border-radius: 27px;
	background-color: #36f;
	font-size: 16px;
	font-weight: 600;
	color: #fff;
	cursor: pointer;
	:disabled {
		background-color: #f2f4f7;
		border-color: transparent;
		color: #cacaca;
		cursor: not-allowed;
	}
`;

const OnboardingPageWrapper = styled.div`
	display: flex;
	justify-content: center;
	min-height: 100vh;
	padding-top: 105px;
	padding-bottom: 105px;
	background-color: #f8f8fa;
	.style-divider {
		display: flex;
		justify-content: center;
		align-items: center;
		height: 34px;
		font-weight: 600;
		color: #36f;
	}
	.container-check {
		display: flex;
		align-items: center;
		position: relative;
		height: 14px;
	}
	.footer-level1 {
		margin-top: 18px;
	}
	.style-label {
		flex: 1 1;
		font-size: 15px;
	}

	.style-check {
		display: flex;
		justify-content: center;
		align-items: center;
		width: 17.3px;
		height: 17.3px;
	}
	.link-agreement {
		float: right;
		font-size: 13px;
		line-height: 19px;
		color: #767676 !important;
	}
	.checkbox-agreement {
		margin-bottom: 40px;
		color: #ececec;
		> div:first-of-type {
			margin-right: 5px;
		}
		> div:last-of-type {
			color: #939393;
		}
		p {
			font-weight: 400;
		}
	}
	.style-guidance {
		color: #767676;
		bottom: 0;
		margin-top: 6px;
		font-size: 12px;
	}
	.hint-level1 {
		font-weight: 500;
		font-size: 12px;
		color: #aaa;
		margin-top: 10px;
	}
	.select-icon {
		position: absolute;
		top: 50%;
		right: 10px;
		-webkit-transform: translateY(-50%);
		transform: translateY(-50%);
	}

	.style-wrapper {
		position: relative;
		padding-bottom: 22px;
		color: #767676;

		input[type='checkbox'] {
			position: absolute;
			left: 1px;
			opacity: 0;
			vertical-align: top;
			cursor: pointer;
		}
	}
	.style-body {
		margin-top: 11px;
	}
	.wrapper-body-container {
		padding: 29px 19px 24px;
	}
	.header-wrapper {
		margin-bottom: 34px;
		font-size: 15px;
		text-align: left;
		color: #333;
		font-weight: 600;
	}
	.header-desc {
		line-height: 1.53;
		white-space: pre-line;
	}

	.step {
		position: relative;
		border-bottom: 1px solid #e1e2e3;
		padding: 20px;
	}
	.step-container {
		display: flex;
		justify-content: flex-start;
		> li > span {
			display: inline-block;
			font-size: 14px;
			color: #333;
			margin-left: 4px;
			font-weight: 700;
			line-height: 23px;
		}
		> li.active,
		> li.completed {
			margin-left: 0;
			color: #333;
			margin-right: auto;
			::after {
				background: #36f;
				width: 33.33%;
				transition: width 0.5s;
				left: 0;
				position: absolute;
				content: '';
				display: block;
				bottom: -1px;
				height: 1px;
			}
		}
		> li.active > em,
		> li.completed > em {
			background: #36f;
			color: #fff;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			width: 23px;
			height: 23px;
			line-height: 1em;
			border-radius: 50%;
			font-style: normal;
			margin-right: 6px;
		}
		> li {
			flex: none;
			height: 23px;
		}
		> li > em {
			display: inline-flex;
			align-items: center;
			justify-content: center;
			width: 23px;
			height: 23px;
			line-height: 1em;
			border-radius: 50%;
			font-style: normal;
			background: #f2f4f7;
			color: #ccc;
			margin-right: 6px;
		}
		> li:nth-of-type(2):after {
			left: 33.33%;
		}
		> li:after {
			position: absolute;
			content: '';
			display: block;
			bottom: -1px;
			height: 1px;
			width: 0;
		}
	}
	.wrapper-body {
		border: 1px solid #ececec;
		border-radius: 5px;
		margin-top: 39px;
		background-color: #fff;
	}

	.wrapper-onboarding {
		width: 400px;
		max-width: 100vw;
	}
	.wrapper-logo {
		display: flex;
		justify-content: center;
		align-items: center;
		> svg:nth-child(2) {
			margin-top: -4px px;
			margin-left: 6px;
		}
	}
`;

export default OnboardingPage;
