/* eslint-disable import/no-cycle */
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { TagListItemDummy } from 'utils/mockData';
import CompanyRec from './CompanyRec';
import { companyByTagResult } from './index';

export function CompanyByTagComponent() {
	let companyByTag;
	let tag1;
	let tag2;
	const [nowLoading, setNowLoading] = useState(false);
	useEffect(() => {
		try {
			companyByTag = companyByTagResult.companyByTag.read();
			tag1 = TagListItemDummy.find(
				(item) =>
					item.idx ===
					companyByTag.data.result[0][0].companyTagCategory_no,
			);
			tag2 = TagListItemDummy.find(
				(item) =>
					item.idx ===
					companyByTag.data.result[1][0].companyTagCategory_no,
			);
			setNowLoading(true);
		} catch (error) {
			console.log(error);
			setNowLoading(false);
		}
	}, []);

	return (
		<>
			{nowLoading && (
				<>
					<div className="featured-joblist">
						<CompanyRec
							company
							title={`#${tag1.content}`}
							icon={tag1.imgUrl}
							data={companyByTag.data.result[0]}
						/>
					</div>

					<div className="featured-joblist">
						<CompanyRec
							company
							title={`#${tag2.content}`}
							icon={tag2.imgUrl}
							data={companyByTag.data.result[1]}
						/>
					</div>
				</>
			)}
		</>
	);
}
