/* eslint-disable no-use-before-define */
/* eslint-disable array-callback-return */
import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import {
	ScrollMenu,
	getItemsPos,
	slidingWindow,
} from 'react-horizontal-scrolling-menu';
import styled from 'styled-components';
import { HomeChipDummydata } from 'utils/mockData';
import { LeftArrow, RightArrow } from './Arrows';
import { ChipScrollBox } from './ChipscrollBox';

// const elemPrefix = 'test';
// const getId = (index) => `${elemPrefix}${index}`;
// const getItems = () =>
// 	Array(20)
// 		.fill(0)
// 		.map((_, ind) => ({ id: getId(ind) }));

function App() {
	const [items] = useState(HomeChipDummydata);
	const [selected, setSelected] = useState(0);

	const handleItemClick =
		(itemId) =>
		({ getItemById, scrollToItem }) => {
			setSelected(selected !== itemId ? itemId : '');
			scrollToItem(getItemById(itemId), 'smooth', 'center', 'nearest');
		};

	return (
		<ChipContainer>
			<ScrollMenu
				LeftArrow={LeftArrow}
				RightArrow={RightArrow}
				// onWheel={onWheel}
				onMouseUp={({ getItemById, scrollToItem, visibleItems }) =>
					() => {
						// NOTE: for center items
						const { center } = getItemsPos(visibleItems);
						scrollToItem(getItemById(center), 'smooth', 'center');
					}}
				options={{ throttle: 0 }}
			>
				{items.map((value) => (
					<ChipScrollBox
						title={value.content}
						itemId={value.idx}
						key={value.idx}
						onClick={handleItemClick(value.idx)}
						selected={value.idx === selected}
					/>
				))}
			</ScrollMenu>
		</ChipContainer>
	);
}
export default App;

const ChipContainer = styled.div`
	margin: 30px 0;
	position: relative;
	display: flex;
	overflow-x: hidden;
`;

function onWheel({ getItemById, items, visibleItems, scrollToItem }, ev) {
	const isThouchpad = Math.abs(ev.deltaX) !== 0 || Math.abs(ev.deltaY) < 15;
	if (isThouchpad) {
		ev.stopPropagation();
		return;
	}
	if (ev.deltaY < 0) {
		const nextGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).next();
		const { center } = getItemsPos(nextGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	} else if (ev.deltaY > 0) {
		const prevGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).prev();
		const { center } = getItemsPos(prevGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	}
}
