import { InputContainer } from 'components/common/styled';
import React, { useState } from 'react';
import styled from 'styled-components';

function OccupationSetting() {
	const navigate = useNavigate();
	const [school, setSchool] = useState('');

	const handleInputSchool = (e) => {
		setSchool(e.target.value);
	};

	return (
		<>
			<ComponentsContainer className="wrapper-body">
				<div className="step">
					<ul className="step-container">
						<li className="completed" style={{ marginRight: '0' }}>
							<em style={{ padding: '5px 0 0 2px' }}>
								<svg
									className=""
									width="15"
									height="15"
									viewBox="0 0 15 15"
								>
									<defs>
										<path
											id="al3je9dvha"
											d="M576.95 196.13c-.217-.217-.57-.217-.787 0-.217.218-.217.57 0 .788l3.71 3.71c.217.217.57.217.787 0l6.677-6.678c.217-.217.217-.57 0-.787-.217-.217-.57-.217-.787 0l-6.284 6.284-3.316-3.316z"
										/>
									</defs>
									<g fill="currentColor" fillRule="evenodd">
										<g transform="translate(-575 -192)">
											<use
												fill="#FFF"
												fillRule="nonzero"
												stroke="#FFF"
												strokeWidth=".3"
												xlinkHref="#al3je9dvha"
											/>
										</g>
									</g>
								</svg>
							</em>
						</li>
						<li className="active">
							<em>
								<span>2</span>
							</em>
							<span>학교/직장 설정</span>
						</li>
						<li className="">
							<em>
								<span>3</span>
							</em>
						</li>
					</ul>
				</div>
				<div className="wrapper-body-container">
					<div className="header-wrapper">
						<p className="header-desc">
							아래 정보를 추가해 주세요!
							<br />
							더욱 정교한 추천을 받으실 수 있어요.
						</p>
					</div>
					<div className="style-wrapper">
						<p htmlFor="school_name" className="style-label">
							학교
						</p>
						<div className="style-body">
							<div className="inputsearch-wrapper">
								<div className="inputsearch-input">
									<InputContainer
										className="inputsearch-ellipsis"
										type="text"
										name="school_name"
										placeholder="검색해 주세요."
										autoComplete="off"
										onChange={(e) => handleInputSchool(e)}
									/>
									<svg
										xmlns="https://www.w3.org/2000/svg"
										width="18"
										height="18"
										viewBox="0 0 18 18"
									>
										<defs>
											<path
												id="qt2dnsql4a"
												d="M15.727 17.273a.563.563 0 10.796-.796l-4.875-4.875-.19-.165a.563.563 0 00-.764.028 5.063 5.063 0 111.261-2.068.562.562 0 101.073.338 6.188 6.188 0 10-1.943 2.894l4.642 4.644z"
											/>
										</defs>
										<g fill="none" fillRule="evenodd">
											<use
												fill="#333"
												fillRule="nonzero"
												stroke="#333"
												strokeWidth=".3"
												xlinkHref="#qt2dnsql4a"
											/>
										</g>
									</svg>
								</div>
							</div>
						</div>
						<div className="style-guidance" />
					</div>
					<div className="style-wrapper">
						<p className="style-label">직장</p>
						<div className="style-body">
							<div className="inputsearch-wrapper">
								<div className="inputsearch-input">
									<InputContainer
										className="inputsearch-ellipsis"
										type="text"
										name="company_name"
										placeholder="검색해 주세요."
										autoComplete="off"
									/>
									<svg
										xmlns="https://www.w3.org/2000/svg"
										width="18"
										height="18"
										viewBox="0 0 18 18"
									>
										<defs>
											<path
												id="qt2dnsql4a"
												d="M15.727 17.273a.563.563 0 10.796-.796l-4.875-4.875-.19-.165a.563.563 0 00-.764.028 5.063 5.063 0 111.261-2.068.562.562 0 101.073.338 6.188 6.188 0 10-1.943 2.894l4.642 4.644z"
											/>
										</defs>
										<g fill="none" fillRule="evenodd">
											<use
												fill="#333"
												fillRule="nonzero"
												stroke="#333"
												strokeWidth=".3"
												xlinkHref="#qt2dnsql4a"
											/>
										</g>
									</svg>
								</div>
							</div>
							<div className="hint-level2">
								신입의 경우, ‘신입’ 또는 ‘없음’을 직접 입력해
								주세요.
							</div>
						</div>
						<div className="style-guidance" />
					</div>
					<Button type="button" disabled={school.length === 0}>
						다음
					</Button>
				</div>
			</ComponentsContainer>

			<div className="wrapper-skip">
				<button type="button">
					나중에 입력할게요
					<svg width="12" height="12" viewBox="0 0 12 12">
						<path
							fill="currentColor"
							d="M4.22 9.72a.75.75 0 001.06 1.06l4.25-4.25a.75.75 0 000-1.06L5.28 1.22a.75.75 0 00-1.06 1.06L7.94 6 4.22 9.72z"
						/>
					</svg>
				</button>
			</div>
		</>
	);
}

const Button = styled.button`
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
	height: 54px;
	border: 0;
	border-radius: 27px;
	background-color: #36f;
	font-size: 16px;
	font-weight: 600;
	color: #fff;
	cursor: pointer;
	:disabled {
		background-color: #f2f4f7;
		border-color: transparent;
		color: #cacaca;
		cursor: not-allowed;
	}
`;

const ComponentsContainer = styled.div`
	.inputsearch-wrapper,
	.inputsearch-input {
		position: relative;
		> svg {
			position: absolute;
			top: 50%;
			right: 21px;
			-webkit-transform: translateY(-50%);
			transform: translateY(-50%);
			color: #333;
		}
		::after {
			content: '';
			position: absolute;
			top: 50%;
			right: 60px;
			width: 1px;
			height: 28px;
