import Loading from 'components/common/Loading';
import React, { Suspense, useEffect, useState, useTransition } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { setLoginModalVisible } from 'store/action/LoginAction';
import styled from 'styled-components';
import { ChartDummyData } from 'utils/mockData';
import { fetchSalaryData } from './fetchSalaryData';
import RechartLayer from './RechartLayer';

function SalaryGraph({ jobIdx }) {
	const navigate = useNavigate();
	const dispatch = useDispatch();
	const [isPending, startTransition] = useTransition();
	const { isLogin } = useSelector((state) => state.UserDataReducer);

	const [salaryList, setSalaryList] = useState([]);
	const [job, setJob] = useState({});
	const [diffSalary, setDiffSalary] = useState('0');
	const [career, setCareer] = useState('0');
	const [salaryData, setSalaryData] = useState(
		fetchSalaryData({
			dutyCategoryId: 4,
			jobCategoryId: 2,
		}),
	);

	const handleClickSearchBar = () => {
		if (!isLogin) {
			dispatch(setLoginModalVisible());
		}
	};

	const handleChangeJob = (e) => {
		startTransition(() => {
			setSalaryData(
				fetchSalaryData({
					dutyCategoryId: e.target.value,
					jobCategoryId: 2,
				}),
			);
		});
		navigate(`/salary/${e.target.value}`);
	};

	const handleChangeCareer = (e) => {
		setCareer(e.target.value);
	};

	const handleChangeDiff = (e) => {
		const check = /^[0-9]+$/;
		if (e.target.value.split(',').join('') === '') {
			setDiffSalary('0');
		} else if (
			check.test(e.target.value.split(',').join('')) &&
			e.target.value.split(',').join('').length <= 10
		) {
			setDiffSalary(
				parseInt(e.target.value.split(',').join(''), 10)
					.toString()
					.replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ','),
			);
		}
	};

	useEffect(() => {
		if (
			ChartDummyData.find((item) => item.idx.toString() === jobIdx) ===
			undefined
		) {
			navigate('/salary/1');
		} else {
			setJob(
				ChartDummyData.find((item) => item.idx.toString() === jobIdx),
			);
			setSalaryList(
				ChartDummyData.find((item) => item.idx.toString() === jobIdx)
					.salaryList,
			);
		}
	}, [jobIdx]);

	return (
		<ComponentWrapper>
			<div className="salary-container">
				<div className="salary-chart-container">
					<section>
						<div
							className="recharts-responsive-container"
							style={{ width: '100%', height: '100%' }}
							width="784.390625"
							height="392"
						>
							<div
								className="recharts-wrapper"
								style={{
									position: 'relative',
									cursor: 'default',
									width: '784px',
									height: '392px',
								}}
							>
								<Suspense>
									{salaryList && (
										<RechartLayer
											salaryData={salaryData}
											careerIdx={career}
											salaryList={salaryList}
										/>
									)}
								</Suspense>
							</div>
						</div>
					</section>
					<div className="btngroup">
						<button type="button" className="style_wrapper__IgK7U">
							개발
						</button>
						<button type="button" className="style_wrapper__IgK7U">
							{job.job}
						</button>
					</div>
					<aside>
						<h4 className="info-jobcategory">
							{career === `0` ? '신입' : `${career}년 경력`}
							{job.job} 예상 연봉
						</h4>
						{salaryList.length !== 0 && (
							<h2 className="info-jobcategory-salary">
								{salaryList[parseInt(career, 10)].salary
									.toString()
									.replace(
										/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g,
										',',
									)}
								<sub>만원</sub>
							</h2>
						)}

						{diffSalary !== '0' && (
							<h4 className="info-diff-salary">
								<sup>*</sup> {career === `0` && '신입'} 예상
								연봉 대비
								{parseInt(diffSalary.split(',').join(''), 10) >=
								salaryList[parseInt(career, 10)].salary
									? ` ${Math.round(
											(parseInt(
												diffSalary.split(',').join(''),
												10,
											) /
												salaryList[parseInt(career, 10)]
													.salary) *
												100 -
												100,
									  )}% 높음`
									: ` ${Math.round(
											(salaryList[parseInt(career, 10)]
												.salary /
												parseInt(
													diffSalary
														.split(',')
														.join(''),
													10,
												)) *
												100 -
												100,
									  )}% 낮음`}
							</h4>
						)}
					</aside>

					<div
						role="presentation"
						className="searchbar-container"
						onClick={handleClickSearchBar}
					>
						<div className="box-searchbar-main">
							<div className="searchbar-container-box">
								<div className="selector-category">
									<div className="select-wrapper selectbox-category has-value">
										<select
											defaultValue={518}
											name="category"
											className=""
										>
											<option disabled>직군</option>
											<option value="518">개발</option>
											<option value="507">
												경영·비즈니스
											</option>
											<option value="523">
												마케팅·광고
											</option>
											<option value="511">디자인</option>
											<option value="530">영업</option>
											<option value="510">
												고객서비스·리테일
											</option>
											<option value="517">HR</option>
											<option value="959">
												게임 제작
											</option>
											<option value="524">미디어</option>
											<option value="513">
												엔지니어링·설계
											</option>
											<option value="508">금융</option>
											<option value="532">
												물류·무역
											</option>
											<option value="515">
												의료·제약·바이오
											</option>
											<option value="522">
												제조·생산
											</option>
											<option value="10101">교육</option>
											<option value="10057">
												식·음료
											</option>
											<option value="509">
												건설·시설
											</option>
											<option value="514">
												공공·복지
											</option>
										</select>
										<svg
											width="20"
											height="20"
											viewBox="0 0 24 24"
											className="icon-select"
										>
											<path
												fill="currentColor"
												d="M16 9.25a.75.75 0 01.586 1.218l-3.986 5a.75.75 0 01-1.17.002l-4.015-5A.75.75 0 018 9.25h8z"
											/>
										</svg>
