/* eslint-disable no-use-before-define */
/* eslint-disable jsx-a11y/no-noninteractive-tabindex */
/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import React from 'react';
import ReactDOM from 'react-dom';
import {
	ScrollMenu,
	VisibilityContext,
	getItemsPos,
	slidingWindow,
} from 'react-horizontal-scrolling-menu';

import { LeftArrow, RightArrow } from './arrows';
import { Card } from './card';
import { Footer } from './footer';
import { Header } from './header';
import './globalStyles.css';

import useDrag from './useDrag';

// NOTE: embrace power of CSS flexbox!
// import "./hideScrollbar.css";
// import "./firstItemMargin.css";

type scrollVisibilityApiType = React.ContextType<typeof VisibilityContext>;

const elemPrefix = 'test';
const getId = (index: number) => `${elemPrefix}${index}`;

const getItems = () =>
	Array(20)
		.fill(0)
		.map((_, ind) => ({ id: getId(ind) }));

function App() {
	const [items] = React.useState(getItems);

	const { dragStart, dragStop, dragMove, dragging } = useDrag();

	const handleDrag =
		({ scrollContainer }: scrollVisibilityApiType) =>
		(ev: React.MouseEvent) =>
			dragMove(ev, (posDiff) => {
				if (scrollContainer.current) {
					scrollContainer.current.scrollLeft += posDiff;
				}
			});

	const [selected, setSelected] = React.useState < string > '';
	const handleItemClick =
		(itemId: string) =>
		({ getItemById, scrollToItem }: scrollVisibilityApiType) => {
			if (dragging) {
				return false;
			}
			setSelected(selected !== itemId ? itemId : '');
			// NOTE: for center items
			scrollToItem(getItemById(itemId), 'smooth', 'center', 'nearest');
		};

	return (
		<>
			<Header />
			<div className="example" style={{ paddingTop: '100px' }}>
				<div onMouseLeave={dragStop}>
					<ScrollMenu
						LeftArrow={LeftArrow}
						RightArrow={RightArrow}
						onWheel={onWheel}
						onMouseDown={() => dragStart}
						onMouseUp={({
								getItemById,
								scrollToItem,
								visibleItems,
							}: scrollVisibilityApiType) =>
							() => {
								// NOTE: for center items
								dragStop();
								const { center } = getItemsPos(visibleItems);
								scrollToItem(
									getItemById(center),
									'smooth',
									'center',
								);
							}}
						options={{ throttle: 0 }} // NOTE: for center items
						onMouseMove={handleDrag}
					>
						{items.map(({ id }) => (
							<Card
								title={id}
								itemId={id}
								key={id}
								onClick={handleItemClick(id)}
								selected={id === selected}
							/>
						))}
					</ScrollMenu>
				</div>
				<Footer />
			</div>
		</>
	);
}
export default App;

function onWheel(
	{ getItemById, items, visibleItems, scrollToItem }: scrollVisibilityApiType,
	ev: React.WheelEvent,
): void {
	const isThouchpad = Math.abs(ev.deltaX) !== 0 || Math.abs(ev.deltaY) < 15;

	if (isThouchpad) {
		ev.stopPropagation();
		return;
	}

	if (ev.deltaY < 0) {
		// NOTE: for center items
		const nextGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).next();
		const { center } = getItemsPos(nextGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	} else if (ev.deltaY > 0) {
		const prevGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).prev();
		const { center } = getItemsPos(prevGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	}
}

ReactDOM.render(<App />, document.getElementById('root'));
