import { ApiFilled } from '@ant-design/icons';
import { API } from 'utils/api';

/* eslint-disable no-shadow */
const { default: axios } = require('axios');
const { useState, useEffect } = require('react');

const useGetData = () => {
	const [data, setData] = useState();
	const [isLoading, setIsLoading] = useState(false);
	const [error, setError] = useState('');

	const initialization = () => {
		setData(undefined);
		setIsLoading(false);
	};

	const HocGet = (url) => {
		const fetchData = async () => {
			try {
				await API.get(url)
					.then((res) => {
						setData(res);
					})
					.finally(() => {
						setIsLoading(true);
						initialization();
					});
			} catch (error) {
				// setError(error); 에러를 내보내고 싶으면 이렇게 하기
				throw new Error(error);
			}
		};

		if (!isLoading) {
			fetchData();
		}
	};

	// useEffect(() => {
	// 	const fetchData = async () => {
	// 		try {
	// 			await API.get(url)
	// 				.then((res) => {
	// 					setData(res);
	// 				})
	// 				.finally(() => {
	// 					setIsLoading(true);
	// 				});
	// 		} catch (error) {
	// 			// setError(error); 에러를 내보내고 싶으면 이렇게 하기
	// 			throw new Error(error);
	// 		}
	// 	};

	// 	if (!isLoading) {
	// 		fetchData();
	// 	}
	// }, [url]);

	return { data, error, isLoading, HocGet };
};

export default useGetData;
