			> div {
				margin-bottom: 20px;
			}
			.header {
				color: #999;
				width: 80px;
			}
			.body,
			.header {
				display: inline-block;
				font-size: 16px;
				font-weight: 600;
				vertical-align: text-top;
			}
		}

		.divider {
			margin: 0;
			border: 0;
			border-top: 1px solid #eee;
			box-sizing: content-box;
			height: 0;
		}

		.jobdecription {
			margin-bottom: 60px;
			padding-right: 20px;
			margin: 40px 0;
			.skill-wrapper {
				display: flex;
				flex-wrap: wrap;
				grid-gap: 10px 8px;
				gap: 10px 8px;
				margin-top: 5px;
			}
			.skillitem {
				background-color: #e4f4ec;
				border-radius: 20px;
				padding: 9px 14px;
				font-size: 12px;
				line-height: 14.4px;
				font-weight: 600;
			}
			p {
				color: #333;
				font-weight: 400;
				word-wrap: break-word;
				white-space: pre-wrap;

				font-size: 16px;
				line-height: 1.75;
			}
		}
		.jobheader {
			margin: 40px 0 30px;
			.taglist {
				margin-top: 20px;
				> ul > li {
					display: inline-block;
				}
				> ul > li > a {
					display: inline-block;
					margin-right: 6px;
					margin-bottom: 10px;
					padding: 9px 14px;
					font-size: 12px;
					font-weight: 500;
					line-height: 1;
					color: #333;
					background-color: #f3f5f8;
					border-radius: 25px;
				}
			}
			> h2 {
				color: #333;
				font-size: 22px;
				font-weight: 600;
				margin: 0 0 10px;
				word-break: keep-all;
				word-wrap: break-word;
			}
			> div > h6 {
				color: #999;
				font-size: 14px;
				font-weight: 600;
				margin: 0;
				display: inline-flex;
			}
			> div > div {
				display: inline-flex;
			}
			.label-response-container {
				display: inline-flex;
				white-space: nowrap;
				margin: 0 0 0 10px;
				position: relative;
				top: -1px;
			}
			> div > span {
				padding: 0 10px;
				position: relative;
				white-space: nowrap;
				margin-left: 10px;
				color: #999;
				:before {
					content: '';
					display: block;
					width: 1px;
					height: 12px;
					background: #e1e2e3;
					position: absolute;
					top: 4px;
					left: 0;
				}
				.header-dot {
					margin: 0 5px;
					top: -3px;
					position: relative;
				}
				> div:after {
					display: block;
					content: '';
					clear: both;
				}
			}
		}
	}
`;
