import { WindowsFilled } from '@ant-design/icons';
import ArticleEmployment from 'components/common/ArticleEmployment';
import LabelResponseLevel from 'components/LabelResponseLevel';
import React, { useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import styled from 'styled-components';
import { DescriptionDummy } from 'utils/mockData';

function WdPage() {
	const { wdIndex } = useParams();
	const ref = useRef();
	const testArr = new Array(12).fill(0);

	const handleScrollPrev = (type) => {
		if (type === 'left') {
			ref.current.scrollBy({
				top: 0,
				left: -1,
				behavior: 'smooth',
			});
		} else {
			ref.current.scrollBy({
				top: 0,
				left: 1,
				behavior: 'smooth',
			});
		}
	};

	return (
		<ComponentWrapper>
			<div className="jobDetail-container">
				<div style={{ position: 'relative' }}>
					<div className="jobcontent-container">
						<section className="jobimage-imgcontainer">
							<div className="jobimage-slider" ref={ref}>
								<Button
									onClick={() => {
										handleScrollPrev('left');
									}}
									className="arrow-left"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<Button
									onClick={() => {
										handleScrollPrev('right');
									}}
									className="arrow-right"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2F2cv2kbezyp0xheqs__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
							</div>
						</section>
						<section className="jobheader">
							<h2>Senior AI Engineer (시니어 AI 개발자)</h2>
							<div>
								<h6 style={{ maxWidth: 'calc(100% - 88px)' }}>
									<a
										href="/company/28654"
										data-company-name="엔도아이(endoAI)"
									>
										엔도아이(endoAI)
									</a>
								</h6>
								<LabelResponseLevel />
								<span className="location-conatiner">
									서울
									<span className="header-dot">.</span>
									한국
								</span>
							</div>
							<div className="taglist">
								<ul>
									<li>
										<a href="/">#육아휴직</a>
									</li>
									<li>
										<a href="/">#음료</a>
									</li>
									<li>
										<a href="/">#커피</a>
									</li>
									<li>
										<a href="/">#교육비</a>
									</li>
								</ul>
							</div>
						</section>
						<div className="jobcontent-secription-warpper">
							<section className="jobdecription">
								<p>{DescriptionDummy}</p>
								<div className="skill-wrapper">
									<div className="skillitem">Git</div>
									<div className="skillitem">GitHub</div>
									<div className="skillitem">GitLab</div>
									<div className="skillitem">Pytorch</div>
									<div className="skillitem">Tensroflow</div>
									<div className="skillitem">C / C++</div>
								</div>
							</section>
							<hr className="divider" />
							<section className="jobworkplace">
								<div>
									<span className="header">마감일</span>
									<span className="body">상시</span>
								</div>
								<div>
									<span className="header">근무지역</span>
									<span className="body">
										서울특별시 서초구 마방로2길 89
									</span>
								</div>
								<a
									className="navepMap"
									href="https://map.naver.com/v5/search/서울특별시 서초구 양재천로29길 15 진미빌딩?"
									rel="noreferrer noopener"
									target="_blank"
								>
									<img
										alt="Map with company address"
										src="https://naveropenapi.apigw.ntruss.com/map-static/v2/raster-cors?w=700&amp;h=254&amp;markers=type:d|size:mid|pos:127.041813 37.4795099|color:red&amp;scale=2&amp;X-NCP-APIGW-API-KEY-ID=z7e7ujba1u"
									/>
								</a>
							</section>
						</div>
						<section className="companyInfo">
							<button type="button" className="left">
								<div
									className="logo"
									style={{
										backgroundImage:
											'url("https://static.wanted.co.kr/images/wdes/0_5.6f5b2f0f.png")',
									}}
								/>
								<div>
									<h5>엔도아이(endoAI)</h5>
									<h6>IT, 컨텐츠</h6>
								</div>
							</button>
							<button type="button" className="btn-follow">
								<span className="Button_Button__label__1Kk0v">
									팔로우
								</span>
							</button>
						</section>
						<section>
							<div className="warning">
								<div className="warning-header">
									<svg
										width="24"
										height="24"
										viewBox="0 0 24 24"
									>
										<g
											fill="currentColor"
											fillRule="evenodd"
										>
											<path
												fillRule="nonzero"
												d="M15.266 20.658A9.249 9.249 0 0112 21.25a9.25 9.25 0 010-18.5 9.21 9.21 0 016.54 2.71A9.217 9.217 0 0121.25 12a9.213 9.213 0 01-2.71 6.54.75.75 0 101.061 1.062A10.713 10.713 0 0022.75 12c0-2.89-1.146-5.599-3.149-7.601A10.717 10.717 0 0012 1.25C6.063 1.25 1.25 6.063 1.25 12S6.063 22.75 12 22.75c1.31 0 2.591-.235 3.794-.688a.75.75 0 10-.528-1.404z"
											/>
											<path d="M13 16a1 1 0 11-2 0 1 1 0 012 0" />
											<path
												fillRule="nonzero"
												d="M11.25 7v5a.75.75 0 101.5 0V7a.75.75 0 10-1.5 0z"
											/>
										</g>
									</svg>
									<div className="warning-header-content">
										<h5>
											본 채용정보는 원티드랩의 동의없이
											무단전재, 재배포, 재가공할 수&nbsp;
											없으며, 구직활동 이외의&nbsp;
											<br className="break-lg" />
											용도로 사용할 수 없습니다.
										</h5>
										<button type="button">
											<i className="icon-cheveron-right" />
										</button>
									</div>
								</div>
							</div>
						</section>
					</div>
					<aside className="jobprocess-container">
						<div>
							<header>
								<div className="reward-container">
									<h3>채용보상금</h3>
									<ul>
										<li>
											<h4>추천인</h4>
											<p>500,000원</p>
										</li>
										<li>
											<h4>지원자</h4>
											<p>500,000원</p>
										</li>
									</ul>
									<button className="btn-share" type="button">
										<svg
											xmlns="https://www.w3.org/2000/svg"
											width="20"
											height="20"
											viewBox="0 0 19 19"
										>
											<defs>
												<path
													id="shareIcon"
													d="M5.336 7.75c-.551-.703-1.418-1.136-2.365-1.136C1.337 6.614 0 7.898 0 9.494c0 1.596 1.336 2.879 2.971 2.879.93 0 1.785-.419 2.338-1.102l8.495 4.482c.128.068.276.092.42.068l.025-.004c.213-.036.395-.173.489-.367.101-.21.249-.393.437-.54.673-.526 1.643-.407 2.168.266.526.673.407 1.643-.265 2.167-.673.526-1.643.407-2.168-.266-.226-.29-.644-.34-.933-.115-.29.226-.34.644-.115.933.977 1.251 2.783 1.473 4.034.496 1.25-.976 1.472-2.782.495-4.033-.977-1.251-2.783-1.473-4.033-.496-.169.132-.32.28-.454.442L5.478 9.858c-.322-.241-.816-.145-1 .255-.259.558-.844.93-1.507.93-.913 0-1.642-.7-1.642-1.55 0-.849.73-1.55 1.642-1.55.636 0 1.2.343 1.473.863.107.368.526.64.954.413l9.026-4.762.118-.079.029-.024c.233-.197.303-.527.169-.8-.104-.212-.158-.442-.158-.68 0-.853.692-1.545 1.544-1.545.853 0 1.545.692 1.545 1.544 0 .854-.691 1.545-1.545 1.545-.367 0-.664.297-.664.664 0 .367.297.665.664.665C17.714 5.747 19 4.46 19 2.873 19 1.287 17.713 0 16.126 0c-1.586 0-2.873 1.287-2.873 2.873 0 .224.026.445.076.66L5.336 7.748z"
												/>
											</defs>
											<g fill="none" fillRule="evenodd">
												<use
													fill="#36F"
													xlinkHref="#shareIcon"
												/>
											</g>
										</svg>
									</button>
								</div>
								<button className="btn-bookmark" type="button">
									<svg
										width="13"
										height="17"
										viewBox="0 0 13 17"
										style={{ color: 'rgb(51, 102, 255)' }}
									>
										<defs>
											<path
												id="bookmarkIconLine"
												d="M1.481 1.481h9.382v10.727c0 .409.331.74.74.74.41 0 .741-.331.741-.74V.74c0-.41-.331-.741-.74-.741H.74C.33 0 0 .332 0 .74v14.814c0 .568.614.925 1.108.643l5.18-2.873 5.104 2.873c.355.203.807.08 1.01-.276.203-.355.08-.808-.275-1.01l-5.471-3.083c-.228-.13-.507-.13-.735 0l-4.44 2.45V1.48z"
											/>
										</defs>
										<g fill="none" fillRule="evenodd">
											<use
												fill="currentColor"
												xlinkHref="#bookmarkIconLine"
											/>
										</g>
									</svg>
									북마크하기
								</button>
								<div className="btn-apply">
									<button type="button">지원하기</button>
								</div>
								<div className="reaction">
									<button className="likes" type="button">
										<i className="icon-heart" />
										<span>0</span>
									</button>
								</div>
							</header>
							<footer className="footer-jobprogress" />
							<div className="wantedAi-container">
								<div className="item-content">
									<img
										src="https://static.wanted.co.kr/images/ai/logo-wantedai.png"
										width="70"
										alt="logo-wantedai"
										style={{ marginBottom: '3px' }}
									/>
									<span>합격예측</span>
								</div>
								<div className="item-description">
									서류합격률이 궁금하다면?
									<button
										type="button"
										className="btn-question"
									>
										<svg
											width="17"
											height="17"
											className=""
											viewBox="0 0 17 17"
										>
											<defs>
												<filter id="bfoh3u0w3a">
													<feColorMatrix
														in="SourceGraphic"
														values="0 0 0 0 0.600000 0 0 0 0 0.600000 0 0 0 0 0.600000 0 0 0 1.000000 0"
													/>
												</filter>
											</defs>
											<g fill="none" fillRule="evenodd">
												<g>
													<g>
														<g transform="translate(-1080 -374) translate(1080 374)">
															<g>
																<path
																	stroke="#999"
																	strokeLinecap="round"
																	strokeLinejoin="round"
																	strokeWidth="1.2"
																	d="M9.421 13.334c-.736.277-1.535.43-2.368.43-3.706 0-6.71-3.005-6.71-6.711 0-3.707 3.004-6.71 6.71-6.71 1.853 0 3.53.75 4.745 1.965 1.214 1.214 1.965 2.892 1.965 4.745 0 1.853-.75 3.53-1.965 4.745"
																	transform="translate(1 1)"
																/>
																<path
																	fill="#999"
																	d="M6.382 10.408c0-.371.3-.671.67-.671.371 0 .672.3.672.67 0 .372-.3.672-.671.672-.37 0-.671-.3-.671-.671"
																	transform="translate(1 1) rotate(-180 7.053 10.408)"
																/>
																<path
																	stroke="#999"
																	strokeLinecap="round"
																	strokeLinejoin="round"
																	strokeWidth="1.2"
																	d="M5.04 5.655c0-1.08.901-1.958 2.013-1.958 1.11 0 2.013.877 2.013 1.958 0 1.08-1.007 1.957-2.013 1.957v.783"
																	transform="translate(1 1)"
																/>
															</g>
														</g>
													</g>
												</g>
											</g>
										</svg>
									</button>
								</div>
							</div>
						</div>
					</aside>
				</div>
				<div className="associated-container">
					<h5 className="associated-title">
						이해찬님, 이 포지션을 찾고 계셨나요?
					</h5>
					<div className="associated-joblist">
						<ul>
							{testArr.map((value) => (
								<li>
									<ArticleEmployment />
								</li>
							))}
						</ul>
					</div>
				</div>
			</div>
		</ComponentWrapper>
	);
}

const Button = styled.button`
	position: absolute;
	width: 9%;
	height: 100%;
	font-size: 26px;
	color: #b5b5b5;
	opacity: 0.7;
	z-index: 1;
`;

const ComponentWrapper = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	max-width: 1060px;

	.jobprocess-container {
		top: 70px;
		width: 340px;
		position: fixed;
		right: calc((100% - 1060px) / 2);

		.footer-jobprogress {
			margin-top: 10px;
		}
		.wantedAi-container {
			margin-top: 4px;
			align-items: stretch;
			display: flex;
			flex-direction: row;
			flex-wrap: nowrap;
			justify-content: space-between;
			align-content: stretch;
			border-radius: 4px;
			background-color: #f3f5f8;
			padding: 11px 15px 10px;
			.item-description {
				line-height: 1.5;
				color: #999;
			}

			<style > .WantedAi_container_item__I9Fkv {
				-ms-flex-order: 0;
				order: 0;
				-ms-flex: 0 1 auto;
				flex: 0 1 auto;
				-ms-flex-item-align: auto;
				align-self: auto;
				font-size: 12px;
			}
			.item-content {
				order: 0;
				flex: 0 1 auto;
				align-self: auto;
				font-size: 12px;
				color: #111;
				> img {
					margin-right: 5px;
					vertical-align: middle;
				}
				> span {
					font-weight: 600;
					margin-right: 5px;
					vertical-align: -1px;
				}
			}
		}

		header {
			width: 340px;
			border: 1px solid #e1e2e3;
			background-color: #fff;
			padding: 24px 20px;
			.reaction {
				margin: 24px 0 0;
				height: 30px;
				.likes {
					height: 30px;
					border-radius: 15px;
					border: 1px solid #e1e2e3;
					margin-right: 12px;
					padding: 0 15px;
					float: left;
					> i {
						/* 좋아요했을 때  */
						color: #fe415c;
						font-size: 16px;
						/* color: #dbdbdb; */
						margin-right: 10px;
						position: relative;
						top: 1px;
					}
					> span {
						font-size: 14px;
						font-weight: 600;
						line-height: 30px;
						letter-spacing: normal;
						text-align: left;
						color: #333;
						position: relative;
						top: -1px;
					}
				}
			}
			.btn-apply {
				order: 0;
				flex: 1 1;
				align-self: auto;
				> button {
					width: 100%;
					height: 50px;
					border-radius: 25px;
					background-color: #36f;
					font-size: 16px;
					font-weight: 600;
					letter-spacing: normal;
					color: #fff;
					text-align: center;
				}
			}
			.btn-bookmark {
				display: flex;
				flex-direction: row;
				flex-wrap: nowrap;
				justify-content: center;
				align-content: stretch;
				align-items: center;
				width: 100%;
				height: 50px;
				border-radius: 25px;
				background-color: #fff;
				border: 1px solid #36f;
				font-size: 16px;
				font-weight: 600;
				letter-spacing: normal;
				color: #36f;
				text-align: center;
				margin-bottom: 10px;
				> svg {
					margin-right: 8px;
				}
			}
			.btn-share {
				margin-bottom: 10px;
				position: absolute;
				top: 20px;
				right: 20px;
				width: 40px;
				height: 40px;
				background: #fff;
				border-radius: 20px;
				border: 1px solid #e1e2e3;
				display: flex;
				align-items: center;
				justify-content: center;
			}
			h3 {
				font-size: 15px;
				font-weight: 600;
				letter-spacing: normal;
				text-align: left;
				color: #333;
			}
			ul {
				overflow: hidden;
				margin: 24px 0;
				> li {
					float: left;
					width: 50%;
					letter-spacing: normal;
					text-align: left;
				}
			}
		}
	}

	.associated-container {
		margin: 80px 0 0;
		.associated-title {
			margin: 0 0 20px;
			font-size: 20px;
			font-weight: 600;
			color: #333;
			line-height: normal;
		}
		.associated-joblist {
			margin: -10px;
			padding: 0;
			ul li {
				width: 25%;
				padding: 10px;
				list-style: none;
				display: inline-block;
				vertical-align: top;
			}
		}
	}

	.warning {
		margin-top: 10px;
		border-radius: 5px;
		background-color: #f3f5f8;
		.warning-header {
			padding: 25px 30px;
			display: flex;
			align-items: center;
		}

		.warning-header-content {
			width: 100%;
			display: flex;
			justify-content: space-between;
			align-items: center;
			> h5 {
				margin: 0 20px;
				font-size: 12px;
				font-weight: 700;
				line-height: 22px;
				color: #333;
			}
			i {
				display: block;
				transform: rotate(90deg);
			}
		}
	}

	.companyInfo {
		border-radius: 3px;
		border: 1px solid #e1e2e3;
		padding: 20px;
		margin: 80px 0 0;
		display: flex;
		flex-direction: row;
		justify-content: space-between;
		align-items: center;
		background: #fff;
		.btn-follow {
			border: 1px solid #e1e2e3;
			height: 40px;
			font-size: 15px;
			color: #36f;
			background-color: #fff;
			border: 1px solid inherit;
			position: relative;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			vertical-align: middle;
			min-width: 64px;
			padding: 0 27px;
			box-sizing: border-box;
			border-radius: 25px;
			font-weight: 700;
			line-height: normal;
			cursor: pointer;
		}
		h6 {
			color: #999;
			font-size: 15px;
			font-weight: 600;
			margin: 0;
			padding-right: 10px;
		}
		h5 {
			color: #333;
			font-size: 15px;
			font-weight: 600;
			margin: 0 0 5px;
			word-break: break-word;
			display: -webkit-box;
			-webkit-line-clamp: 2;
			-webkit-box-orient: vertical;
			overflow: hidden;
			padding-right: 10px;
		}
		.logo {
			background-position: 50%;
			background-size: contain;
			width: 50px;
			height: 50px;
			box-shadow: 0 0 1px 0 rgb(0 0 0 / 10%);
			margin-right: 15px;
			background-repeat: no-repeat;
		}
		.left {
			display: flex;
			align-items: center;
			flex-direction: row;
			text-align: left;
		}
	}

	.jobimage-imgcontainer {
		position: relative;
		overflow: hidden;

		.jobimage-slider {
			display: flex;
			flex-wrap: nowrap;
			flex-direction: row;
			align-items: flex-start;
			padding-bottom: 4px;
			height: auto;
			overflow-x: auto;
			scroll-snap-type: x mandatory;
			overscroll-behavior: contain;
			.arrow-right {
				right: 0;
			}
			.arrow-left {
				i {
					display: block;
					transform: rotate(180deg);
				}
			}
			::-webkit-scrollbar {
				height: 10px;
			}
			::-webkit-scrollbar-thumb {
				background-color: #b5b5b5;
				border-radius: 10px;
			}

			.img-jobimage {
				scroll-snap-align: start;
				position: relative;
				width: 100%;
				flex-shrink: 0;
				padding-bottom: 70%;
			}
			img {
				position: absolute;
				width: 100%;
				height: 100%;
				left: 0;
				top: 0;
				border-radius: 4px;
				object-fit: cover;
				outline: 1px solid rgba(0, 0, 0, 0.05);
				outline-offset: -1px;
			}
		}
	}
	.jobcontent-container {
		display: inline-block;
		width: calc(100% - 360px);
		vertical-align: top;

		.jobworkplace {
			margin: 40px 0;
			.navepMap {
				display: block;
				width: 100%;
				height: 100%;
				> img {
					display: block;
					width: 100%;
					background-size: cover;
					background-position: 50%;
				}
			}
			> div {
				margin-bottom: 20px;
			}
			.header {
				color: #999;
				width: 80px;
			}
			.body,
			.header {
				display: inline-block;
				font-size: 16px;
				font-weight: 600;
				vertical-align: text-top;
			}
		}

		.divider {
			margin: 0;
			border: 0;
			border-top: 1px solid #eee;
			box-sizing: content-box;
			height: 0;
		}

		.jobdecription {
			margin-bottom: 60px;
			padding-right: 20px;
			margin: 40px 0;
			.skill-wrapper {
				display: flex;
				flex-wrap: wrap;
				grid-gap: 10px 8px;
				gap: 10px 8px;
				margin-top: 5px;
			}
			.skillitem {
				background-color: #e4f4ec;
				border-radius: 20px;
				padding: 9px 14px;
				font-size: 12px;
				line-height: 14.4px;
				font-weight: 600;
			}
			p {
				color: #333;
				font-weight: 400;
				word-wrap: break-word;
				white-space: pre-wrap;

				font-size: 16px;
				line-height: 1.75;
			}
		}
		.jobheader {
			margin: 40px 0 30px;
			.taglist {
				margin-top: 20px;
				> ul > li {
					display: inline-block;
				}
				> ul > li > a {
					display: inline-block;
					margin-right: 6px;
					margin-bottom: 10px;
					padding: 9px 14px;
					font-size: 12px;
					font-weight: 500;
					line-height: 1;
					color: #333;
					background-color: #f3f5f8;
					border-radius: 25px;
				}
			}
			> h2 {
				color: #333;
				font-size: 22px;
				font-weight: 600;
				margin: 0 0 10px;
				word-break: keep-all;
				word-wrap: break-word;
			}
			> div > h6 {
				color: #999;
				font-size: 14px;
				font-weight: 600;
				margin: 0;
				display: inline-flex;
			}
			> div > div {
				display: inline-flex;
			}
			.label-response-container {
				display: inline-flex;
				white-space: nowrap;
				margin: 0 0 0 10px;
				position: relative;
				top: -1px;
			}
			> div > span {
				padding: 0 10px;
				position: relative;
				white-space: nowrap;
				margin-left: 10px;
				color: #999;
				:before {
					content: '';
					display: block;
					width: 1px;
					height: 12px;
					background: #e1e2e3;
					position: absolute;
					top: 4px;
					left: 0;
				}
				.header-dot {
					margin: 0 5px;
					top: -3px;
					position: relative;
				}
				> div:after {
					display: block;
					content: '';
					clear: both;
				}
			}
		}
	}
`;

export default WdPage;
