import { InputContainer } from 'components/common/styled';
import React from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';

function LoginPassword({ email }) {
	const {
		register,
		handleSubmit,
		formState: { errors },
	} = useForm();

	return (
		<ComponentWrapper>
			<div className="modal-header-wrapper">
				회원가입
				<button type="button">
					<svg
						width="24"
						height="24"
						viewBox="0 0 24 24"
						color="#999"
					>
						<path
							fill="currentColor"
							d="M17.97 19.03a.75.75 0 001.06-1.06l-6.5-6.5a.75.75 0 00-1.06 0l-6.5 6.5a.75.75 0 001.06 1.06L12 13.06l5.97 5.97zM12 10.94L6.03 4.97a.75.75 0 00-1.06 1.06l6.5 6.5a.75.75 0 001.06 0l6.5-6.5a.75.75 0 00-1.06-1.06L12 10.94z"
						/>
					</svg>
				</button>
			</div>

			<div className="modal-content-wrapper">
				<div className="modal-emailinput-wrapper">
					<p>비밀번호</p>
					<div>
						<InputContainer
							{...register('password', {
								required: true,
							})}
							type="password"
							placeholder="비밀번호을 입력해 주세요."
							errortype={(typeof errors.password).toString()}
						/>

						{errors.password?.type === 'required' && (
							<div className="error-style">
								비밀번호은 필수정보 입니다.
							</div>
						)}
					</div>
				</div>
				<button
					style={{ marginTop: '20px' }}
					className="EnterPassword_button__Of971"
					id="setpassword-form"
					// onClick={handleSubmit(handleonSubmit, handleonError)}
					type="submit"
					form="setpassword-form"
					// disabled={!acceptPrivacy}
				>
					로그인
				</button>
				<button type="button" className="style_wrapper__IgK7U as-link">
					비밀번호 초기화/변경
				</button>
			</div>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.div`
	.EnterPassword_button__Of971 {
		margin-top: 10px;
		display: flex;
		justify-content: center;
		align-items: center;
		width: 100%;
		height: 54px;
		border: 0;
		border-radius: 27px;
		background-color: #36f;
		font-size: 16px;
		font-weight: 600;
		color: #fff;
		cursor: pointer;
	}
	.EnterPassword_link__faqkR {
		margin-top: 4px;
	}
	.style_wrapper__IgK7U {
		background-color: transparent;
		font-size: 14px;
		color: #36f;
		margin-top: 10px;
		display: flex;
		justify-content: center;
		align-items: center;
		width: 100%;
		height: 54px;
		font-family: inherit;
		border: 0;
		border-radius: 27px;
		font-weight: 600;
		cursor: pointer;
	}
`;

export default LoginPassword;
