import React, { Component } from 'react';
import styled from 'styled-components';

function ArticleCard({
	linkUrl,
	imgUrl,
	title,
	subtitle,
	publisher,
	publisherimgUrl,
	type,
}) {
	return (
		<ComponentWrapper type={type}>
			<a href={linkUrl}>
				<div className="img-wrapper">
					<img src={imgUrl} alt="[삼삼한] 목표, 3가지 키워드" />
				</div>
				<p className="title-wrapper">{title}</p>
				<p className="subcontent-wrapper">{subtitle}</p>
				<div className="publisher-wrapper">
					<img
						src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Ft1.daumcdn.net%2Fbrunch%2Fstatic%2Ficon%2Fios%2Ficon120.png&amp;w=60&amp;q=90"
						alt="brunch"
					/>
					<p>{publisher}</p>
				</div>
			</a>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.li`
	display: flex;
	flex-direction: column;
	width: ${(props) => !(props.type === 'List') && 'calc(25% - 20px)'};
	margin: 0 10px 50px;
	.publisher-wrapper {
		margin-top: 12px;
		display: flex;
		-ms-flex-align: center;
		align-items: center;
		img {
			width: 30px;
			height: 30px;
			border-radius: 50%;
			border: 1px solid #ececec;
		}
		p {
			margin-left: 10px;
			overflow: hidden;
			text-overflow: ellipsis;
			white-space: nowrap;
			font-style: normal;
			font-weight: 600;
			font-size: 13px;
			line-height: 19px;
			color: #aaa;
		}
	}
	.subcontent-wrapper {
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
		font-style: normal;
		font-weight: 600;
		font-size: 13px;
		line-height: 21px;
		color: #aaa;
	}
	.title-wrapper {
		overflow: hidden;
		text-overflow: ellipsis;
		font-style: normal;
		font-weight: 700;
		font-size: 16px;
		line-height: 24px;
		color: #333;
		margin: 17px 0 8px;
	}
	.img-wrapper {
		position: relative;
		height: auto;
		padding-bottom: 70%;
		padding-left: 0;
		img {
			position: absolute;
			width: 100%;
			height: 100%;
			left: 0;
			top: 0;
			border-radius: 4px;
			object-fit: cover;
			object-position: top;
			outline: 1px solid rgba(0, 0, 0, 0.05);
			outline-offset: -1px;
		}
	}
`;

export default ArticleCard;
