import React from 'react';
import styled from 'styled-components';

function SalaryBody() {
	return (
		<ComponentWrapper>
			<div className="Salary_bodyContainer__xtDQ9">
				<h5 className="Salary_bodyContainer_cautionText__0sV55">
					<sup>*</sup> 이 데이터는 채용 정보에 포함된 직무 별 요구
					경력(최저, 최대)과 연봉(최저, 최대)을 바탕으로 추정한 예상
					연봉 데이터입니다.
				</h5>
				<div className="MatchupBanner_MatchupBanner__xIrVp">
					<div className="MatchupBanner_MatchupBanner_title__UgL50">
						이제 밤새워 채용사이트 보지 마세요.
					</div>
					<div className="MatchupBanner_MatchupBanner_desc__cTAqB">
						원티드 매치업에 프로필을 등록하면, 기업의 인사담당자가
						직접 면접을 제안합니다.
					</div>
					<div className="MatchupBanner_MatchupBanner_companyListContainer__Dv1v9">
						<ul className="MatchupBanner_MatchupBanner_companyListContainer_companyList___W6cw">
							<li
								style={{
									backgroundImage:
										'url("https://static.wanted.co.kr/images/wdes/0_5.c52b0714.jpeg")',
									backgroundPosition: 'center center',
									backgroundSize: 'cover',
								}}
							/>
						</ul>
						<div className="MatchupBanner_MatchupBanner_companyListContainer_lastItem__kvu7P">
							+8714
						</div>
					</div>
					<a href="/profile/matching" className="" aria-label="">
						시작하기
					</a>
				</div>
				<div className="List_List_titleWrapper__gjWiM">
					<h2 className="List_List_title__a92fx">
						연봉 업그레이드 포지션
					</h2>
					<a
						href="/wdlist/10101/10109"
						className="List_List_button__nysEk"
						aria-label=""
						data-attribute-id="salary__seeMore"
					>
						더 보기
					</a>
				</div>
				<div className="List_List__kDrrx List_List_jobList__RXxgF">
					<div className="List_List_container__JnQMS">
						<ul data-cy="job-list" className="clearfix">
							<li>
								<div
									data-cy="job-card"
									className="Card_className__u5rsb"
								>
									<a href="/wd/119638">
										<header
											style={{
												backgroundImage:
													'url("https://image.wanted.co.kr/optimize?src=https://static.wanted.co.kr/images/company/11372/k1mmnamjxfeyfulh__400_400.jpg&amp;w=400&amp;q=75")',
											}}
										>
											<button
												className="bookmarkBtn"
												type="button"
												aria-label="bookmark button"
												data-attribute-id="position__bookmark__click"
												data-job-category-id="10101"
												data-job-category="Education"
												data-company-id="11372"
												data-company-name="모두의연구소"
												data-position-id="119638"
												data-position-name="교육 총괄 매니저"
												data-kind="add"
											>
												<svg
													width="22"
													height="22"
													viewBox="0 0 18 18"
													fill="none"
													xmlns="https://www.w3.org/2000/svg"
												>
													<path
														fillRule="evenodd"
														clipRule="evenodd"
														d="M3.58065 1C3.25997 1 3 1.26206 3 1.58533V16.4138C3 16.8632 3.48164 17.145 3.86873 16.922L9.00004 13.9662L14.1313 16.922C14.5184 17.145 15 16.8632 15 16.4138V1.58533C15 1.26206 14.74 1 14.4194 1H9.00004H3.58065ZM8.71195 12.7838C8.89046 12.681 9.10961 12.681 9.28812 12.7838L13.8387 15.4052V2.17067H9.00004H4.1613V15.4052L8.71195 12.7838Z"
														fill="white"
													/>
													<path
														d="M9.28812 12.7838C9.10961 12.681 8.89046 12.681 8.71195 12.7838L4.1613 15.4052V2.17067H9.00004H13.8387V15.4052L9.28812 12.7838Z"
														fill="black"
														fillOpacity="0.25"
													/>
												</svg>
											</button>
										</header>
										<div className="body">
											<div className="job-card-position">
												교육 총괄 매니저
											</div>
											<div className="job-card-company-name">
												모두의연구소
											</div>
											<div className="job-card-company-location">
												서울
												<span className="addressDot">
													.
												</span>
												<span>한국</span>
											</div>
											<div className="reward">
												채용보상금 1,000,000원
											</div>
										</div>
									</a>
								</div>
							</li>
						</ul>
						<div />
					</div>
				</div>
			</div>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.div`
	.Salary_bodyContainer__xtDQ9 {
		width: 90%;
		max-width: 1060px;
		margin: 0 auto 80px;
	}
	.Salary_bodyContainer_cautionText__0sV55 {
		margin: 10px 0;
		font-size: 14px;
		font-weight: 400;
		color: #b5b5b5;
	}
	.MatchupBanner_MatchupBanner__xIrVp {
		margin: 42px 0 40px;
		padding: 55px;
		background: #fff;
		> a {
			display: -ms-flexbox;
			display: flex;
			-ms-flex-pack: center;
			justify-content: center;
			-ms-flex-align: center;
			align-items: center;
			width: 100%;
			height: 54px;
			border: 0;
			border-radius: 27px;
			background-color: #36f;
			font-size: 16px;
			font-weight: 600;
			color: #fff !important;
			cursor: pointer;
		}
	}
	.MatchupBanner_MatchupBanner_title__UgL50 {
		font-size: 40px;
		font-weight: 600;
		font-style: normal;
		font-stretch: normal;
		line-height: 1;
		letter-spacing: normal;
		color: #258bf7;
		margin: 0 0 20px;
		word-break: keep-all;
	}
	.MatchupBanner_MatchupBanner_desc__cTAqB {
		font-size: 20px;
		font-weight: 400;
		font-style: normal;
		font-stretch: normal;
		line-height: 1;
		letter-spacing: normal;
		color: #000;
		margin: 0 0 40px;
		word-break: keep-all;
	}
	.MatchupBanner_MatchupBanner_companyListContainer__Dv1v9 {
		position: relative;
		ul {
			margin: 30px -6px;
			padding-right: 48px;
			height: 55px;
			overflow: hidden;
			> li {
				width: 48px;
				height: 48px px;
				border-radius: 3px;
				border: 1px solid #eee;
				float: left;
				margin: 6px;
			}
		}
	}
	.List_List_titleWrapper__gjWiM {
		position: relative;
		margin-bottom: 20px;
	}
	.List_List_title__a92fx {
		display: inline-block;
		padding: 0;
		margin: 0;
		font-size: 22px;
		font-weight: 600;
