import React, { useState } from 'react';
import styled from 'styled-components';

function LikeBtn({ handleClickAside, likeCount }) {
	// const [likeCnt, setLikeCnt] = useState(likeCount);

	return (
		<ComponentWrapper className="reaction">
			<button
				onClick={() => {
					handleClickAside('like');
				}}
				className="likes"
				type="button"
			>
				<i className="icon-heart" />
				<span>{likeCount}</span>
			</button>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.div`
	i {
		color: ${(props) => props.isLogin && '#fe415c !important'};
	}
`;

export default LikeBtn;
