const Aside = styled.aside`
	height: 100%;
	padding: 10px 0;
	.btn-signup {
		margin-right: 6px;
		line-height: 1.4;
		padding: 0 3px;
		font-size: 14px;
		color: #333;
		font-weight: 600;
		font-family: inherit;
	}
	> ul {
		height: 100%;
		margin: 0;
		.left-division {
			display: inline-flex;
			::before {
				display: block;
				content: '';
				width: 1px;
				height: 10px;
				background-color: #e1e2e3;
				margin: auto 10px auto 5px;
			}
		}
	}
	.profilebox {
		display: inline-flex;
		flex-direction: row;
		align-items: center;
		margin-right: 10px;
		.avatarBorder {
			width: 32px;
			height: 32px;
			border-radius: 50%;
			border: 1px solid #e1e2e3;
			background-color: #fff;
			display: flex;
			justify-content: center;
			align-items: center;
			overflow: hidden;
			> div {
				width: 28px;
				height: 28px;
				border-radius: 50%;
				background-position: 50%;
				background-size: cover;
				background-repeat: no-repeat;
			}
		}
	}
	> ul li {
		position: relative;
		display: inline-block;
		height: 100%;
		vertical-align: middle;
		margin-right: 5px;
		* {
			font-size: 14px;
			color: #333;
			font-weight: 600;
			line-height: 1;
		}
		.btn-search {
			position: relative;
			margin-top: 5px;
			padding: 0 5px;
		}

		.btn-notification {
			position: relative;
			margin-top: 5px;
			padding: 0 5px;
		}
		.btn-profile {
			height: 32px;
			display: flex;
			align-items: center;
			justify-content: space-between;
			vertical-align: top;
			padding: 0;
		}
		.btn-dashboard {
			font-size: 13px;
			color: #666;
			line-height: 30px;
			height: 30px;
			border: 1px solid #e1e2e3;
			border-radius: 15px;
			padding: 0 10px;
			margin-left: 15px;
			font-weight: 400;
			display: inline-block;
			vertical-align: bottom;
		}
	}
`;

const NavLogo = styled.div`
	display: flex;
	align-items: center;
	justify-content: space-between;
	button {
		margin-top: -2px;
		margin-right: 15px;
		img {
