const MenuClassName = styled.ul`
	height: inherit;
	text-align: center;
	margin: 0;
	.selectedNav {
		box-shadow: inset 0 -2px #258bf7;
	}
	li {
		height: inherit;
		display: inline-block;
		a {
			position: relative;
			vertical-align: middle;
			font-size: 14px;
			line-height: 20px;
			font-weight: 600;
			padding: 15px;
			display: inline-block;
			:hover {
				box-shadow: inset 0 -2px #ddd;
			}
			em {
				position: absolute;
				top: 10px;
				right: -5px;
				pointer-events: none;
				font-style: normal;
			}
		}
		a:after {
			height: 100%;
			content: '';
			display: inline-block;
			vertical-align: middle;
		}
	}
`;

const ComponentsWrapper = styled.div`
	position: fixed;
	padding-right: initial;
	width: 100%;
	background-color: #fff;
	box-shadow: 0 1px 0 0 rgb(0 0 0 / 10%);
	z-index: 800;
	.box-mainbar {
		margin: 0 auto;
		height: 50px;
		width: 90%;
		position: relative;
		max-width: 1060px;
		.box-mainbar-nav {
			display: flex;
			align-items: center;
			justify-content: space-between;
			flex-direction: row;
			flex-wrap: nowrap;
		}
	}
`;

