const Aside = styled.aside`
	height: 100%;
	padding: 10px 0;
	> ul {
		height: 100%;
		margin: 0;
		.left-division {
			display: inline-flex;
			::before {
				display: block;
				content: '';
				width: 1px;
				height: 10px;
				background-color: #e1e2e3;
				margin: auto 10px auto 5px;
			}
		}
	}
	> ul li {
		position: relative;
		display: inline-block;
		height: 100%;
		padding: 0 10px;
		vertical-align: middle;
		* {
			font-size: 14px;

			color: #333;
			font-weight: 600;
			line-height: 1;
		}
		.btn-search {
			position: relative;
			margin-top: 5px;
		}
		.btn-signup {
			margin-right: 6px;
			line-height: 1.4;
		}
		.btn-dashboard {
			font-size: 13px;
			color: #666;
			line-height: 30px;
			height: 30px;
			border: 1px solid #e1e2e3;
			border-radius: 15px;
			padding: 0 10px;
			margin-left: 15px;
			font-weight: 400;
			display: inline-block;
			vertical-align: bottom;
		}
	}
`;

