/* eslint-disable react/no-children-prop */
/* eslint-disable jsx-a11y/control-has-associated-label */
/* eslint-disable react/button-has-type */
import React from 'react';
import {
	VisibilityContext,
	slidingWindow,
	getItemsPos,
} from 'react-horizontal-scrolling-menu';
import styled from 'styled-components';

function Arrow({ children, disabled, onClick }) {
	return (
		<ArrowButton
			disabled={disabled}
			onClick={onClick}
			children={children}
		/>
	);
	// return React.createElement(
	// 	'button',
	// 	{
	// 		disabled,
	// 		onClick,
	// 		style: {
	// 			cursor: 'pointer',
	// 			display: 'flex',
	// 			flexDirection: 'column',
	// 			justifyContent: 'center',
	// 			right: '1%',
	// 			width: '20px',
	// 			border: '1px solid black',
	// 			opacity: disabled ? '0' : '1',
	// 			userSelect: 'none',
	// 		},
	// 	},
	// 	children,
	// );
}

export function LeftArrow() {
	const {
		items,
		visibleItems,
		getItemById,
		isFirstItemVisible,
		scrollToItem,
		visibleItemsWithoutSeparators,
		initComplete,
	} = React.useContext(VisibilityContext);

	const [disabled, setDisabled] = React.useState(
		!initComplete || (initComplete && isFirstItemVisible),
	);

	React.useEffect(() => {
		// NOTE: detect if whole component visible
		if (visibleItemsWithoutSeparators.length) {
			setDisabled(isFirstItemVisible);
		}
	}, [isFirstItemVisible, visibleItemsWithoutSeparators]);
	// NOTE: for center items
	const prevGroupItems = slidingWindow(
		items.toItemsKeys(),
		visibleItems,
	).prev();
	const { center } = getItemsPos(prevGroupItems);
	const scrollPrevCentered = () =>
		scrollToItem(getItemById(center), 'smooth', 'center');

	return (
		<Arrow
			disabled={disabled}
			onClick={() => {
				scrollPrevCentered();
			}}
		>
			Left
		</Arrow>
	);
	// return React.createElement(
	// 	Arrow,
	// 	{ disabled, onClick: scrollPrevCentered },
	// 	'Left',
	// );
}

export function RightArrow() {
	const {
		getItemById,
		isLastItemVisible,
		items,
		scrollToItem,
		visibleItems,
		visibleItemsWithoutSeparators,
	} = React.useContext(VisibilityContext);
	const [disabled, setDisabled] = React.useState(
		!visibleItemsWithoutSeparators.length && isLastItemVisible,
	);
	React.useEffect(() => {
		if (visibleItemsWithoutSeparators.length) {
			setDisabled(isLastItemVisible);
		}
	}, [isLastItemVisible, visibleItemsWithoutSeparators]);
	// NOTE: for center items
	const nextGroupItems = slidingWindow(
		items.toItemsKeys(),
		visibleItems,
	).next();
	const { center } = getItemsPos(nextGroupItems);
	const scrollNextCentered = () =>
		scrollToItem(getItemById(center), 'smooth', 'center');
	return React.createElement(
		Arrow,
		{ disabled, onClick: scrollNextCentered },
		'Right',
	);
}

const ArrowButton = styled.div`
	 			cursor: 'pointer',
	 			display: 'flex',
	 			flexDirection: 'column',
	 			justifyContent: 'center',
	 			right: '1%',
	 			width: '20px',
	 			border: '1px solid black',
	 			opacity: disabled ? '0' : '1',
	 			userSelect: 'none',
`;
