import Divider from 'pages/home/Divider';
import React, { useState } from 'react';
import styled from 'styled-components';

function WdlistPage() {
	const [isMoreJob, setIsmoreJob] = useState(false);
	const [isSorting, setIsSorting] = useState(false);

	return (
		<WdListPageComponent>
			<article className="navbar-category-container">
				<div className="navbar-categorymain">
					<MoreButton
						clicked={isMoreJob}
						onClick={() => {
							setIsmoreJob(!isMoreJob);
						}}
					>
						<span className="text-jobgroup-title">전체</span>
						<span className="btn-jobgroup">
							<svg
								xmlns="https://www.w3.org/2000/svg"
								width="12"
								height="12"
								viewBox="0 0 12 12"
							>
								<path
									fill="#666"
									fillRule="nonzero"
									d="M2.28 3.22a.75.75 0 0 0-1.06 1.06l4.25 4.25a.75.75 0 0 0 1.06 0l4.25-4.25a.75.75 0 0 0-1.06-1.06L6 6.94 2.28 3.22z"
								/>
							</svg>
						</span>
					</MoreButton>

					<div className="box-jobcategory">
						<span className="btn-jobcategorytitle-empty">
							직군을 선택해주세요.
						</span>
					</div>
				</div>
			</article>

			<JobListContainer>
				<div>
