const JobSortContainer = styled.div`
	height: 40px;
	position: relative;
	.mobileicon-selector {
		display: none;
		svg:not(:root) {
			overflow: hidden;
		}
	}
	select {
		appearance: none;

		display: none !important;
		padding: 0 15px;
		border: 1px solid #e1e2e3;
		border-radius: 5px;
		background-color: #fff;
		color: #333;
		font-size: 15px;
		font-weight: 400;
		width: 100%;
		height: 40px;
	}
	.customselector-jobsort {
		width: 104px;
		position: relative;
		svg {
			transform: ${(props) => props.issort && 'rotate(180deg)'};
			transition: 0.3s;
		}
		> ul {
			position: absolute;
			top: 39px;
			left: 0;
			right: 0;
			z-index: 98;
			background: #fff;
			border: 1px solid #ececec;
			border-radius: 0 0 4px 4px;
		}
		> ul > li button {
			width: 100%;
			font-size: 14px;
			font-weight: 400;
			font-stretch: normal;
			font-style: normal;
			line-height: 40px;
			letter-spacing: normal;
			color: #333;
			text-align: left;
			padding: 0 0 0 16px;
			border-bottom: 1px solid #ececec;
		}
		> button {
			border-radius: 4px;
			border: 1px solid #ececec;
			background-color: #fff;
			font-size: 14px;
			font-weight: 400;
			height: 40px;
			letter-spacing: normal;
			color: #000;
			padding: 0 16px;
			position: relative;
			width: 100%;
			text-align: left;
			display: flex;
			justify-content: space-between;
			align-items: center;
		}
	}
	.selector-jobsort {
		position: relative;
	}
`;

const JobListContainer = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	position: relative;
	max-width: 1060px;

	.container-filterbtn {
		margin-left: 10px;
		position: relative;
	}
	.text-filterbtn-display {
		color: #36f !important;
		font-weight: 600;
		margin-right: 0;
		overflow: hidden;
		text-overflow: ellipsis;
		font-size: 14px;
		letter-spacing: normal;
		white-space: nowrap;
	}
	.text-filterbtn {
		margin-right: 15px;
		font-size: 14px;
		font-weight: 400;
		letter-spacing: normal;
		color: #333;
		white-space: nowrap;
	}

	.btn-filterwdlist-first {
		align-items: center;
		display: inline-flex;
		height: 40px;
		padding: 0 15px !important;
		margin-bottom: 0;
		border-radius: 5px;
		border: 1px solid #ececec;
		position: relative;
		background: #fff;
		color: #333;
		font-size: 13px;
		font-weight: 400;
		> svg {
			margin-left: 8px;
			transition: 0.3s;
		}
	}

	.btngroup-filterwdlist {
		display: flex;
		align-items: center;
		flex: auto;
		justify-content: left;
		max-width: none;
	}

	.container-filterwdlist-inner {
		display: flex;
		flex-direction: row;
		justify-content: space-between;
	}

	.container-filterwdlist {
		top: 50px;
		margin-bottom: 25px;
		padding-top: 10px;
	}
`;

const MoreButton = styled.button`
	display: flex;
	align-items: center;
	white-space: nowrap;

	.text-jobgroup-title {
		padding-top: 3px;
		margin-right: 15.5px;
		font-size: 24px;
		line-height: 29px;
		font-weight: 700;
		color: #333;
	}
	.btn-jobgroup {
		transform: ${(props) => props.clicked && 'rotate(180deg)'};
		position: relative;
		border: 1px solid #e1e2e3;
		border-radius: 9999px;
		background-color: #fff;
		width: 26px;
		height: 26px;
		transition: 0.3s;
		flex-shrink: 0;
		svg {
			margin-top: 7px;
		}
	}
`;

const WdListPageComponent = styled.div`
	background: #fff;
	min-height: calc(100vh - 50px);

	.navbar-category-container {
		background: #fff;
		padding-top: 40px;
	}
	.navbar-categorymain {
		margin: 0 auto;
		width: 87.72%;
		position: relative;
		display: flex;
		max-width: 1060px;
		align-items: center;
	}

	.box-jobcategory {
		display: flex;
		align-items: center;
		white-space: nowrap;
		:before {
			content: '|';
			font-size: 28px;
			line-height: 29px;
			padding: 3px 24px 0;
			color: #ececec;
		}
	}
	.btn-jobcategorytitle-empty {
		padding-top: 3px;
		font-size: 24px;
		line-height: 29px;
		font-weight: 400;
		color: #aaa;
	}
`;

export default WdlistPage;
