/* eslint-disable array-callback-return */
/* eslint-disable react/no-array-index-key */
import ArticleCard from 'components/common/ArticleCard';
import ChipButton from 'components/common/ChipButton';
import SkeletonUIArticleCard from 'components/common/skeletonUI/SkeletonUIArticleCard';
import { LeftArrow, RightArrow } from 'pages/home/ChipArrows';
import { ChipScrollBox } from 'pages/home/ChipscrollBox';
import React, { useContext, useEffect, useState } from 'react';
import {
	ScrollMenu,
	getItemsPos,
	slidingWindow,
	VisibilityContext,
} from 'react-horizontal-scrolling-menu';
import styled from 'styled-components';
import { CustomGet } from 'utils/api';
import { EventChipDummydata } from 'utils/mockData';

function EventChipScroll({ url, tag }) {
	const [items] = useState(EventChipDummydata);
	const [selected, setSelected] = useState(tag || 5);
	const [boxselect, setBoxselect] = useState(false);
	const [articleList, setArticleList] = useState([]);
	const [articleLoading, setArticleLoading] = useState(false);
	const testArticle = new Array(8).fill(0);

	const handleItemClick =
		(itemId) =>
		({ getItemById, scrollToItem }) => {
			setBoxselect(false);
			setSelected(selected !== itemId ? itemId : '');
			scrollToItem(getItemById(itemId), 'smooth', 'center', 'nearest');
		};

	useEffect(() => {
		setArticleLoading(true);
		const result = CustomGet({
			url: `web/home/insights?InterestTagCategoryId=${selected}`,
		});
		result
			.then((res) => {
				switch (res.code) {
					case 1006:
						setArticleList(res.result);
						break;
					default:
				}
			})
			.finally(() => {
				setArticleLoading(false);
			});
	}, [selected]);

	return (
		<ComponentWrapper>
			<ChipContainer>
				<ScrollMenu
					LeftArrow={LeftArrow}
					RightArrow={RightArrow}
					onMouseUp={({ getItemById, scrollToItem, visibleItems }) =>
						() => {
							const { center } = getItemsPos(visibleItems);
							scrollToItem(
								getItemById(center),
								'smooth',
								'center',
							);
						}}
					options={{ throttle: 0 }}
				>
					{items.map((value) => (
						<ChipScrollBox
							title={value.content}
							itemId={value.idx}
							key={value.idx}
							url={url}
							onClick={handleItemClick(value.idx)}
							selected={value.idx === selected}
						/>
					))}
				</ScrollMenu>

				<ModalButton
					onClick={() => {
						setBoxselect(!boxselect);
					}}
					boxselect={boxselect}
				>
					<button type="button" className="btn-more-info">
						<svg width="16" height="3" viewBox="0 0 16 3">
							<path
								fillRule="evenodd"
								clipRule="evenodd"
								d="M2 0C2.828 0 3.5 0.672 3.5 1.5C3.5 2.328 2.828 3 2 3C1.172 3 0.5 2.328 0.5 1.5C0.5 0.672 1.172 0 2 0ZM8 0C8.828 0 9.5 0.672 9.5 1.5C9.5 2.328 8.828 3 8 3C7.172 3 6.5 2.328 6.5 1.5C6.5 0.672 7.172 0 8 0ZM14 0C14.828 0 15.5 0.672 15.5 1.5C15.5 2.328 14.828 3 14 3C13.172 3 12.5 2.328 12.5 1.5C12.5 0.672 13.172 0 14 0Z"
								fill="currentColor"
							/>
						</svg>
					</button>
				</ModalButton>
			</ChipContainer>

			{boxselect && (
				<ModalContainer>
					<div className="box-modalcontent-wrapper">
						{items.map((value) => (
							<ChipButton
								key={value.idx}
								title={value.content}
								itemId={value.idx}
								onClick={handleItemClick(value.idx)}
								selected={value.idx === selected}
							/>
						))}
					</div>
					<div className="box-moretag-wrapper">
						<button type="button">
							<span className="label-moretag">
								관심태그 설정하기
								<span className="btn-endicon-wrapper">
									<span>
										<svg viewBox="0 0 18 18">
											<path d="m11.955 9-5.978 5.977a.563.563 0 0 0 .796.796l6.375-6.375a.563.563 0 0 0 0-.796L6.773 2.227a.562.562 0 1 0-.796.796L11.955 9z" />
										</svg>
									</span>
								</span>
							</span>
						</button>
					</div>
				</ModalContainer>
			)}

			<h2 className="EventList-title Title_Title__N_nVD">
				경영·비즈니스
			</h2>
			<div className="Sticky_Sticky_edge__c8cz0" />
			<div class="Sticky_Sticky_target__3v02E Header_Header__xnAUj EventList_EventList_header__7lei8">
				<div class="EventList_EventList_container__mcoes Header_Header_inner__wM44e">
					<div class="CategorySelect_CategorySelect__fIcxt">
						<label
							for="CategorySelect"
							class="CategorySelect_CategorySelect_label__sN7mg"
						>
							경영·비즈니스
							<svg
								width="10"
								height="7"
								viewBox="0 0 10 7"
								fill="none"
								xmlns="http://www.w3.org/2000/svg"
								class="CategorySelect_CategorySelect_icon__Gxr3n"
							>
								<path
									d="M8.75106 0C9.38006 0 9.73005 0.726 9.33805 1.218L5.35205 6.218C5.05205 6.593 4.48206 6.594 4.18106 6.22L0.167057 1.22C-0.227943 0.729 0.122057 0 0.751057 0H8.75106V0Z"
									fill="#333333"
								></path>
							</svg>
						</label>
						<select
							id="CategorySelect"
							class="CategorySelect_CategorySelect_input__SsGvp"
							data-attribute-id="eventList__category__choose"
						>
							<option value="">전체</option>
							<option value="tag.keorieo">커리어고민</option>
							<option value="tag.insa">HR</option>
							<option value="tag.ilban_samu">
								경영·비즈니스
							</option>
							<option value="tag.hoesasaenghwal">회사생활</option>
							<option value="tag.it2">개발</option>
							<option value="tag.cwijun_ijig">취업/이직</option>
							<option value="tag.beuraending">브랜딩</option>
							<option value="tag.gisul_it">IT/기술</option>
							<option value="tag.rideosib">리더십</option>
							<option value="tag.maketing_gwanggo_hongbo">
								마케팅·광고
							</option>
							<option value="tag.jojigmunhwa">조직문화</option>
							<option value="tag.nomu">노무</option>
							<option value="tag.dijain">디자인</option>
							<option value="tag.gyeongyeong_jeonryag">
								경영/전략
							</option>
							<option value="tag.seobiseu_gihoeg">
								서비스기획
							</option>
							<option value="tag.raipeuseutail">
								라이프스타일
							</option>
							<option value="tag.ux">UX/UI</option>
							<option value="tag.gogaegseobiseu">
								고객서비스·리테일
							</option>
							<option value="tag.deiteo">데이터</option>
							<option value="tag.peuriraenseo">프리랜서</option>
							<option value="tag.ingangwangye">인간관계</option>
							<option value="tag.yeongeob">영업</option>
							<option value="tag.enjinieoring_seolgye">
								엔지니어링·설계
							</option>
							<option value="tag.gyoyuk">교육</option>
							<option value="tag.geumyung">금융</option>
						</select>
					</div>
					<div class="Header_Header_triggers__P5ule">
						<div class="FilterMenu_FilterMenu__Xrj_g">
							<button
								type="button"
								class="FilterMenu_FilterMenu_button__aZjnZ"
								data-attribute-id="eventList__filter"
							>
								<svg
									width="15"
									height="15"
									viewBox="0 0 15 15"
									fill="none"
									xmlns="http://www.w3.org/2000/svg"
									class="FilterMenu_FilterMenu_icon__Q0svG"
