export default EventChipScroll;

const ContentList = styled.ul`
	display: flex;
	flex-wrap: wrap;
	margin: 0 -10px;
`;

const ComponentWrapper = styled.div`
	position: relative;
	.btn-morecontent-wrapper {
		display: flex;
		justify-content: center;
		button {
			width: 345px;
			color: #333;
			margin: 0 auto;
			height: 50px;
			font-size: 16px;
			background-color: #fff;
			border: 1px solid #e1e2e3;
			position: relative;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			vertical-align: middle;
			min-width: 64px;
			padding: 0 27px;
			box-sizing: border-box;
			border-radius: 25px;
			font-weight: 800;
			line-height: normal;
			cursor: pointer;
		}
	}
	.btn-moreinfo-label {
		width: 100%;
		font-size: inherit;
		font-weight: inherit;
		display: inherit;
		align-items: inherit;
		justify-content: inherit;
		color: inherit;
		> span {
			margin-top: -2px;
			margin-right: -1px;
			margin-left: 2px;
		}
	}
`;

const ModalContainer = styled.div`
	z-index: 1;
	background-color: #fafafa;
	position: absolute;
	top: 58px;
	padding: 30px 30px 13px !important;
	max-width: 1060px;
	border: 1px solid #e1e2e3;
	box-sizing: border-box;
	box-shadow: 0 4px 20px rgb(0 0 0 / 15%);
	border-radius: 4px;
	.box-modalcontent-wrapper {
		padding: 4px;
		flex: 1 1;
		overflow-y: auto;
		button {
			margin-bottom: 10px;
		}
	}
	.box-moretag-wrapper {
		margin-top: 10px;
		text-align: center;
		button {
			height: 50px;
			font-size: 16px;
			color: #36f;
			padding: 6px 8px;
			position: relative;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			vertical-align: middle;
			min-width: 64px;
			border-radius: 25px;
			font-weight: 700;
			line-height: normal;
			border: none;
			background-color: none;
			cursor: pointer;
		}
	}
	.btn-endicon-wrapper {
		margin-top: -2px;
		margin-right: -1px;
		margin-left: 2px;
		span {
			width: 100%;
			display: flex;
			align-items: inherit;
			justify-content: inherit;
			svg {
				overflow: hidden;
				user-select: none;
				width: 1em;
				height: 1em;
				display: inline-block;
				fill: currentColor;
				flex-shrink: 0;
				font-size: inherit;
			}
		}
	}
	.label-moretag {
		width: 100%;
		font-size: inherit;
		font-weight: inherit;
		display: inherit;
		align-items: inherit;
		justify-content: inherit;
		color: inherit;
	}
`;

const ModalButton = styled.div`
	position: absolute;
	right: -60px;
	top: 0px;
	.btn-more-info {
		min-width: 50px;
		min-height: 50px;
		display: inline-flex;
		justify-content: center;
		align-items: center;
		border: 1px solid #e1e2e3;
		background-color: ${(props) => props.boxselect && '#36f'};
		color: ${(props) => (props.boxselect ? '#fff' : '#939393')};
		border-radius: 5px;
		box-shadow: 0 4px 4px rgb(0 0 0 / 5%);
	}
`;

const ChipContainer = styled.div`
	margin: 30px 0;
	position: relative;
	margin-right: 60px;
`;

