import React, { Component, useState } from 'react';
import Slider from 'react-slick';
import styled from 'styled-components';
import MainBannerContent from './MainBannerContent';

function TopBanner() {
	const [bannerwidth, setBannerwidth] = useState(window.innerWidth * 0.8);

	const settings = {
		dots: false, // 점 안 보이게
		infinite: true, // 무한으로 즐겨요
		speed: 600,
		slidesToShow: 1,
		slidesToScroll: 1,
		variableWidth: true,
		// nextArrow: <HandleNextArrow />,
		// prevArrow: <HandlePrevArrow />,
		arrows: true,
	};

	return (
		<ComponentWrapper>
			<CustomSlider {...settings} width={`${bannerwidth.toString()}px`}>
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
				<MainBannerContent />
			</CustomSlider>
		</ComponentWrapper>
	);
}

const CustomSlider = styled(Slider)`
	z-index: 1;
	.slick-list {
		padding: 0px 50px;
		position: relative;
		overflow: hidden;
	}

	.slick-slide {
		display: inline-block;
		padding: 0 12px;
		float: left;
		width: 1060px;
		width: ${(props) => props.width || '1060px'};
	}
`;

const ComponentWrapper = styled.div`
	height: auto;
	position: relative;
	overflow: hidden;
`;

export default TopBanner;
