import { WindowsFilled } from '@ant-design/icons';
import LabelResponseLevel from 'components/LabelResponseLevel';
import React, { useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import styled from 'styled-components';

function WdPage() {
	const { wdIndex } = useParams();
	const ref = useRef();

	const handleScrollPrev = (type) => {
		if (type === 'left') {
			ref.current.scrollBy({
				top: 0,
				left: -1,
				behavior: 'smooth',
			});
		} else {
			ref.current.scrollBy({
				top: 0,
				left: 1,
				behavior: 'smooth',
			});
		}
	};

	return (
		<ComponentWrapper>
			<div className="jobDetail-container">
				<div style={{ position: 'relative' }}>
					<div className="jobcontent-container">
						<section className="jobimage-imgcontainer">
							<div className="jobimage-slider" ref={ref}>
								<Button
									onClick={() => {
										handleScrollPrev('left');
									}}
									className="arrow-left"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<Button
									onClick={() => {
										handleScrollPrev('right');
									}}
									className="arrow-right"
									type="button"
								>
									<i className="icon-cheveron-right" />
								</Button>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2F2cv2kbezyp0xheqs__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
								<div className="img-jobimage">
									<img
										src="https://image.wanted.co.kr/optimize?src=https%3A%2F%2Fstatic.wanted.co.kr%2Fimages%2Fcompany%2F33804%2Fm8xm5ocbnzvjdpa8__1080_790.jpg&amp;w=1000&amp;q=75"
										alt="해봄데이터  - 웹 개발(프론트엔드, 백엔드)"
									/>
								</div>
							</div>
						</section>
						<section className="jobheader">
							<h2>Senior AI Engineer (시니어 AI 개발자)</h2>
							<div>
								<h6 style={{ maxWidth: 'calc(100% - 88px)' }}>
									<a
										href="/company/28654"
										data-company-name="엔도아이(endoAI)"
									>
										엔도아이(endoAI)
									</a>
								</h6>
								<LabelResponseLevel />
								<span className="location-conatiner">
									서울
									<span className="header-dot">.</span>
									한국
								</span>
							</div>
							<div className="taglist">
								<ul>
									<li>
										<a href="/">#육아휴직</a>
									</li>
									<li>
										<a href="/">#음료</a>
									</li>
									<li>
										<a href="/">#커피</a>
									</li>
									<li>
										<a href="/">#교육비</a>
									</li>
								</ul>
							</div>
						</section>
						<div className="jobcontent-secription-warpper">
							<section className="jobdecription">
								<p>
									Happiness through good health 안녕하세요,
									EndoAI입니다. EndoAI는 Medical AI Solution
									start-up으로, 인공지능기술을 바탕으로 보다
									건강한 삶을 추구하는 전문가들이 모인
									집단입니다. 우리는 국내 최고의 의료진들이
									직접 수집/가공한 데이터를 기반으로 차별화된
									소화기 전문 AI Solution을 제공합니다. 우리는
									1. 의료기기와 AI의 융합을 통해 진료의
									정확도를 높이고, 2. 과잉진료 및 오진단 등의
									복잡한 문제들을 해결하며, 3. 개인별 맞춤형
									진단과 치료방법을 제공하여 소화기 진료의
									새로운 패러다임을 제시하고 더 건강한 삶을
									영위할 수 있도록 돕습니다. EndoAI가 가고자
									하는 길은 결코 쉬운 길이 아닙니다. 하지만
									우리들의 기술과 노하우는 불가능 해보였던
									일을 현실로 만들었고, 이제는 본격적인 도약을
									준비하고 있습니다. 또한 이러한 기술력을 인정
									받아 2021년 Series A 투자를 성공적으로
									마쳤으며, 2022년 Series B 투자를 계획하고
									있습니다. ** [합류 여정] 1. 서류검토 -
									본인의 역량이나 강점을 나타낼 수 있는
									것이라면 무엇이든 알려주세요. - 입사지원서는
									자유양식입니다. 파일형태 또한 자유입니다. -
									포트폴리오나 Github, Notion 등의 링크를 함께
									보내주시면 더욱 좋습니다. 2. 직무 인터뷰 -
									지원하신 포지션에 대해 함께 할 동료들과
									자세히 이야기를 나눕니다. - 약 1시간 정도
									소요되며, 직무에 대해 궁금한 점들을 확인할
									수 있습니다. - 직무에 따라 포트폴리오에 대한
									프레젠테이션을 부탁 드릴 수도 있어요. 3.
									컬쳐핏 인터뷰 - EndoAI와 지원자님께서 서로의
									방향성과 가치관에 대한 생각을 나눕니다. - 약
									1시간 정도 소요되며, 회사에 대해 더욱 깊게
									알아보실 수 있습니다. 4. 처우협의 - 우리는
									개인별 역량에 따른 보상패키지를 제공합니다.
									- 보상패키지와 입사일에 대한 자세한 정보는
									별도로 협의합니다. 5. 최종합격
								</p>
							</section>
						</div>
					</div>
					<div className="jobprocess-container" />
				</div>
			</div>
		</ComponentWrapper>
	);
}

const Button = styled.button`
	position: absolute;
	width: 9%;
	height: 100%;
	font-size: 26px;
	color: #b5b5b5;
	opacity: 0.7;
	z-index: 1;
`;

const ComponentWrapper = styled.div`
	margin: 0 auto;
	padding: 20px 0 80px;
	width: 87.72%;
	max-width: 1060px;

	.jobimage-imgcontainer {
		position: relative;
		overflow: hidden;

		.jobimage-slider {
			display: flex;
			flex-wrap: nowrap;
			flex-direction: row;
			align-items: flex-start;
			padding-bottom: 4px;
			height: auto;
			overflow-x: auto;
			scroll-snap-type: x mandatory;
			overscroll-behavior: contain;
			.arrow-right {
				right: 0;
			}
			.arrow-left {
				i {
					display: block;
					transform: rotate(180deg);
				}
			}
			::-webkit-scrollbar {
				height: 10px;
			}
			::-webkit-scrollbar-thumb {
				background-color: #b5b5b5;
				border-radius: 10px;
			}

			.img-jobimage {
				scroll-snap-align: start;
				position: relative;
				width: 100%;
				flex-shrink: 0;
				padding-bottom: 70%;
			}
			img {
				position: absolute;
				width: 100%;
				height: 100%;
				left: 0;
				top: 0;
				border-radius: 4px;
				object-fit: cover;
				outline: 1px solid rgba(0, 0, 0, 0.05);
				outline-offset: -1px;
			}
		}
	}
	.jobcontent-container {
		display: inline-block;
		width: calc(100% - 360px);
		vertical-align: top;

		.jobdecription {
			margin-bottom: 60px;
			padding-right: 20px;
			margin: 40px 0;
		}
		.jobheader {
			margin: 40px 0 30px;
			.taglist {
				margin-top: 20px;
				> ul > li {
					display: inline-block;
				}
				> ul > li > a {
					display: inline-block;
					margin-right: 6px;
					margin-bottom: 10px;
					padding: 9px 14px;
					font-size: 12px;
					font-weight: 500;
					line-height: 1;
					color: #333;
					background-color: #f3f5f8;
					border-radius: 25px;
				}
			}
			> h2 {
				color: #333;
				font-size: 22px;
				font-weight: 600;
				margin: 0 0 10px;
				word-break: keep-all;
				word-wrap: break-word;
			}
			> div > h6 {
				color: #999;
				font-size: 14px;
				font-weight: 600;
				margin: 0;
				display: inline-flex;
			}
			> div > div {
				display: inline-flex;
			}
			.label-response-container {
				display: inline-flex;
				white-space: nowrap;
				margin: 0 0 0 10px;
				position: relative;
				top: -1px;
			}
			> div > span {
				padding: 0 10px;
				position: relative;
				white-space: nowrap;
				margin-left: 10px;
				color: #999;
				:before {
					content: '';
					display: block;
					width: 1px;
					height: 12px;
					background: #e1e2e3;
					position: absolute;
					top: 4px;
					left: 0;
				}
				.header-dot {
					margin: 0 5px;
					top: -3px;
					position: relative;
				}
				> div:after {
					display: block;
					content: '';
					clear: both;
				}
			}
		}
	}
`;

export default WdPage;
