import React from 'react';
import styled from 'styled-components';

function UserPopup() {
	return (
		<ComponentWrapper>
			<div className="MenuPopover_menuWrapper__Sju5Y">
				<div className="MenuPopover_header__u1J9n">
					<button type="button">
						<svg width="24" height="24" viewBox="0 0 24 24">
							<path
								fill="currentColor"
								d="M17.97 19.03a.75.75 0 001.06-1.06l-6.5-6.5a.75.75 0 00-1.06 0l-6.5 6.5a.75.75 0 001.06 1.06L12 13.06l5.97 5.97zM12 10.94L6.03 4.97a.75.75 0 00-1.06 1.06l6.5 6.5a.75.75 0 001.06 0l6.5-6.5a.75.75 0 00-1.06-1.06L12 10.94z"
							/>
						</svg>
					</button>
				</div>
				<ul className="MenuPopover_list__BzAsJ MenuPopover_desktop__QcuKl">
					<li className="MenuPopover_hasNewMywantedAlarm__Ij29H">
						<a
							href="/mywanted"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="myWanted"
							data-badge="true"
						>
							<span>MY 원티드</span>
						</a>
					</li>
					<li className="">
						<a
							href="/profile/matching"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="profile"
						>
							<span>프로필</span>
						</a>
					</li>
					<li className="">
						<a
							href="/status/applications"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="applications"
						>
							<span>지원 현황</span>
						</a>
					</li>
					<li className="">
						<a
							href="/profile/status"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="offersReceived"
						>
							<span>제안받기 현황</span>
						</a>
					</li>
					<li className="">
						<a
							href="/profile/likes"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="likes"
						>
							<span>좋아요</span>
						</a>
					</li>
					<li className="">
						<a
							href="/profile/bookmarks"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="bookmarks"
						>
							<span>북마크</span>
						</a>
					</li>
					<li className="">
						<a
							href="/referral"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="refer"
						>
							<span>추천</span>
						</a>
					</li>
					<li className="">
						<a
							href="/profile/point"
							className=""
							aria-label=""
							data-attribute-id="lnb"
							data-lnb-kind="points"
						>
							<span>포인트</span>
						</a>
					</li>
					<li>
						<a
							href="/logout"
							className="is-logout"
							data-attribute-id="lnb"
							data-lnb-kind="logout"
						>
							<span>로그아웃</span>
						</a>
					</li>
				</ul>
			</div>
		</ComponentWrapper>
	);
}

const ComponentWrapper = styled.div`
	height: 100%;
	font-size: 14px;
	color: #333;
	font-weight: 600;
	line-height: 1;
	position: absolute;
	top: 100%;
	right: -27px;
	margin-top: 13px;
	padding: 0 !important;
	display: block;
	transform: translate(50%, 8px);

	.MenuPopover_menuWrapper__Sju5Y {
		overflow: hidden;
		min-width: 194px;
		border-radius: 10px;
		box-shadow: 1px 2px 10px 0 rgb(0 0 0 / 30%);
		border: 1px solid #cdcdcd;
		background-color: #fff;
	}
	.MenuPopover_desktop__QcuKl {
		padding-top: 14px;
		li {
			height: 34px !important;
			position: relative;
			display: inline-block;
			vertical-align: middle;
			width: 100%;
			* {
				height: 100%;
				font-size: 14px;
				color: #333;
			}
			a {
				display: flex;

				color: #333;
				align-items: center;
				justify-content: center;
				vertical-align: bottom;
				font-weight: 500 !important;
				line-height: normal !important;
				padding: 8px !important;
				:hover span {
					z-index: 1;
					position: relative;
				}
				:hover::before {
					position: absolute;
					content: '';
					display: block;
					top: 0;
					right: 0;
					left: 0;
					bottom: 0;
					border-radius: 8px;
					background-color: #f7f7f7;
					margin: 0 8px;
					height: 34px;
					z-index: 0;
				}
			}
			:nth-child(3),
			:nth-child(7) {
				margin-top: 18px;
			}
			:nth-child(2),
			:nth-child(6)::after {
				content: '';
				display: block;
				margin: 9px 7px;
				height: 1px;
				background-color: #ececec;
			}
			:last-child {
				border: 0;
				margin-top: 9px;
				background-color: #f7f7f7;
				overflow: hidden;
				height: 50px !important;
				border-radius: 0 0 10px 10px;
				border-top: 1px solid #ececec;
				span {
					height: auto;
				}
			}
		}
	}
`;

export default UserPopup;
