/* eslint-disable no-return-assign */
import React, { useEffect } from 'react';
import styled from 'styled-components';

function LoginModal() {
	useEffect(() => {
		document.body.style = `overflow: hidden`;
		return () => (document.body.style = `overflow: auto`);
	}, []);

	return (
		<LoginModalRoot>
			<div className="content-modal-wrapper content-modal-regular">
				<div className="modal-header-wrapper">
					<i className="icon-logo" />
					<button type="button">
						<svg
							width="24"
							height="24"
							viewBox="0 0 24 24"
							color="#999"
						>
							<path
								fill="currentColor"
								d="M17.97 19.03a.75.75 0 001.06-1.06l-6.5-6.5a.75.75 0 00-1.06 0l-6.5 6.5a.75.75 0 001.06 1.06L12 13.06l5.97 5.97zM12 10.94L6.03 4.97a.75.75 0 00-1.06 1.06l6.5 6.5a.75.75 0 001.06 0l6.5-6.5a.75.75 0 00-1.06-1.06L12 10.94z"
							/>
						</svg>
					</button>
				</div>

				<div className="modal-content-wrapper">
					<div className="modal-textpanel">
						<h1>
							직장인을 위한 <br /> 커리어 플랫폼, 원티드!
						</h1>
						<h2>
							커리어 성장과 행복을 위한 여정 <br /> 지금
							원티드에서 시작하세요.
						</h2>
					</div>
					<div className="modal-inputpanel">
						<div className="modal-emailinput-wrapper">
							<p>이메일</p>
							<div>
								<input
									type="email"
									placeholder="이메일을 입력해 주세요."
									id="email"
									// value=""
								/>
							</div>
						</div>
					</div>
				</div>
			</div>
			{/* 배경 opacity 0.4 */}
			<div className="overlay-modal" />
		</LoginModalRoot>
	);
}

const LoginModalRoot = styled.div`
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	position: fixed;
	overflow: hidden;
	z-index: 1400;

	.modal-inputpanel {
		position: relative;
		overflow: hidden;
		z-index: 1;
		.modal-emailinput-wrapper {
			padding-bottom: 14px;
			position: relative;
			color: #767676;
			p {
				font-size: 14px;
				font-weight: 400;
			}
			div {
				margin-top: 11px;
			}
			div input {
				width: 100%;
				height: 50px;
				padding-right: 15px;
				padding-left: 15px;
				border-radius: 5px;
				border: 1px solid #e1e2e3;
				background-color: #fff;
				font-size: 15px;
				color: #333;
				box-shadow: none;
				box-sizing: border-box;
				outline: none;
				:focus {
					border-color: #36f;
				}
			}
		}
	}

	.modal-content-wrapper {
		background-color: #fff;
		padding: 20px;
		.modal-textpanel {
			text-align: center;
			word-break: break-word;
			margin-top: 24px;
			margin-bottom: 40px;
			h1 {
				line-height: 1.54;
				font-size: 26px;
				font-weight: 600;
				color: #333;
			}
			h2 {
				margin-top: 16px;
				line-height: 1.5;
				font-size: 16px;
				font-weight: 400;
				color: #666;
			}
		}
	}

	.modal-header-wrapper {
		border-bottom: 0;
		height: 24px;
		padding: 16px 20px;
		position: relative;
		color: #333;
		text-align: center;
		font-size: 16px;
		font-weight: 600;
		word-wrap: break-word;
		button {
			position: absolute;
			top: 50%;
			right: 0;
			transform: translateY(-50%);
			padding: 15px;
			line-height: 0;
		}
	}
	.content-modal-wrapper {
		width: 400px;
		overflow-y: auto;
		border-radius: 5px;
		background-color: #fff;
		position: absolute;
		overflow: hidden;
	}
	.content-modal-regular {
		top: 50%;
		left: 50%;
		width: 100%;
		max-width: 400px;
		max-height: calc(100vh - 150px);
		transform: translate(-50%, -50%);
		z-index: 5;
	}
	.overlay-modal {
		position: fixed;
		left: 0;
		right: 0;
		top: 0;
		bottom: 0;
		z-index: 4;
		background-color: rgba(0, 0, 0, 0.5);
	}
	.icon-logo {
		font-family: 'Elysio Bold' !important;
		line-height: 0.7;
		font-size: 23px;
		::before {
			content: 'wanted';
		}
	}
`;

export default LoginModal;
